INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr1%20Archivos/default-avatar-2020-34.jpg", "date_comment": "28 may", "date_stayed": "mayo de 2023", "author": "Maria Cristina B", "title": "Viaje Familiar", "comment": "Me\n gusto el restaurante Tunkul, la comida estaba deliciosa. Me encant\u00f3 el \nshow en el atrium fue prehisp\u00e1nico, divino. Muy buen ballet. De igual \nmanera el Kids Club todo precioso, en especial las chicas Karla, Nancy, \nMirian, Ana y Lili", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r892339223-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": "nov 2022", "date_stayed": "octubre de 2022", "author": "Francisco A", "title": "Que bonito es sentirse como en casa", "comment": "Empiezo\n por el d\u00eda de la llegada. El recibimiento fue espectacular por parte de\n Alejandra explic\u00e1ndome absolutamente todo.  Las habitaciones con vistas\n al mar son una maravilla y el servicio excelente. Durante toda mi \nestancia Alex, del equipo de management, estuvo pendiente de mi como un \nhu\u00e9sped en su casa; se nota cuando la gente trabaja con pasi\u00f3n. Me he \ndedicado a la hosteler\u00eda y los restaurantes del hotel son top. Benazuza y\n Sakura especialmente son obras de arte de mano de su chef Ignacio que \ntiene ideas, gusto y t\u00e9cnica.  Las camas balinesas en la playa son como \nestar en el para\u00edso literalmente atendido por Dorinel, el mejor camarero\n del Resort siempre pendiente de que estes bien.  Como \u00fanico punto \nnegativo comentar que las habitaciones dentro de la pir\u00e1mide son \nbastante", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r868244439-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
