INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr2%20Archivos/default-avatar-2020-15.jpg", "date_comment": "23 may", "date_stayed": "mayo de 2023", "author": "Edgard J", "title": "The Pyramid Cancun", "comment": "Excelente\n atenci\u00f3n desde los servicios de Limpieza hasta la Gerencia, un \nreconocimiento muy Especial para Tristan Gonzalez, quien manejo mi \nreserva y se ocupo de cada detalle, a gustavo tb de gerencia, que sin \ncontemplar Horarios trataba de satisfacer el capricho de no haber \nreservado con tiempo un restaurant y/o Show. Recomendable 100% el Hotel y\n vivir la experiencia The Pyramid. Agradecido a Todos ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r891647752-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
