INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr4%20Archivos/default-avatar-2020-26.jpg", "date_comment": "7 may", "date_stayed": "mayo de 2023", "author": "sofia G", "title": "Visita oasis piramide", "comment": "El\n hotel es bueno en general pero no me gusto el hecho de que pusiera todo\n incluido y cuando llegas te cobran extra por los restaurantes a algunos\n ni tienes acceso con la pulsera \"todo incluido\" cobran por las camas de\n la picina las camas son buenas la comida es rica y variada el personal \nes atento un poco introvertido (hay poco personal) el entretenimiento \ntambien es bueno y variado para adultos y ni\u00f1os , vine a festejar  mi \ncumple y me permitieron disfrutar de las actividades de la kid zone las \ncuales son manualidades,juegos y videojuegos las chicas del lugar : \nMirian, Ana , Karla, Nancy, Lili con las que tuve la oportunidad de \ncompartir son amables y divertidas tan bien son muy organizadas con los \nhorarios del cronograma. En general es muy bueno y divertido buena \nubicacion", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r889413150-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr4%20Archivos/default-avatar-2020-4.jpg", "date_comment": "5 may", "date_stayed": "mayo de 2023", "author": "Jackeline G", "title": "Viaje familiar", "comment": "En\n lo personal me gusto mucho el hotel los puestos de comida, y l tama\u00f1o \nde las albercas y donde estaba ubicado todo, me gusto mucho el area de \nkids club donde participaron las chicas nancy,karla,ana, miriam, \nberenice y lili ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r889119163-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr4%20Archivos/default-avatar-2020-34.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Ohcar", "title": "Excelente amabilidad y rico caf\u00e9", "comment": "Reservamos\n tres d\u00edas todo inclu\u00eddo para la familia. Las instalaciones del hotel \nson buenas, la comida deja mucho que desear para un precio cinco \nestrellas. Lo mejor fue el servicio en general de todo el personal, \ntanto de meseros, de personal de alberca como en recepci\u00f3n. El mejor \nservicio y amabilidad lo tuvimos en el caf\u00e9 Coffee and me por parte de \nLaura y Andrea. Muchas gracias!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r888386239-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr4%20Archivos/default-avatar-2020-48.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "Carolina A", "title": "Diricto de chileee a cancun", "comment": "Divertido.\n Comida internacional  y nacional. Full carrete. Todos locos!! Cuidado \nsi son muy tradicionales jaja. Excelente. Mencion especial  a Alondra \ndel equipo de animacion y a Viridiana del bar beach club ue hicieron \nnuestra estafia mucho mas amena. GRACIAAAS!!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r878952951-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr4%20Archivos/default-avatar-2020-45.jpg", "date_comment": "feb 2023", "date_stayed": "enero de 2023", "author": "Sergio R", "title": "En si el personal muy amable, s\u00f3lo un poco desconcertado con la actitud de servicio a la hora del Check in", "comment": "Excelente\n ubicaci\u00f3n salvo que el hotel ya est\u00e1 un poco viejo deteriorado y ya \nhace falta mucho mantenimiento, ese d\u00eda llovi\u00f3 bastante que fue el \nviernes 26 de enero y por la puerta de la terraza se meti\u00f3 todo el agua y\n se inund\u00f3 la habitaci\u00f3n, hicieron un m\u00e1ximo esfuerzo por solucionar mas\n sin embargo de tanta gente que tienen los tiempos de espera y de toma \nde acci\u00f3n son bastante largos, mas sin embargo el resto de la estad\u00eda \nestuvo placentera ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r877597566-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr5%20Archivos/default-avatar-2020-4.jpg", "date_comment": "feb 2023", "date_stayed": "enero de 2023", "author": "Silvia A", "title": "Vacaciones familiares", "comment": "Muy\n buena la atenci\u00f3n en el kiddo zone, las animadoras Lili, Berenice, \nMirian, Ana, Karla y Nancy son excelentes!!! Los ni\u00f1os siempre bien \natendidos y felices!!!! Hay pileta, manualidades, pinta carita, show de \npiratas, etc. Lo pasan de 10 los ni\u00f1os!!!!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r877238181-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr5%20Archivos/default-avatar-2020-68.jpg", "date_comment": "ene 2023", "date_stayed": "enero de 2023", "author": "Florencia G", "title": "SIN CAMASTROS, recu\u00e9stese en la arena", "comment": "En\n l\u00edneas generales es hotel es bueno. Cosas negativas seg\u00fan mi \nexperiencia, es IMPOSIBLE conseguir reposera/silla/camastro en la playa.\n Esto se debe a que hu\u00e9spedes dejan las toallas sobre las mismas para \nreservarlas, quedando muchas sin utilizar el d\u00eda entero, a esto se le \nsuma que no hay ning\u00fan tipo de control de esta situaci\u00f3n por parte del \nhotel, NO HAY personal del hotel en la playa para organizar NADA, \ngenerado fuertes conflictos entre hu\u00e9spedes. Cuesti\u00f3n muy desagradable \nteniendo en cuenta que uno viene a Canc\u00fan a descansar en la playa. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r876206913-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr5%20Archivos/default-avatar-2020-34.jpg", "date_comment": "ene 2023", "date_stayed": "enero de 2023", "author": "Marco Antonio V", "title": "Congreso netWorth", "comment": "La\n atenci\u00f3n bastante buena, te ayudan a organizar tu evento de tal forma \nque salga muy bien, la atenci\u00f3n en especial de Poleth y \u00c1lvaro de lo \nmejor . Si por alguna raz\u00f3n necesit\u00e1bamos algo ellos lo consegu\u00edan, muy \nbuen servicio", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r875885156-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr5%20Archivos/default-avatar-2020-68.jpg", "date_comment": "ene 2023", "date_stayed": "enero de 2023", "author": "SOFIA ARANTXA R", "title": "A\u00f1o nuevo, familia y The Pyramid Grand Oasis", "comment": "Agradezco\n infinitamente a The Pyramid Grand Oasis, es el primer hotel que visito \nCanc\u00fan y que voy de vacaciones con toda mi familia junta. Despu\u00e9s de no \nhaberlos visto por un gran tiempo y poder festejar el cumplea\u00f1os de mi \nabuelito, sin duda el mejor motivo y que estoy segura que lo bueno se \nnos present\u00f3 en cada momento. Agradezco que pudo tener una silla de \nruedas para poder transportarlo y espero m\u00e1s hu\u00e9spedes puedan tener una \nsi la necesitan, y como sugerencia, puedan hacerles dicha invitaci\u00f3n \ndesde la recepci\u00f3n. El ba\u00f1o en su cuarto no se adaptaba para que el se \nba\u00f1ara m\u00e1s confortablemente,sin duda las ganas de pasarla bien y \ndisfrutar no lo impidio. Claro que desde la comida en Market Place el \npimer d\u00eda que llegamos.  La zona de Helados en el \u00e1rea de Piscinas, el \nservicio", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r874204862-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr5%20Archivos/default-avatar-2020-49.jpg", "date_comment": "dic 2022", "date_stayed": "diciembre de 2022", "author": "Maryelix G", "title": "Excelente hotel. Buenos restaurantes y ameno.", "comment": "Muy\n bueno el hotel en t\u00e9rminos generales, buenos restaurantes y la \nexperiencia en Benazuza INCRE\u00cdBLE. Los chicos de las actividades muy \nsimp\u00e1ticos, especialmente Karen, platicadora y te hace pasar un rato \nameno a\u00fan cuando no est\u00e1s realizando alguna actividad. La \u00fanica \nrecomendaci\u00f3n que les dejar\u00eda ser\u00eda mejorar la calidad de las bebidas \nincluidas en el r\u00e9gimen \u201call inclusive\u201d, esas si dejan mucho que desear.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r870335361-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr6%20Archivos/default-avatar-2020-11.jpg", "date_comment": "nov 2022", "date_stayed": "noviembre de 2022", "author": "Diana M", "title": "Visiten Gelato and Me Pyr Les ENCANTAR\u00c1", "comment": "Estuve\n varios d\u00edas en el hotel y mi esposo y yo nos sentimos satisfechos, \ndefinitivamente el brazalete negro vali\u00f3 totalmente la pena. No duden en\n venir, hay muchos lugares bonitos que puedes disfrutar. Quiero \nagradecer a la Srita Alejandra Morales del Gelato, que nos hizo sentir \ncomo en casa, siempre recibiendonos de la mejor forma, gracias Alejandra\n Morales por tu servicio.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r870000607-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr6%20Archivos/default-avatar-2020-17.jpg", "date_comment": "nov 2022", "date_stayed": "noviembre de 2022", "author": "Maribel Trejo m", "title": "Congreso CONASIM", "comment": "Excelente\n las exposiciones y las instalaciones muy bien las animaciones buenas \nsolo en las albercas falt\u00f3 m\u00e1s actividades ojal\u00e1 y puedan realizar ba\u00f1o \nde espuma y dar m\u00e1s opciones para los desayunos para no estar todos en \nun solo restaurant haciendo fila esperando mesa", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r867983274-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": null, "date_comment": "nov 2022", "date_stayed": "octubre de 2022", "author": "andrea m", "title": "Buena experiencia para grupos", "comment": "Fui\n de trabajo para la 13va convenci\u00f3n de la empresa, c\u00f3mo hu\u00e9sped \nindividual creo que hay cosas que mejorar como el servicio y amabilidad \nde los meseros y barmans, y la calidad de las bebidas, en el restaurante\n Maki tako Artemio nos atendi\u00f3 muy bien y en Sakura todo est\u00e1 delicioso y\n el servicio y recomendaciones de marios hicieron fuera una mejor \nvelada. pero en cuanto a grupos la experiencia fue incre\u00edble, poleth \npinto fue la coordinadora de mi grupo y desde la cotizaci\u00f3n, negociaci\u00f3n\n y operaci\u00f3n del grupo fue demasiado atenta, abierta, confiable y r\u00e1pida\n es casi perfecta solo porque es humano jiji pero en verdad es una \ncoordinadora que te ayuda a que todo en tu grupo salga de los mejor y te\n resuelve en minutos lit, pero tambi\u00e9n detr\u00e1s de eso est\u00e1 todo su equipo\n de trabajo ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r867022093-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": null, "date_comment": "oct 2022", "date_stayed": "octubre de 2022", "author": "Jorge Hoyos", "title": "Coffe Shop", "comment": "Lo\n mejor del viaje fue la atenci\u00f3n en el Coffe & Me, por Guadalupe y \nLaura,  s\u00faper recomendamos por el trato hacia la familia y la calidad de\n atenci\u00f3n. Adem\u00e1s de la diversidad de caf\u00e9s y la disposici\u00f3n del \npersonal el tiempo en el cual se encuentra abierto. Felicidades!!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r863987397-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": null, "date_comment": "sept 2022", "date_stayed": "septiembre de 2022", "author": "Juan Alberto L", "title": "Recomendable", "comment": "Agradezco\n mucho la atenci\u00f3n brindada de Alberto Kampfner, Jency Lopez, Sof\u00eda de \nlos Santos, Zacil Ramirez, Yahaira Chikani, Sebastian Casiano, Poleth \nPinto, Alvaro Vergara, Oscar Patlan.  Excelente equipo en el hotel", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r858974542-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr1%20Archivos/default-avatar-2020-20.jpg", "date_comment": "28 may", "date_stayed": "mayo de 2023", "author": "Mikail adam Alakozai", "title": "Great Customer Service", "comment": "I\n had a very great experience at the pyramid. Mr Carlos Cepeda and his \nstaff made sure I was taken care of at the time of my check in and check\n out . Him and his staff were very nice throughout my entire stay at the\n hotels. Lucelly was also very helpful when I had questions and concerns\n about hotel activities. There are alot of great people the resort ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r892337418-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr1%20Archivos/default-avatar-2020-1.jpg", "date_comment": "24 may", "date_stayed": "mayo de 2023", "author": "jnickolichsells", "title": "Best of the Pyramid", "comment": "My\n 65 trip to Cancun and I believe my28th to the Pyramid! Just fantastic \nas alwaysI Can\'t get enough of that place! Matter of fact just booked \ntoday fpr Aug 2nd for another 2 weeks!! The upper management, Ricardo De\n La Pas, Carlos Cepeda and Oscar Overa are continuously working on \nupgrading the property and customer satisfaction. Ex. Putting in new spa\n area as we speak, Built a new huge gym with it ALL, New restaurant \noverlooking the gulf and the eating in the dark dining experience  just \nto mention a few of the changes you will see!! NOW FOR THE IMPORTANT \nPART... You MUST book Pyramid to access all these great things with \nBlack bracelet!! The staff is the best,, From Humberto (Beto) at the \ncigar bar to the Fountain Bleau staff, Ernesto, Carlos, Nathaniel and \nHector.A special", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r891770507-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/5d/default-avatar-2020-32.jpg", "date_comment": "12 may", "date_stayed": "mayo de 2023", "author": "Monika m", "title": "Awesome resort!!!", "comment": "Great\n hotel overall! Definitely do black band for access to most of the \nhotel. Food is far better then any Mexican resort I\'ve stayed at thus \nfar! Amazing atmosphere and so much to do. Erick at oasis Beach club is \namazing and went way above and beyond to make our experience amazing! \nDefinitely should get a raise! Would definitely come back! ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r890086614-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr1%20Archivos/default-avatar-2020-1.jpg", "date_comment": "9 may", "date_stayed": "mayo de 2023", "author": "Storyteller716446", "title": "The Miramar Beach Club and Restaurant/Grand  Oasis Hotel", "comment": "The\n hotel rooms  were a little dated and house keeping was lacking, they  \ndid not restock refrig with waters daily but hotel grounds were \nbeautiful. I would like to acknowledge The Miramar Restaurant and Bar \nfor there excellent service and hospitality! We rented a private cabana \nfor 4 days and it was well worth it! Jose was very attentive and \nfriendly! We never went without food or drinks and it was always service\n with a smile! Edgar the manager and the rest of the staff was great \ntoo! Excellent food and drinks! We will be back!    ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r889712980-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr1%20Archivos/default-avatar-2020-20.jpg", "date_comment": "5 may", "date_stayed": "abril de 2023", "author": "LoveTravel596366", "title": "Amazing staff! Thanks for the experience.", "comment": "Amazing\n staff. Thanks Oscar by the kids pool. Oscar at Fountain blue. and \nFielipe at Kinky. Best workers at the resort! Made the experience so \nmuch better.  The overall experience was much better. These guys made \nsure I have fun. Will definitely come back!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r889140042-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr2%20Archivos/default-avatar-2020-46.jpg", "date_comment": "3 may", "date_stayed": "abril de 2023", "author": "CNC1980", "title": "Pool & Beach Paradise", "comment": "This\n was my 3rd time staying at Oasis Property in Cancun however this was my\n first time staying at Pyramid at Grand Oasis compared to Grand Oasis.  \nPyramid had many more options for food and always had something great to\n eat. Always enjoyed the shows especially the Caribbean show and all the\n aerial art acts. The app is very useful as you can contact customer \nservice and reserve restaurants. A huge shout out to Tristan Rocher, \nOasis representative as he worked very closely with me via emails to \nmake sure I had the best experience despite me being on crutches. He was\n always friendly and willingly to help.  I appreciate his dedication and\n work ethics. Overall, Oasis property is huge and offers a variety for \nall ages.  I loved my plunge pool and all the bali beds and pools on \nthis", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r888853894-The_Pyramid_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "marzo de 2023", "author": "Whitney M", "title": "Love love love this hotel", "comment": "My\n husband and I love this hotel I surprise him last year for his birthday\n to this hotel and he just fell in love with it so we visited again this\n year in march 2023 he even visited some of his friends and they came \nalong as well. The hotel as so many restaurants we didn\u2019t get bored of \nthe food and also activities to do we would like to go again next year. \nThe hotel and the staff is amazing on my last day I had a massage \nbooked, but I forgot my necklace in the spa and only noticed when I was \nat the airport. I didn\u2019t want to risk it to go back to the hotel so I \nwaited until I got back to england and I emailed them and Tristan Rocher\n made sure that I got back my necklace and I am so grateful. Would \ndefinitely recommend this hotel. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r887741016-The_Pyramid_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/9c/default-avatar-2020-14.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "jamesdhammal2", "title": "My own personal coffee shop \u2764\ufe0f", "comment": "Really\n enjoyed our stay, the coffe shop (Coffee and Me) was amazing and a real\n selling point of the resort. A big thank you to Guadalupe and Laura for\n keeping us caffeinated and happy, they were both extremely helpful and \npatient, always happy and always smiling. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r887136618-The_Pyramid_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/43/default-avatar-2020-11.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Sherpa686007", "title": "Our favorite resort", "comment": "We\n were dining at Cariyes restaurant  And Adrian, Johnny and Owen  \nProvided an excellent service! Thank you so much! We will be back! Food \nand drinks were also great! We love this resort and are regular  \nCostumers.  It\'s our 7th visit  Larissa Harwood ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r886935182-The_Pyramid_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/77/ae/default-avatar-2020-2.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Erika C", "title": "Nice place to stay .", "comment": "Kiddo\n Zone - best place for the kids all the staff very friendly . My \ndaughter and her cousin had a great time always looking forward for the \nnext day activities . Thank you to Mirian , Bere,lili, Karla , Nancy, \nAna for all the attention to our girls .. also a bug thanks to Rafael \nJarvio for the tennis lessons , they had a great time ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r886803994-The_Pyramid_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr3%20Archivos/default-avatar-2020-64.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Brian W", "title": "Spring break", "comment": "Ricardo\n at the pool bar was the best, he took great care of us, and we will be \nba5. Great time during \"happy hour\". We will be back. A great value in \nCancun. A 5 day trip during spring break and the resort did not seem \nover crowed.Great location for us. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r884974067-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "pyr3%20Archivos/avatar064.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Jon S", "title": "Relaxing", "comment": "Excellent\n vacation. Food was very good. Buffet was tasty and sit down restaurants\n were plentiful and delicious. Pool was quiet on the sides and beach was\n perfect! We stayed in the Pyramid so we had more options than regular \nvacationers. Room was basic.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r884579473-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/3d/default-avatar-2020-55.jpg", "date_comment": "abr 2023", "date_stayed": "marzo de 2023", "author": "E A", "title": "Great Family Destination!", "comment": "We\n had a wonderful trip for our first time as a family to Cancun and the \nPyramids at Grand Oasis. We have a 3 year old and 5 year old and the \nkids club was amazing for them!  We met the sweetest women, Mirian,Lili,\n Ana, Bere, Nancy, Karla, and felt so comfortable with our children in \ntheir care.  They have wonderful activities for all ages and every day \nis different!  Some of our favorite activities have been the swimming \ngames, trampoline time, the most incredible face painting, beautiful \ncrafts and group games!    The pool is incredible and perfect for all \nages and swimming ability!  We especially loved posting up near the zero\n depth entry! Very clean! Towel service included!  Plenty of chairs for \neveryone, claim chairs early if you want a certain spot!   The food \nvariety was", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r884531011-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "marzo de 2023", "author": "castonplace", "title": "Loved this resort!", "comment": "My\n family stayed at the Grand Pyramid Oasis for 5 nights during spring \nbreak week this year.  I am so glad that we chose this hotel as we had a\n fabulous time!  We booked this vacation through Jetblue vacations so I \ndon\'t know if you get to pick which building you are in if you book \ndirectly thru the hotel but would strongly recommend staying in the main\n building (Sian Ka\'an) .  We had 2 rooms booked and both were fabulous \nbut be aware that it is very loud at night (esp during spring break) as \ndoors close and the sounds carry/echo.  Our rooms were very clean and \nhousekeeping came daily to clean the rooms and they were restocked with \nwater/beer/snacks.  The only thing they didn\'t restock was our beach \ntowels which were wet on day 3 so we left them on the floor and they \nwere taken", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r883494744-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "febrero de 2023", "author": "shaughnessy f", "title": "Fontainebleau", "comment": "We\n had a great stay at the pyramid! Carlos Dguez and H\u00e9ctor at the \nFontainebleau bar were the best bartenders ever! They were very kind and\n speedy! Treated us like family and made the best mojitos and lemon drop\n martinis! We went to this bar every night of our 10 night trip. We \nalready miss them! They deserve a raise!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r882962691-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr4%20Archivos/ksquarednyc.jpg", "date_comment": "12 may", "date_stayed": "abril de 2023", "author": "ksquarednyc", "title": "Gorgeous hotel and lots of nighttime entertainment - Pyramid is worth it", "comment": "I\'m\n really glad we chose the Pyramid tower, since with the black bracelet \nwe had access to more nightlife and restaurants. The entertainment staff\n is AMAZING.  Every night we would see a show in the atrium and then \nanother at the nightclub with the cabaret singers and sexy dancers. It \nwas great to have so much to do at night, you don\'t have to go downtown.\n  The wild peacocks were such a vibe!!!!! Add in the jungle vine lobby \nand you really do feel like you are somewhere special.   Sakura Japanese\n restaurant was 5 stars, I wish we had tried it earlier in the week so \nwe could have eaten here twice.  The Black Sens dinner was also super \nfun, I wrote a separate review on that. The buffets were good with a lot\n of variety, especially if you are a seafood or meat eater! As a \nvegetarian", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r890090032-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr4%20Archivos/default-avatar-2020-21.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "caitlin j", "title": "Beautiful, Beach, Drinks, Bathrooms", "comment": "At\n first glance, beautiful landscaping and welcoming/ friendly staff ready\n to eagerly help customers. Our rooms were upgraded in The Pyramid! \n(Yay!) excellent pool water quality. One side of the pool was a party \nzone, but if you walk to the other side you cannot hear the loud music \nand can enjoy relaxation, if needed. Well accommodating bars with great \nbar tenders. Alcoholic beverages are a 3/5 - taste great (high sugar \ncontent), but low alcohol mixed in. The Beach was beautiful and well \ntaken care of. My family and I were lucky to witness the staff discover \nsea turtle eggs. Their staff educated college students and surrounding \nstaff and celebrated the safety of the eggs and ability to discover and \nrelocate them to a safer area. Public bathrooms are a 2/5. Public \nBathrooms near", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r886815791-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/ea/default-avatar-2020-63.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Getaway68468104217", "title": "Pyramid Hotel, Cancun", "comment": "Well\n justified a four star rating. The staff can be a little slow at serving\n and tend to go at their own pace but overall very friendly. The hotel \noffers an abundance of different restaurants to eat at and one in \nparticular called Cereys was something special. Plenty and bars and \ncoffee shops to take advantage of including a cocktail bar and cigar \nlounge. My only small issue was with the design of the hotel lobby and \nroom. In one sense the hotel lobby is stunning and in another it allows \nnoise to travel all around the hotel so if you have a drunk group being \nloud it tends to wake the whole hotel up as you have no corridors to \nblock the noise. The hotel room was nice and clean however part of the \nbathroom wall was floor to ceiling glass so if someone used the bathroom\n at night", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r886363288-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ee/68/default-avatar-2020-9.jpg", "date_comment": "mar 2023", "date_stayed": "febrero de 2023", "author": "kaandSam", "title": "Birthday getaway", "comment": "We\n booked as reviews were good and glad through reccomendations we stayed \nin the master Sian Ka\'an suite as the room was amazing. We had two \nbathrooms, ocean view, two fully stocked fridges, two tv\'s room service.\n I really wanted jacuzzi on balcony which is subject to availability \nwhich they changed for me on my birthday.  The view wasn\'t ocean view \nbut still we had sunset and gorgeous view. The hotel is situated on the \nhotel strip and not too far from airport. Be wary at airport you get \nbombarded into booking trips when all we wanted to do is get to hotel. \nWe tired most of the restaurants and they all are lovely. We had our own\n concierge area and faults can be reported via app and they respond \nquickly. Only issue we had was no hot water one day. This was looked \ninto. Jacuzzi", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r880696549-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/a9/default-avatar-2020-50.jpg", "date_comment": "mar 2023", "date_stayed": "febrero de 2023", "author": "peekabooessex", "title": "Pyramid Grand Oasis", "comment": "1st\n Tax environmental you have to pay on arrival at hotel 1.60 pesos per \nperson per night, which you are nott advised about until you book in.   \n2nd tax if you go on any boat trips your\"ll have to pay 15 dollars each \nfor dock & reef tax on top of your trip.   Airport tax to leave the \ncountry is also charged per person at 259.5 pesos for two people.   Lack\n of paper seviates, people not using hand gel at restaurant  Hand gels \nnot refilled in lift & toilets   Careys restaurant 10/10 excellent  \nBahia steak House 8/10 good Cabaret Red restaurant 9/10, exotic dancers,\n singers, but we found the songs way too loud & distorted   Swimming\n pool beautiful & kept really clean, plenty of room for people to \nswim.   Tips go a long way if you want a good service at the bars 20 \npesos\u00a0 is the", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r880643405-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr5%20Archivos/default-avatar-2020-2.jpg", "date_comment": "mar 2023", "date_stayed": "febrero de 2023", "author": "gem b", "title": "Honest review", "comment": "The\n hotel itself is very nice, they could do with a bit of updating in the \nrooms though. Shower was unpredictable hot/cold kept changing and the \nmaids were not that attentive unless you left a tip of course. Which I \ndidn\u2019t mind had she cleaned everyday but they didn\u2019t. For some reason we\n were last on the list and kept knocking around 6pm to clean at that \npoint wee was trying to get ready for dinner. Some tips before you \narrive, download the Oasis Hotels app, you need this to book restaurants\n and look at the menus. Sakura, Careyes, the white box and Makuto were \nall really good and served amazing food! Didn\u2019t mind leaving a good tip \nthere. The beach was lonely all be it a lot of seaweed had washed up. \nPool area is nice and well kept. Some staff at the coffe shop were rude \nbut", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r880623042-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr5%20Archivos/default-avatar-2020-51.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "Mark E", "title": "Good Place -Could Improve", "comment": "We\n just got back. We had a very good time. I highly recommend only going \nhere with the Pyramid or Sian Ka\u2019an black bands. We booked all \nrestaurant reservations at check in. The best restaurants were Benazuza \nand Careyes (Amazing). Dos Lunus was great Italian food for third place \nand Bahia Steak House was just OK. I suggest for steak anywhere get \nfilet Or medallion (Excellent) as rib eyes and New York are a bit tough.\n The White Box had wonderful lunches and Coco had good breakfast and \nlunch. The Market place buffet was also very good. Never had a problem \nseating, but saw  very long lines for people with blue bands going to \ntheir buffet. Our room was very nice room 5302 (lagoon view)   I have \nbeen to Many all inclusives in Mexico and the food at the restaurants I \nmentioned above", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r879455168-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/20/default-avatar-2020-31.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "Katie B", "title": "Worth the visit!", "comment": "We\n spent two weeks at the Pyramid Grand Oasis, and only returned this \nweek. Overall, it\u2019s definitely recommended and absolutely worth going.  \nEntertainment - you cannot say you\u2019re bored at Grand Oasis / Pyramid. \nDay activities such as Zumba, aqua-aerobics / volleyball or an event at \nthe Beach Club will keep your daytime full, then there\u2019s a full on \nevening schedule - their cabaret shows are incredible. We went to Coco \nBongo one night, and the entertainment at the hotel was better than \nthat!   Food - Here is where you definitely need to go 5* (black wrist \nbands) vs 4*. 5* \u201cexclusively pyramid\u201d restaurants are flawless, \nexcellent service, great food and taste. Highly recommend Careyes and \npick the steaks from the grill menu. Also it\u2019s worth paying for a dinner\n at Benazuza - 14", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r879089231-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e3/6a/default-avatar-2020-47.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "Julia", "title": "Not luxury 100%", "comment": "Everyone\n here was very nice but you\u2019d think that over time they would really \ntake more care of the facilities. We had a 9 day stay but our last 2 \ndays weren\u2019t the best. It rained and when we woke up there was water on \nthe floor it was coming from the ceiling . This could have caused \nsomeone to fall and get hurt , they did change us to a new room but it \nwas the same thing . At that point I was just ready to go back home \nsince the room had a odd smell. The bad guys Pedro & Gael (believe \nthat\u2019s his name ) were awesome . They really new how to make some \ndelicious drinks & made our stay a lot better . The restaurants are \ndelicious here and so is the coffee shop . The resort offers lots of \nentertainment & recommend getting a massage the spa is amazing ! \nOther than the water and rooms", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r878986266-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/32/default-avatar-2020-12.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "Monika P", "title": "First time in Cancun", "comment": "We\n arrived at the hotel early and the bell boy took our luggage  Next we \nwent to the Mexican restaurant by the pool\u2026good service & food was \ngreat Our room was ready early by 2:30 We were in room 1542 right by the\n beach with pool view It was a little noisy at night as they had the \nentertainment by the pool right in front of our balcony , so no quiet \nnights Our room was dated but clean! There was only a bed sheet to sleep\n with so we had AC off at night We noticed the water dripping out of the\n faucet the whole time so we kept the bathroom door closed at night \nHousekeeping came everyday  The beach was beautiful although the waves \nwere rough  We woke up Early every morning to get a good spot either by \nthe pool or the beach As for the restaurants we booked the steak house \nand service", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r878983750-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr6%20Archivos/default-avatar-2020-10.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "Barbara D", "title": "34th Anniversary; returned after 25 years", "comment": "We\n visited Oasis Pyramid in 1996, as our very first vacay to Cancun over \n25 yrs ago.  We have been back to Cancun many times since then, but we \nhad not been back to Oasis until this year, so we booked Oasis, the \nPyramid; Sian Kaan to reminisce.   To start, we booked via a Southwest \nPackage with upgrade to Sian Kaan, and if you do this it\'s fine, bot \nOasis has turned into a time share type resort with reps bombarding you \nto take buy into the resort.  We should have been obstinate and checked \nin at the Sian Kaan desk, but they convinced us otherwise.  We still \nreceived appropriate wrist bands and made reservations while drinking \nchampagne,  but they were also trying to get us to come back for \nbreakfast sales pitch.  So if you book Sian  Kaan, don\'t let the sales \npeople intercept;", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r877716673-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": "pyr6%20Archivos/default-avatar-2020-12.jpg", "date_comment": "feb 2023", "date_stayed": "enero de 2023", "author": "CLMC28", "title": "Average Hotel but great grounds and beach!", "comment": "I\n just came back from The Pyramid at Grand Oasis. Overall my stay was \ngood and gave me what I really wanted from this vacation. A great beach \nto relax, walk and enjoy swimming in the Caribbean sea, nice service and\n a variety of food.   Though it says \"newly renovated\" it is really just\n new paint and design on the wall and maybe a new headboard. The other \nfurniture in the room was tattered and tired. The room is very standard \nand the bathtub had rust rings around the drain - newly renovated? I \ndon\'t think so. Maybe they should say \"refreshed recently\"!? There are \nthree buildings on the property and we stayed in the Pyramid which was \nthe Luxury VIP package with access to all VIP restaurants and areas of \nthe property.  BE AWARE: If you don\'t want to be around ANY SMOKE or \nwant to", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r877583945-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": null, "date_comment": "feb 2023", "date_stayed": "enero de 2023", "author": "shaydester", "title": "Gorgeous Beach and Views", "comment": "Mexico\n Review  Hello,  Just returned from a week at The Pyramid Cancun. I \ndon\u2019t normally do reviews but thought I would for this one. Overall my \nsister and I had a fabulous vacation. First off the grounds at the \nPyramid are beautiful and the beach was amazing!!! When I vacation in \ntropical destinations my priority is always the beach, followed by \nfood/drinks and then the accommodations.  The beach is expansive and you\n can walk for miles. Being on the Caribbean Sea is also a bonus. The \nwater is warm, aqua and other shades of blue, and the white soft sand \ncan\u2019t be beat. Because the grounds are shared with the Grand Oasis there\n is no shortage of chairs and palapas on the beach, especially if you \nget down there early and leave your towels. We spent every day on the \nbeach and it was", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r877250909-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": null, "date_comment": "ene 2023", "date_stayed": "enero de 2023", "author": "liz75703", "title": "Disappointed at first", "comment": "Just\n got back from The Grand Pyramid Oasis.  I insisted we booked to upgrade\n to Sian Kan and pay the additonal cost. When we arrived at VIP check in\n we were told those rooms were all booked. We had reservations eight \nmonths in advance. They knew we were coming...So we were told we were \nupgraded to another room which was also Sian Kan but not in the main \npryamid. It was called a Sian Kaan but was  certainly not an upgrade. \nLocated in the south building our room was practically in a basement. \nNothing special, a good view, outdoor jacuzzi but a real disappointment.\n Not what we paid for. My husband went back to VIP check in, talked with\n Iana and the Resort Manager several times. The next day Iana came to \nour rescue and offered to show us rooms they did have available in Grand", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r876928375-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 4.0, '{"profile_picture": null, "date_comment": "ene 2023", "date_stayed": "enero de 2023", "author": "electronicpaper", "title": "Pyramid Yes, Sian Ka\u2019an No", "comment": "We\n booked the Sian Ka\u2019an upgrade at The Pyramid, and at the end of the day\n could not see the value of the upgrade. The Pyramid is beautiful and \nits exclusive restaurants with great food would have been enough, as \nmany of the club benefits advertised in the Sunwing upgrade for Sian \nKa\u2019an category were not available:  1.\tWelcome amenity was not offered \n2.\tThe exclusive beach was so tiny that we could never find chairs. As a\n matter of fact, this was the very first time that no chairs were \navailable anywhere in the resort, unless you got up early. There were no\n chairs by the pool or at the beach, and we had to pay a pretty price to\n reserve some. People left towels on chairs all day, and nobody from the\n resort cared, there were no signs like in other resorts. Once we got to\n the paid", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d8009670-r876807588-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Alejandra V", "title": "Muy buena", "comment": "En\n la cafeter\u00eda coffee & You nos atendi\u00f3 Simri Huchin nos atendi\u00f3 \nexelente muy amable nos ofreci\u00f3 bebidas muy atenta en su trabajo, r\u00e1pida\n y muy eficiente al igual que un mesero en el restaurante de bufette ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r892718641-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/7a/default-avatar-2020-25.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Moira L", "title": "Bah\u00eda Steak House", "comment": "La\n atenci\u00f3n de Joel y Justino  impecable. El lugar muy agradable y con \nm\u00fasica al vivo; acorde al lugar, sobrio. El gerente, Oscar; de 10! Me \nayud\u00f3 con la reserva (reservar 24 hrs antes y con hora) d\u00e1ndome un \nticket para  la cena con la hora de mi elecci\u00f3n (solo por ser una \npersona).\u263a\ufe0f El bufete de ensaladas bueno, tambi\u00e9n.puedes armar tu \ntablita de quesos y jamones. Y el corte de carne (ojo de bife) tambien \nbueno y en el punto que lo ped\u00ed ...pero, he probado mejores. \ud83d\ude15 \u2b50\ufe0f\u2b50\ufe0f\u2b50\ufe0f", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r892717919-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/11/default-avatar-2020-23.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Herbert W", "title": "Muy bueno", "comment": "Muy\n buena experiencia, un hotel m\u00e1s de fiesta que de descanso, la comida, \nlos tragos, el personal, en general muy bueno, las habitaciones \nagradables y de buena calidad, limpieza buena, buenas actividades ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r892389207-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/91/default-avatar-2020-62.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "CARLOS M", "title": "Mejor experiencia", "comment": "Mi\n llegada al hotel fue sensacional por qu\u00e9 los recibieron de la mejor \nmanera posterior menten optamos por ir a comer al restaurante \nterritorial y nos atendi\u00f3 Francisco y nos brindo el mejor servicio del \nhotel y nos trajo los platillos en tiempo y forma ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r892387955-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Aar\u00f3n Hern\u00e1n S", "title": "Buena comida y mejor servcio", "comment": "\n Desde la llegada al hotel este lugar ha sido m\u00e1s que nada \u00e9pico un \nlugar donde la fantas\u00eda y la realidad se conectan de una manera \nindescriptible. Es casi real lo bueno que ese lugar .Muy buen servicio \nen el restaurante de comida italiana (tratoria) Francisco nuestro mesero\n fue muy amable y atento. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r892386383-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Kirssy C", "title": "Gran experiencia", "comment": "\n Personal muy amable y con muy buena atenci\u00f3n al cliente, nos quedamos 8\n d\u00edas y cada d\u00eda desayunamos en el mismo sitio La comida de muy buena \ncalidad y exquisita  Agradecemos a Yudith, Doris,  Ana, Luis Carrillo y a\n Luis Guzm\u00e1n por su buena  atenci\u00f3n y hacer que nuestra estancia sea m\u00e1s\n agradable ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r892308908-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
