INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "goc3%20Archivos/default-avatar-2020-5.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Kate M", "title": "The best dinner", "comment": "Justino\n was the best! His glasses are great! All the food was good and Justino \nis the best ever and we are so happy to be in this resort with the best \nfood, service, and drinks. Me gusta la comida!!!!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r891526434-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "goc3%20Archivos/caption.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Jessica G", "title": "Can't wait to be back!", "comment": "I\n had an unforgettable time at Grand Oasis. The facilities were stunning,\n with a large swimming pool and a beautiful beach. The rooms were \nspacious and tastefully decorated. The food was delicious and varied, \nwith options for all tastes. Moreover, the staff was friendly and \nhelpful, always ensuring our stay was enjoyable. I loved the daily \nactivities too. I highly recommend this hotel for a super fun Caribbean \ngetaway!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r890887451-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ea/b2/default-avatar-2020-69.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Olivia F", "title": "Definitely reserve a table at Dos Lunas for amazing cuisine, atmosphere and company! Absolutely fantastic time and good!", "comment": "Amazing\n service and the wait staff was incredible at Dos Lunas!! Angel was a \nliteral angel and was so attentive and kind!! We loved him and loved the\n service and the food was incredible! Definitely recommend booking a \nreservation here and you will not be disappointed coming here. Please \nask for Angel since he is amazing and will make sure you are taken well \ncare of! ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r890796243-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/a3/default-avatar-2020-38.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Bridget W", "title": "Must stop in and see Carlos at FountainBlu", "comment": "The\n best most outgoing bartender\u2026made our experience so fun!!!  A great big\n Thank you to Carlos at the Fountain Blue.  Best drinks ever!! He made \nour time so entertaining!!  Also his coworker Ernesto added to the \ncharm!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r890795419-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Jack R", "title": "Dos Lunas", "comment": "The\n restaurant Dos Lunas was great. Gregorio and Efriain were magnificent \nservers, the live music was subtle and well executed with a great 3 \npiece band. The food was good and the wine selection was expansive. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r890385742-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "febrero de 2023", "author": "Rolivasrealtor", "title": "Great resort\u2026just bombarded by \u201cupsellers\u201d", "comment": "This\n beachfront resort is very nice with lots to do. All inclusive was the \nway to go, although most of the food was mediocre and drinks were \nseriously watered down.  It did, however, keep costs down. The room was \nnice with an ocean view and there was a tiny balcony to take it all in. \nLoud music in outdoor areas is something I personally enjoyed, although I\n know it\u2019s not for everyone.  Very large grounds, so lots of walking. We\n took a day trip to Isla de Mujeres and that was a great diversion. We \nreally liked this resort overall and we loved its glorious setting in \nCancun. What we didn\u2019t love (at all) was the constant hounding by up \nsellers - that started the minute after we checked in. For this trip it \nwas a higher level all inclusive wristband they were relentlessly \npushing. We", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r885186007-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "febrero de 2023", "author": "GeeKno", "title": "Super Relaxing For Couples!", "comment": "Huge\n resort, beautiful beach with GORGEOUS water!  Friendly, helpful staff. \n Clean rooms with quiet hallways in adult only floors.  Many, many of \ndining choices, though all-inclusive does not cover the cost of a couple\n of the high-end restaurants.  You MUST ask for a double if you are \nhaving mixed drinks, otherwise rather watered-down.  While the Caribbean\n all-inclusives we\'ve been to do not allow tipping, it is highly \nsuggested & recommended in Mexico so bring single dollar bills or \nexchange for Pesos before going! ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r884972873-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "marzo de 2023", "author": "Greg R", "title": "Magali Escobar & Aololfo Hernandez were amazing at Coffee & You!! They provided great service!!", "comment": "Magali\n Escobar & Aololfo Hernandez were amazing at Coffee & You!! They\n provided great service each morning and had very positive attitudes. \nAlways loved the mango mojito ice tea to start out my mornings :)", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r884325358-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "marzo de 2023", "author": "Philippe C", "title": "Varying costumer service at the party hotel", "comment": "The\n resort is very big with a lot of restaurants. We arrived on Friday at \nthe end of the US spring break, so the hotel was very busy and we had a \nhard time the first day with our room that wasn\'t ready and only had a \nking bed instead of the 2 double bed like I asked when I booked. After \nasking a few times, they put an extra single bed in the room in the \nevening to accommodate.   On our second day we had a much better \nservice. The concierge at our building (Frida Najera) was very nice and \ndid everything she could to help us. As an extra bonus, she did all that\n in very good french (my mother tongue). Having only 3 nights at the \nhotel she was helpful to make the most of our trip.  It\'s a party h\u00f4tel \nso I would not recommend for a family vacation even if there\'s a kid \nplayground.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r883489737-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "marzo de 2023", "author": "Paige A", "title": "Staff 10/10", "comment": "The\n perfect college resort!! Me and about 30 of my college friends came \ntogether. Although it\u2019s a little confusing to navigate, every single \nstaff member is amazing. They go above and beyond to make sure you\u2019re \nhaving a good time! Special shout-out to Ruth, concierge in building 3 \nand Alexis from housekeeping. Lost my purse w ID and camera and they are\n bringing it back to me as I type. VIVA OASIS", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r882559873-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
