INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": "goc6%20Archivos/default-avatar-2020-51.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "Jessica H", "title": "Great Trip and KiddO Club", "comment": "We\n stayed for 10 nights in Febrary 2023. Found that all staff were \nfriendly and attentive. I would say the most attentive restaurant staff \nI\'ve very experienced, we never found ourselves at the corner table with\n no one assigned! Our 7 year old daughter really enjoyed the KiddO Club.\n She liked to go for a couple hours each day to do crafts with Bere, \nLili, Mirian, Ana, Nancy and Karlan. All of these ladies were so \nfriendly and spoke excellent English. The special program on Valentine\'s\n Day was great! The kids club is quite spacious with shaded areas and \nair conditioned rooms with lots of activities, even a playroom for \nbabies and toddlers. It is a great spot to get out of the sun for a \nlittle while for kids of any age.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r880338784-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": "goc6%20Archivos/eric-s.jpg", "date_comment": "feb 2023", "date_stayed": "enero de 2023", "author": "Eric H O", "title": "A pleasurable experience for two 90 year old's", "comment": "Flights,\n ground transport, and accommodation went smoothly. All inclusive was \nthat, including gratuities and imported whiskies. Room was clean, view \nexcellent. Choice of restaurants was flexible, and all we used, good. \nGrounds and pools well laid out and accommodated the large number of \nguest rooms in the complex. Negative: we were in building #2, one \nelevator at one end only, meant a lot of walking which was a little hard\n with a walker. Other guests would put towels or other items on pool \nlounge chairs to save but left unused for hours, excluding others using.\n A final positive: All staff we encountered were very helpful and \nfriendly.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r877971092-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": null, "date_comment": "ene 2023", "date_stayed": "enero de 2023", "author": "nextleveltravel216", "title": "Hidden Gem In Cancun!!", "comment": "This\n is truly a hidden Gem in Cancun!   First, the location is ideal. right \nin the middle of the Hotel Zone, down the street from malls and bars. \nSurrounded by tons of restaurants.   My expectations are usually pretty \nlow with resort food, but Grand Oasis has some of the best food I\'ve \never had on a resort. Tons of restaurants, even the snack bars (hot \ndogs, fries chicken tenders) are above average. If you get the chance, \ntry Benazuza. Its truly an experience if you are a foodie  Beach club \nwas awesome, casino is bigger than most. Tons of things to do as far as \nbars and clubs.   The atmosphere is kinda older/families however, we had\n no problem finding a party and people to party with.   The grounds are \nBEAUTIFUL with the prettiest beach in Cancun. The rooms could use an", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r874782172-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": null, "date_comment": "dic 2022", "date_stayed": "diciembre de 2022", "author": "PLS05", "title": "Good value resort", "comment": "Nice\n hotel. Good value for the money. I would not pay a premium for it \nthough. Definitely a party hotel. There were many families though, I was\n traveling solo on this one. Would I bring my family and young children \nhere? Probably not. Loud music. Smoking all types of things. Belligerent\n drinking. It\u2019s like spring break. All year!  The staff are nice. My \nconcierges in my building were super nice. Thank you Karina Garcia, Ruth\n de Lao, Jonathan Rodriguez. Your friend from New York!  The property is\n vast. Lots of swimming areas. No kids park. Lots of adult only \nsections. No jacuzzi. Food was ok. The Japanese. Not even sure what kind\n of sushi rolls they were. The buffet was meh. The one restaurant called\n cocoa is pretty good. The rest are nothing special. The alcohol drinks \nare super", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r871917494-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 4.0, '{"profile_picture": null, "date_comment": "ago 2022", "date_stayed": "agosto de 2022", "author": "nella r", "title": "Beach club!", "comment": "Amazing\n services! Great jungle party at the Oh! Club beach party! Jorge was a \ngreat server and Pablo from entertainment was very informative and \nfunny. So attentive to everything and very friendly people.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d154444-r856395954-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
