INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop3%20Archivos/default-avatar-2020-41.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Roberto C", "title": "Vacaciones", "comment": "En\n esta tercera ocasi\u00f3n que visitamos el hotel sens, muy contentos estamos\n por su excelente servicio y calidad en el trato de sus meseros, que es \nlo m\u00e1s importante visitamos el restaurante cocoa, el mesero Carlos y \nGuadalupe fue el que ahora nos atendi\u00f3 y quedamos bien agradecidos por \nsu bien servicio, no cabe duda que pronto estaremos de vuelta", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r892725292-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
