INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/eb/default-avatar-2020-27.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Amir Abraham A", "title": "Canc\u00fan 2023", "comment": "Me\n toc\u00f3 mi habitaci\u00f3n en el lado The Sens,  bastante bonita y con vista al\n mar. La comida y las bebidas son bastante buenas y la atenci\u00f3n es buena\n tambi\u00e9n.  Lo que no me gust\u00f3 es que en los restaurantes de reservaci\u00f3n \nte dicen que a las 4 son las reservaciones y desde las 3 ya hay fila y \nen 5 minutos se agota el cupo total para esa noche, me pas\u00f3 en Maki Tako\n y en  Otro que no recuerdo el nombre. Las cortes\u00edas del barco pirata, \nla misma situaci\u00f3n, desde las 7 am ya hay fila y solo con las primeras \n10 o 12 personas se acaba el cupo porque es para un n\u00famero limitado de \ngente y hay personas que reservan hasta para 8 y 10 personas....", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r889831619-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
