INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 4.0, '{"profile_picture": null, "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "marah j", "title": "Cancunazo", "comment": "Restaurante\n cocoa 10/10, victor una joya en atenci\u00f3n, r\u00e1pidez, servicio!  En Pool \nbar Josu\u00e9 10/10  Las habitaciones en buen estado Es un hotel muy muy \nfamiliar, tiene varias actividades aunque podr\u00eda tener m\u00e1s, la playa es \nmuy linda, muy poco Oleage!  En cuanto al hotel tardamos 1:30 en hacer \ncheck in, es un poco tedioso el servicio de recepci\u00f3n! ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r889012922-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
