INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/5d/default-avatar-2020-32.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Vanessa Q", "title": "Rica cena en Cocoa", "comment": "En\n general el hotel est\u00e1 s\u00faper bien, la comida de los restaurantes est\u00e1 \nrica pero la que m\u00e1s me gusto fue la de Cocoa, ademas de que el mesero \n(Ignacio) fue s\u00faper agradable y servicial.  La playa est\u00e1 limpia, me \ntoc\u00f3 sin zargazo y el mar s\u00faper rico.  Recomendado! :)", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r888758103-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "siesha a", "title": "Cancun Trip", "comment": "Nice\n hotel, drinks were great, and food was very delicious at Alma, Bites \nFood Hall, and Dos Lunas! Enrique at Dos Lunas provided excellent \nservice and for that I\'ll return for the pasta! Victor at Alma also \nprovided exceptional service. These 2 gentleman were knowledgeable of \nthe menus and provided 1st class service! I\'ll definitely return to this\n resort.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r886500985-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "junio de 2022", "author": "Patrice N", "title": "Great For Families With Kids", "comment": "This\n was a family trip\u2026.2 families with 3 girls ages 10-12. We had a pretty \ngood time. No need to leave the resort if you don\u2019t want to. Take an \nhour or two your first day to learn about the locations of the many \namenities throughout the property as well as the dress codes and dinner \nhours at the restaurants. A few restaurants are adult only. Manuel Reyes\n was our waiter at a few of the restaurants and he was great! Virgin \nPina Colada and Iced Caramel Frozen Drink for the win!!! Definitely \nfamily friendly\u2026the kids were more interested in the pools than \nanything. Get out EARLY EARLY to get you a cabana!!! The shower was a \nlittle tricky. Almost time for the rooms to be updated.  They\u2019re not \nstuck in the early 1900s, but could stand a little modern flair. Looks \nlike the lobbies were", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r885064862-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Tyrone C", "title": "Le Petit", "comment": "Le\n Petit Experience  was EXCELLENT!!!  Food prepared by Chef Cesar was \ndelicious!!!  Bartolome Canche was GREAT!!! Very welcoming and made sure\n my drink was well prepared.   Francisco Silva and Alejandro Goneora \nwere very attentive to my family\'s needs.   It was a great experience \nthat I would recommend to all. My  picky 9 yr eater loved the food and \ntried everything and loved most.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r884730239-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 4.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "marzo de 2023", "author": "Tara L", "title": "Great Stay!", "comment": "This\n was a very nice resort. It was very clean and our rooms were cleaned \nevery morning. The pools were warm and clean as well. The view was \nspectacular and there was a lot to do. My only complaint is the food. At\n most, it was halfway decent and I got sick from it. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r882647545-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 4.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "marzo de 2023", "author": "Anna M", "title": "Great memories created!", "comment": "I\n was iffy at first because of checking reviews a couple days before my \ntrip,when we got there check in was quick didn\u2019t get floor I hoped for \nbut what can you do! Got into room the musty smell was a bit much \n(1208)! But we were going to give it a try anyway well that evening \nafter going back to room after late dinner,we showered and shower leaked\n all over bathroom floor had to use all our towels to soak it up,we were\n calling it a night except floors underneath us has a dj playing till \n1am easy to say we asked for room change first thing in the morning \nthankfully Kevin at front desk took care of it! 2nd room much better \n#2443 great view and no musty smell or leaks! Beach area was nice loved \nthe pool bar spent hours there,we loved la buffet at sens the best and \nohh Gabriel made", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r880772855-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop1%20Archivos/default-avatar-2020-44.jpg", "date_comment": "may 2023", "date_stayed": "abril de 2023", "author": "Maria Teresa L", "title": "Great food at TWB restaurant and excellent activities for Kids", "comment": "Excellent\n service.  We really liked the food and great service from Andr\u00e9s L\u00f3pez \nthe white box restaurant.  He provided us with excellent service and the\n food was amazing! We also used the Kids Club, and our son had the best \ntime ever.  The staff is so friendly and they do arts & crafts and \nother activities that kids will love!  Kudos to Fabiola, Karla, Yessica,\n Zeidy and Cecilia. Absolutely recommended if you want to really enjoy \nyour vacation :)", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r892491649-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop1%20Archivos/default-avatar-2020-25.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Chelsea D", "title": "Our review of the grand oasis palm. Such a beautiful mesmerizing breathtaking place to stay, my first experience here!!!", "comment": "Our\n experience was great and everyone is super nice and welcoming and warm.\n The service was excellent. The amenities were also excellent the pools \nand the beach are so clean and beautiful. The bar service is excellent \nwith awesome drinks. The food is outstanding. As for food service the \nbuffet at la hacienda the server named Mario was super attentive and \nvery very nice, we always had everything we needed he refilled our \ndrinks before they were even empty and is an all around friendly person \nwho talked with us for quite some time go visit Mario at la hacienda! \nAntonio at the Japanese restaurant maki tako, was excellent we went \ntwice and the first time we were lucky enough to get Antonio as our \nserver that we requested him for our second time at the restaurant, he \nis so very nice,", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r892101051-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop1%20Archivos/caption_002.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Downtown Burgers", "title": "Great staff!", "comment": "Amazing\n welcoming by lady at the front desk, rooms were clean, Wilber the at \nthe pool bar was amazing,  always attentive to the very little details. \nVery accommodating. I really appreciate that! Also my server AIDE was \nalways attentive, cordial and that smile that makes you feel welcomed \nevery day! Thank you all for a great time!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r891798817-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop1%20Archivos/default-avatar-2020-19.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Natascha H", "title": "Best bartender!", "comment": "This\n hotel was so fun! We had a blast with the bartender Uziel, he was so \nfun and good at making drinks! He definitely made sure that we were \nhaving a good time. His English was also great, so we talked with him a \nlot. If you\u2019re going to stay at the hotel, definitely check which bar \nhe\u2019s at, and go see him! You won\u2019t be disappointed!\ud83e\udd29", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r891032491-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop1%20Archivos/default-avatar-2020-47.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "New L", "title": "Nice people and place :)", "comment": "The\n restaurants are good and the beach was nice. They have hammocks you can\n lay on. The drinks are really nice too. There was a worker named \nGeraldine who spoke to and helped me and my family. She was nice!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r891015306-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop2%20Archivos/default-avatar-2020-51.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "John D", "title": "Baby blue crab Kids Zone", "comment": "BABY\n BLUE CRAB  Kids Zone.  Open from morning till night so parents can drop\n their kids off anytime, but must remain close and on property to use \nthe kids club. Lots of activities like arts and crafts, dancing, \nswimming and movie night, while other activities have an age limit (7-8 \nan older) just to be allowed to use the video game room. The club takes \nthe kids to the kids buffet for lunch and dinner. My kids loved the club\n and the women running the kids club. During swim time there\'s a \nlifeguard on duty. My little guys met a new friend from Florida there \n(Ian was his name) and they still talk about him from time to time. \nHoping to be back again soon.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r890989716-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop2%20Archivos/default-avatar-2020-62.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Culture40503380416", "title": "A great time!", "comment": "What\n a great experience. I visited the location in Cancun (Oasis Palm). It \nwas entertaining and the food was very good! Victor, Alejandro, \nFrancisco, Bartolime & Chef Jesus did an excellent job! I would \ndefinitely come again and bring my friends. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r890929936-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop2%20Archivos/default-avatar-2020-37.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Dillan P", "title": "Sakura is top notch. Paired with Alejandro as your waiter, it\u2019s impossible to go wrong.", "comment": "Sakura\n has world class fare and even better service. Alejandro was superb. We \ncouldn\u2019t have been more impressed by his knowledge of each and every \ndish. Alejandro was very attentive and friendly. Couldn\u2019t be happier.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r890532926-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop2%20Archivos/default-avatar-2020-50.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Klaire M", "title": "So much fun", "comment": "This\n is our third time at this resort and we always love it. Sakura \nespecially was amazing. Well worth the additional cost. The food was \nincredible and the entertainment was wonderful. Alejandro was the best \nserver and took amazing care of us! Jesus and Evelyn were also great. A \nwonderful night to celebrate my husband\u2019s birthday with some of our \nclosest friends!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r890532416-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "gop2%20Archivos/default-avatar-2020-27.jpg", "date_comment": "may 2023", "date_stayed": "abril de 2023", "author": "javier a", "title": "Great time  with my wife .", "comment": "First\n time in Cancun with my wife . Great hotel  I had a good time , met \ngreat people since day one . Would like to thank Cesar campos for all \nhis help, everyone at the cocoa restaurante (went 3 times) food was \ngreat and staff did an amazing job . Carlos , Ignacio and alejandro . \nAll I can say is I will be back .", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d566509-r888605911-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
