INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/9c/default-avatar-2020-14.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Curiosity735058", "title": "Espectacular la pileta y restoran le buffet de oasis palm", "comment": "Excelente\n la atenci\u00f3n de los mozos en el restoran le buffet. Son muy serviciales.\n  La atenci\u00f3n de Francisco Chuc muy buena! Comida variada y fresca. La \npileta que tienen, de distintos niveles de profundidad es genial para \ndisfrutar toda la flia. La barra dentro de la pileta es sublime! ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892806803-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
