INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/4a/default-avatar-2020-42.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Jackelinne G", "title": "El Dj es s\u00faper recomendado deber\u00edan de ponerlo m\u00e1s tiempo", "comment": "Me\n hosped\u00e9 en el hotel the send by oasis el d\u00eda 31/05/2023, disfrut\u00e9 mucho\n de mi estancia pero lo recomiendo mucho, est\u00e1 el Dj de 11:00pm a 1:00am\n y su ambiente es s\u00faper recomendado. 10/10 Deber\u00edan de ponerlo m\u00e1s horas\n para que todos los que los gusta bailar lo disfrutemos m\u00e1s .", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892735764-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
