INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/11/default-avatar-2020-23.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "ricardo c", "title": "Vacaciones en familia", "comment": "Excelente\n atenci\u00f3n y muy buenas instalaciones , en cuanto a restaurantes le petit\n chef algo fant\u00e1stico no solo por el sabor en cada uno de los platillos \nservidos , una experiencia recomendable 100% lo volver\u00eda a vivir .  Juan\n  Alejandro  Francisco  Cesar  Excelente labor , hicieron que todo \nvaliera la pena", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892722664-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "sens3%20Archivos/default-avatar-2020-17.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "JESSICA J", "title": "Excelente servicio", "comment": "Excelente\n atenci\u00f3n y servicio en el restaurante lee bufet, Mario Merino, muy \namable , atento y dispuesto a servir, paciente y excelente actitud. \u00c9l \n\u00e1rea de ni\u00f1os la remodelaron y qued\u00f3 s\u00faper bonita y divertida para los \nni\u00f1os. Muy segura con las c\u00e1maras y el protocolo, excelente.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892722271-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "sens3%20Archivos/default-avatar-2020-54.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Mario C", "title": "Me la pas\u00e9 incre\u00edble", "comment": "Una\n experiencia inolvidable, todo muy excelente, el servicio muy bien, todo\n muy lindo, la vista al mar muy linda, empleados amables, nos atendieron\n de lo mejor, sin ninguna queja, todo muy excelente, servicio muy bien", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892717284-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f3/7c/default-avatar-2020-29.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Sergio L", "title": "Experiencia", "comment": "Estuve\n hospedado con mi familia en los \u00faltimos d\u00edas de mayo es un excelente \nlugar fui al lugar la cantina que esta dentro del hotel y me encanto el \ntrato del joven uziel nos atendi\u00f3 de excelente manera", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892717147-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/5b/default-avatar-2020-61.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Rafael enrique B", "title": "Excelencia.", "comment": "Muy\n buena atencion del mesero RAFAEL POOL. bueba actitud, calidad de \nservicio. Muy atento. En el restaurante LE BUFFET. Y Las instalaciones \nmuy bien el hotel muy recomendable.  Y regresare pronto.  Tambie \nqgradezco a LAURA lopez..las playas hermosas. Y limpias.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892632696-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/2b/default-avatar-2020-52.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Diana G", "title": "Excelente servicio", "comment": "Playas\n limpias, hotel bonito y limpio, hay muchos restaurantes, la albercas \nest\u00e1 muy amplia y limpia, todos son muy atentos, siempre resuelven sus \ndudas, todo est\u00e1 se\u00f1alizado para que no te pierdas, las bebidas del bar \nest\u00e1n ricas y atienden r\u00e1pido, nos atendieron los sr. Ra\u00fal Salgado e \nHugo Quijano en el restaurante caf\u00e9 del mar, fueron muy atentos y \namables.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892524948-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens4%20Archivos/default-avatar-2020-64.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "KARLA E", "title": "Gran Oasis Palm, Bite", "comment": "La\n comida muy rica, presentaciones muy agradables a la vista y de sabor \n\ud83d\udc4c\ud83c\udffc\ud83d\udc4c\ud83c\udffc\ud83d\udc4c\ud83c\udffc\ud83d\udc4c\ud83c\udffc\ud83d\udc4c\ud83c\udffc,  la atenci\u00f3n de meseros excelente en particular \n\u00d3scar Gonzalez del Bite. Clima agradable dentro de los restaurante. \nRecomiendo preguntar por la forma de preparaci\u00f3n y la cantidad ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892847102-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens4%20Archivos/default-avatar-2020-18.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Paulina D", "title": "Servicio y buena actitud", "comment": "Nos\n hospedamos en el grand oasis palm que esta ubicado a lado del sens,  \npero estuvimos mas en la playa de sens ya que estaba menos concurrida y \nestaba mas tranquila. Nos encanto el trato de karina en las camas a la \norilla de la playa, bebidas y comida deliciosa!  ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892550992-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/59/default-avatar-2020-49.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Lorena B", "title": "Festejo de Cumplea\u00f1os", "comment": "Buenas\n instalaciones, comida deliciosa  , solo es incomodo tener que formarnos\n para todo.   El restaurante Caf\u00e9 del Mar es de lujo y delicioso , la \natenci\u00f3n muy personalizada departe de Julio B., Julio R y Luis gracias \npor la atenci\u00f3n y la delicadeza para nosotros . ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892097615-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/16/1e/d5/12/gustavo-s.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Gustavo S", "title": "Muy bueno pero puede ser mucho mejor!!", "comment": "Para\n personas adultos mayores que disfrutamos de la comodidad que ofrecen \nlos resorts todo incluido como The Sens Canc\u00fan, encontrar\u00e1n la mejor \noferta de restaurantes a la carta en el White Box, Careyes, Sakura y \nCafe del Mar. Exquisita presentaci\u00f3n de los platos y calidad, con un \nexcelente servicio donde se destacan Jesus L\u00e1zaro Romero y Alejandro \nSantiago por su amabilidad y atenci\u00f3n. El hotel tiene importantes \noportunidades de mejora en sus instalaciones pero su Director, Gabriel \nTorre, es conciente de las mismas y est\u00e1 siempre dispuesto a solucionar \nlos inconvenientes que pueda encontrar el huesped y construir sobre la \nexcelente oferta gastron\u00f3mica con que cuentan.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r891807106-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/66/default-avatar-2020-53.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Domingo L", "title": "Recomendable", "comment": "Excelente\n servicio del hotel y meseros atentos, gran servicio ofrecido por el \nmesero Oscar Gonz\u00e1lez. Variedad de actividades divertidas, recomendable \nla actividad en el barco pirata. Gran ambiente familiar.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r891760635-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens5%20Archivos/default-avatar-2020-5.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Arelia P", "title": "The sens en familia lo recomiendo", "comment": "El\n hotel familiar, comodo, habitaciones espaciosas, alberca grande se \ncalienta rapido con el sol, el show de Michael Jackon muy muy bieno lo \nrecomiendo en general y la mejor atencion de Victor del restaurante \nCocoa super atento y deliciosa comida del mar, Los Agaves deli tambien, \nel capitan Hook super espectaculo y muy grata experiencia en familia y \nlo mejor sus aguas tranquilas del mar ideal para ir con ni\u00f1os", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r890937183-The_Sens_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens5%20Archivos/default-avatar-2020-16.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Ana Maria Q", "title": "Muy conforme", "comment": "El\n sensoria Pool Lounge muy bien atendido por Karina y Javier.  No olviden\n reservar su espacio  se los recomiendo es un lugar solo para adultos \nperi vale la atenci\u00f3n  de los chicos omuy amable y acogedores", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r890913914-The_Sens_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e3/1f/default-avatar-2020-46.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Antonio", "title": "Nos gust\u00f3 mucho el hotel", "comment": "Para\n empezar estuvo todo bien, pero si fue alfo tardado la recepci\u00f3n, sin \nembargo nos dieron acceso a las instalaciones, bien ah\u00ed, decidimos \nrecorrer la playa del hotel y esta muy linda, muy limpias despu\u00e9s \ncomimos en algunos restaurantes, pero visitamos un restaurante qu\u00e9 nos \nrecomendaron qu\u00e9 es caf\u00e9 del mar  para la  cena, la comida est\u00e1 \nriqu\u00edsima, de entrada nos atendieron muy bien, el lugar tiene buena \nvista al mar y de sus platillos ni que decir, la atenci\u00f3n de los meseros\n es simplemente muy atentos, mi esposa y yo regresamos  una ocasi\u00f3n m\u00e1s y\n el servicio el mismo, suoer, queremos darle las gracias a Luis, isaias \nJulio Ruby Julio balan, Miguel por el excelente servicio qu\u00e9 nos \nbrindaron estando ah\u00ed. De verdad que si vivir\u00e1n este hotel no olvide ir a\n cenar a caf\u00e9 del mar,", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r889740201-The_Sens_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens5%20Archivos/default-avatar-2020-16.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Omar M", "title": "Vacaciones", "comment": "El\n hotel muy bonito y sus empleados muy amables desde que entre a las \ninstalaciones me trataron de la mejor manera. Quiero hacer \u00e9nfasis sobre\n el restaurante de comida internacional Le Petit Chef por su grandiosa \ncomida, s\u00faper recomendable el salm\u00f3n con finas hierbas. Agradecimiento \ninfinito a todo el equipo Juan, Alejandro, Francisco, Bartolom\u00e9 y el \nchef Cesar. S\u00faper Recomendable ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r889730347-The_Sens_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/20/default-avatar-2020-31.jpg", "date_comment": "may 2023", "date_stayed": "abril de 2023", "author": "Vanessa R", "title": "BEBIDAS EN EL \u00c1REA DE ADULTOS DE 10! SERVICIO PREMIUM", "comment": "Me\n gusta mucho su playa, el sushi es incre\u00edble, la atenci\u00f3n de la barra \npor parte de Jair Sandoval fue estupenda, esa margarita con yerbabuena \nla pedire por siempre, las habitaciones c\u00f3modas, si sugiero que el \nbuffet sea m\u00e1s cuidado y abundante", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r889726101-The_Sens_Cancun-Cancun_Quintana_Roo_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens6%20Archivos/default-avatar-2020-40.jpg", "date_comment": "may 2023", "date_stayed": "abril de 2023", "author": "Explorer17767450527", "title": "Excelente servicio", "comment": "Excelente\n hotel, excelente el servicio de todas las personas que laboran en el \nlugar, destaca la atenci\u00f3n de Victoria Vel\u00e1zquez Mart\u00ednez del \nrestaurante Le Bufett El \u00fanico detalle malo del hotel es que no tiene \nestacionamiento suficiente , tuve que dejar el carro en otro lugar", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r888605042-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens6%20Archivos/default-avatar-2020-28.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "MAXIMILIANO O", "title": "Excelente hospitalidad", "comment": "Excelente\n servicio del hotel en general, pero sobre todo destacar la atenci\u00f3n del\n bar isla mujeres , en especial la de los se\u00f1ores Alejandro rendon y \nFelipe Hern\u00e1ndez , muy amables , el servicio de bar exquisito , 10 de 10\n , visitando desde chileeeeee!!!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r888030375-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Andres A", "title": "Buen lugar para ir en familia", "comment": "Buen\n lugar para ir en familia, tienes acceso al barco pirata es un show  \ncorto pero divertido si no quieres pagar el completo,  lo negativo mi \nhija de 17 no pudo entrar a algunos restaurantes por ser menor pero si \nla cobraron como adulta ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r886916069-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Rene G", "title": "Servicio Concierce", "comment": "Excelente\n recibimiento, trato y atenci\u00f3n por parte de la Concierce Alexa Rangel, \nal ser Socios nosotros el personal telef\u00f3nico de Reservaciones no \nquisieron darnos el beneficio de cambiarnos a Habitaci\u00f3n familiar y al \nllegar al Hotel nos ayud\u00f3 la Concierce Alexa Rangel", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r886645061-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "miguel e", "title": "Buen servicio de isai y gabriel", "comment": "\n Buena atenci\u00f3n de parte del mesero isai y gabriel buen lugar para comer\n le buffet (en cuestion de de la comida si falta variedad por los que \nvamos por vastantes dias)tambi\u00e9n en el lugar dos lunas buenas pizzas y \npastas  y buen lugar para la familia volver\u00eda a repetir mis vacaciones ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r886109117-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "sens1%20Archivos/default-avatar-2020-15.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Pui S", "title": "Must dine", "comment": "Dined\n in the Sukura restaurant where we were entertained by multiple shows \nand the food was fantastic, presentation was excellent and the food was \ndelicious. Special mention to Alejandro Santiago, Jesus Lazaro & \nCapitan Evelyn for being gracious hosts and making our dining experience\n memorable.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892665961-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "sens1%20Archivos/default-avatar-2020-42.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Jeanette W", "title": "The best holiday in the most perfect setting", "comment": "Had\n the best time staying at this hotel, we had a lovely ocean view room \nwhich was nice and spacious with really good aircon!   The staff in this\n hotel are so nice, literally nothing is too much trouble for them and \nthey really go out of their way to make sure you have the best \nexperience. UZIEL was AMAZING, he was super friendly, really \nknowledgeable and so good with my children! He is definitely a great \nasset to t he hotel.  Others that deserve a mention are Emilio, \nEmmanuel, Laura, Reuben and Mario who were all fab too!  ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892620605-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/de/e7/default-avatar-2020-37.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Cruiser27595797252", "title": "Perfect service", "comment": "Great\n service from Uziel great service he was very helpful and kind fast \nservice very polite he was very busy and it was very hot and still had  \npatients for everyone there he was even serving restaurant and was fast \nand accurate was very busy and still was very kind ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892539265-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/28/aa/cd/74/cruiser46109059841.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Roberto Edmunds", "title": "Highly recommended at The Sens", "comment": "Particularly\n appreciated the smooth efficient and private check in. Excellent \nconcierge services provided by Javier. Beautiful and scenic location. \nComfortable and well appointed room. Delicious and well catered to in \nCafe del Mar by the host Luis and the chef, also named Luis! Thank you \nboth for a memorable dinner with soft ambiance . The White was the best \nfor breakfast and lunch. A thoroughly enjoyed visit to Senses!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892488984-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/00/default-avatar-2020-4.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Robert L", "title": "Trip to Hotel Sens.", "comment": "The\n mattress was a little soft. I sunk down and hard to get out. Rafael \nPool Mesero de Buffet was an excellent waiter along with Ana that worked\n in the Buffet We enjoyed our stay. Will definitely come back.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892462785-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "sens2%20Archivos/adevair-r.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Adevair R", "title": "Pool bar and Restaurant", "comment": "We\n were super well served by Ramon, Oman and all the pool waiters in a \nspectacular way and by Rafael and the team in the restaurant Ramon, \nDavid are super friendly and attentive. We hope to be back soon, but \ngetting into the room took a while", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892327848-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "sens2%20Archivos/default-avatar-2020-7.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Ana P", "title": "Awesome vacation", "comment": "I\n use the spa sensoria and I absolutely love it!!!the lady who gave me a \nmassage just when above and beyond!!!!!super professional!!!!going home \nfelling like a new person!!!thank you Arely\ud83d\ude4f\ud83c\udffd\u2764\ufe0fbest vacation ever!!!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r892192819-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Marcia C", "title": "AWSOME!", "comment": "I\n had an AWSOME experience!  Great customer service especially from \nRafael Pool, Joshua, Emmanuel, Emilio, and Daniel. They made me feel \nwelcome like a part of the family!  The food was wonder from Le Buffet. \nand I would highly recommend this resort so much that I\'m already \nplanning to return next year with my family!  \ud83d\ude0a", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r891920670-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/df/2b/default-avatar-2020-39.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "jacob p", "title": "All inclusive", "comment": "I\n came here for my 25th birthday and it was absolutely amazing. The staff\n was super friendly, they do not speak much English but that\u2019s okay! The\n drinks are 10/10 and the food is good! Definitely recommend coming here\n for any type of trip!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r891914148-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-o/11/02/e7/c7/brian-k.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Brian K", "title": "Relaxing beach experience", "comment": "The\n Siam Kaan, the adult area, was very nice and the new pool was \nexcellent. The bar on the beach was great.  It was great to be able to \nreserve a bed on the beach for the day.  The upgraded adult floors are \nvery well done with great views, and a nice bar for sunset.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r891904347-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "sens3%20Archivos/default-avatar-2020-23.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Stefan S", "title": "Every time we go - It gets Better and Better !!!", "comment": "We\n were staying at The Sens Cancun with some friends for four nights and \nwe had a blast at the Adult Only Sian Kaan section. The rooms on the \nninth and tenth floor are amazing with even better views. The bar on the\n 10th floor is a nice after dinner spot and we ended our evenings mostly\n there. We stayed at this hotel already several times because the \nservice and proximity to the beach is second to none. This time to our \nbig surprise - all the adult restaurants got an update or are New! Kudos\n to the team at Careyes \u2013 the food is so delicious. The White Box is \njust great for breakfast and so relaxing with its outside seating. The \nnew Caf\u00e9 del Mar offers a great new style and menu. If you have the \nopportunity - try Sakura and the dinner show. What an experience. We had\n a beach bed", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r891805236-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "sens3%20Archivos/default-avatar-2020-12.jpg", "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Explore44813958304", "title": "Beauty & Service - Great Experiences!", "comment": "The\n Sens was an amazing experience! The check-in process was quick and easy\n as well as the check-out. We LOVED the VIP Lounge Lounge Area, the \nstaff was so attentive and knew all of our orders by the end of our \nstay. I have to give the biggest shout-out to Valentina - she is truly \nthe best! She makes sure everything is perfect every time we stay at an \nOasis Property. I have stayed at many Hotels in the Hotel Zone and this \nis one of the best!", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r891777519-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Blanca G", "title": "Great Beach Vibe !!", "comment": "Llegamos\n a este hermoso hotel donde tiene una vista incre\u00edble al mar las \nalbercas est\u00e1n enormes y muy agradables y el area we loved the hotel and\n we were so happy to visit  Manuel Clemente and Paulette y Arturo Lopez \nwere best people at the bar they were the best service abs we had a \nbirthday party here they were the best please ask for them if you come \nby give them a high five if you want to make a new friend ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r891196029-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": "may 2023", "date_stayed": "mayo de 2023", "author": "Kevin H", "title": "Kid friendly and all around great experience", "comment": "My\n trip here to Cancun has been nothing short of wonderful. Although we \nwent as a couple only there were plenty of kid friendly accommodations. \nThere was food available at all times and variety was all over the map. \nRamen, italian, mexican and available from 630am until about 2 am.  With\n so many choices to choose from as far as cooling down at the pools and \nbeach we found great people who attended us as well. 2 people really \nstood out with me and one of them was  Eduin who catered to the kayak \nactivities and gaming room. His personality stood out and was \nenthusiastic about catering to us on the activities he was assigned. Our\n package was all inclusive and the one guy who stood out to us was \nManuel Javier; a bartender who took our orders and made them the correct\n way. I actually", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r888884972-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": "may 2023", "date_stayed": "abril de 2023", "author": "G K", "title": "Bright CLEAN Warm", "comment": "EXCELLENT\n Service from A-Z The hotel was spotless thanks to people like Kena the \nlobby window washer who warmly greeted me daily and attention to detail \nin cleaning. Most beachfront hotels are littered with sand and grime but\n the Sens was a jewel.  ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r888695035-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens4%20Archivos/dalorzs.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Dalorz S", "title": "Very nice small Resort with  small beach", "comment": "Small\n Resort  with a very small beach and swimming area but at least \nsargassum was no issue so could go in the water.. Right next to the \nferry and channel that boats go through constantly.  Didnt mind the \nboats- fun to wath.BUT the jetskis were extremely annoying & the \nfumes stunk ! The service was amazing best we have had in years. \nEspecially Julio at the front desk- he went out of his way when we \nweren\'t happy with our room-it was right over air-conditioning units and\n was terrible loud.Moved us into #505 which is probably the best room in\n the whole resort! Had amazing ocean view and it\'s own huge terrace!  \nUsually if ask to move you are just told nothing is available- but we \nwere surprised from the desk  they actually went above and beyond!! Food\n was good and had no problem", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r886879089-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens4%20Archivos/default-avatar-2020-52.jpg", "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Mat D", "title": "Good time in Cancun with the Sian Ka'an package", "comment": "My\n girlfriend and I stayed at The Sens (and the accompanying hotel beside \nit, Grand Oasis Palm)  late March-early April of 2023.  We had the Sian \nKa\'an package, meaning access to an exclusive beach area (with beds, \ngood shade, exclusive pool, an exclusive bar and waiters coming see us \nat our bed), some of the restaurants, a pool-lounge area, the 10th-floor\n bar, and concierge services.  We really enjoyed our stay.  The staff \nwas helpful and smiling. (A few seemed still in training and provided \ninformation that we had to double-check afterwards, but that remained \njust a small hiccup.) The a-la-carte restaurant were all good, \nespecially the White Box, Careyes, and Sakura (a new restaurant that \nhasn\'t showed up yet on The Sens hotel website). The food at those three\n places was A+ in", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r885983099-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": null, "date_comment": "abr 2023", "date_stayed": "abril de 2023", "author": "Belinda L", "title": "If you\u2019re a foodie, you gotta visit", "comment": "The\n White Box restaurant was freaking amazing. All the food was delicious. \nIt was definitely my favorite and service was over the top. Nicholas \nserved us most days and he\u2019s second to none. Estrella was great. Other \nrestaurants were delicious as well.  Besides the rooms being a little \noutdated, i would recommend ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r885784466-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "marzo de 2023", "author": "Tampaite", "title": "4 day stay at The Sens", "comment": "Family\n of four and booked a trip on Expedia(flight+hotel)  We chose The Sens \nprimarily since it was within our budget  Overall budget = $5000 USD \nFlight+Hotel = $3000 USD Misc expenses like Entrance Fees, Toll Road \nFees, Tips, Souvenirs etc = $2000 USD  We stayed for 4 days at \"The \nSens\" (Grand Oasis Cancun, The Pyramid are sister resorts)  Of the 4 \ndays, two days were spent for excursions so we weren\'t using the hotel \nfacility say between 7am to 7pm.  ***** Positives *****  - Hotel \nLocation is on the other side of the hotel zone which was a plus for us \nsince we could get on the 180D Toll Rd quickly - Limited parking on-site\n for rental cars but it was adequate since most of the guests had \ntransportation - We stayed at the top level floor (book 4th floor or \nhigher) and were", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r882631703-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": null, "date_comment": "mar 2023", "date_stayed": "marzo de 2023", "author": "Pauline G", "title": "Very good resort", "comment": "We\n were upgraded to a beautiful room with ocean view, balcony, top floor, \nhot tub\u2026bed was comfortable, food in restaurants \u00e0 la carte  was \noutstanding, best I\u2019ve had so far\u2026.Claudia Bustamante was very helpful, \nfriendly and went out of her way to make us feel welcomed.  Uziel, \nbartender on the 10th floor was very hospitable.  Thank you to both.  \nShuttle from airport to hotel and smoking in non designated area was our\n only concern.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r881765419-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens5%20Archivos/external-avatar.jpg", "date_comment": "mar 2023", "date_stayed": "febrero de 2023", "author": "gibson737", "title": "A modern looking, clean hotel", "comment": "A\n modern looking, clean hotel with updated amenities and hard working \nstaff on a beautiful beach.  There are a lot of activities / events to \ndo, with some wonderful restaurants.    Some very talented musicians, \ndancers, and entertainers every evening.  I wouldn\'t necessary rate this\n as a five star hotel as there are several issues that keep it from \nbeing top of the line, such as front desk staff not knowing their \nrewards program (free extended checkout) and not quite trustworthy (he \nsaid he would give us a free upgrade to a suite which was only a \nstandard room), and not explaining to new guests the room numbering \nsystem (on 2 occasions people tried to enter our room in the middle of \nnight as they tried to access our room (1014) instead of 1014S.  The \nhotel is quite loud - from", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r881265726-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/9f/default-avatar-2020-16.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "Line D", "title": "It gets better, once you understand!", "comment": "Interesting!\n  You get use to it.  My favorite restaurants Careyes, The white Box \n& Makitacos.  Actually, both Careyes and White Box are part of the \nbest restaurants I ever ate to.  However, kids are not accepted. Staff \nare very friendly and helpful. I suggest you rent a bed at Sa\u2019an if you \nwant to be confortable.  Not too expensive, but worth it. Hotel a bit \nolder but clean. What I didn\u2019t like are the elevators line ups.  Line \nups at restaurants to make reservations.   Not sufficient chairs at the \nbeach. Still enjoyed my vacations.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r880072734-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e6/ea/default-avatar-2020-54.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "donhen", "title": "Better than expected", "comment": "We\n have just returned from the hotel stayed in the Sens part, we are \nfamily of 4, 2 adults and kids age 5 & 10. We travelled from London.\n I was anxious to arrive after reading a lot of mixed reviews, so \nthought i would write my own, so sorry for long post but hopefully \nsomeone finds it helpful!  We found the hotel on a whole to be nice, the\n lobby and entrance are very nice but the hotel is dated in  parts but \nthere was work being done to rest of hotel. We had room 1323 which had a\n balcony, the rooms are bit dated but clean, bathroom was big and lots \nof wardrobe space but I would have preferred no balcony as these rooms \nhad the same view but were bigger. Pools - we found the water temp OK, \ncold when you first get in but bearable. My kids preferred the Sens pool\n and would go in", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r879865177-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/00/default-avatar-2020-4.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "JLP", "title": "Best bang for your buck for food, service, and beach, but lacking in drink quality and entertainment.", "comment": "Best\n bang for your buck when it comes to food and the beach. The buffet was \nactually really quite decent and servers were very attentive and \npleasant. Especially Isai, who always went above and beyond and was very\n personable with everyone in our family.   The fine-dining restaurant \ncalled Careyes my wife and I enjoyed was unbelievable.   The all \ninclusive drinks are not all that amazing,  however, with the exception \nof the cheap Mezcal, which, mixed with Fresca and rimmed with salt, made\n for a nice cocktail but yeah, you\'re not gonna be drinking Manhattans \nand hazy IPA\'s. Oh, and the water at the pools at both hotels are not \nheated which is alright on hotter days but not nice on cooler, windier \ndays.   The latin band was incredible but overall the entertainment \nwasn\'t out of", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r878600471-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/ef/default-avatar-2020-66.jpg", "date_comment": "feb 2023", "date_stayed": "febrero de 2023", "author": "IrisJoseph", "title": "Decent beachfront all-inclusive", "comment": "Right\n on the beach, with lots of families, decent food, and all you could \nwant from an all-inclusive. Strangely the taxi drivers at the hotel \ndidn\'t know the name Sens, they found it easily enough via Google Maps, \nbut it\'s better known by Oasis.  We were upgraded to a spacious suite \nthat had plentiful space and a spa in the bedroom - unfortunately the \nwater flowing into it was pretty rank but I think that may be a Cancun \nthing.  One day we had winds and heavy rain and leaks came in all over \nthe hotel, I wondered if they weren\'t prepared for such weather. Our \nroom was mostly fine but people with balconies got the worst leaks. I \nthink the building may need some maintenance and from the outside it \ncould do with some updating.  Also the grating around the pool is \nmissing in parts,", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r877949030-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens6%20Archivos/rmann5450.jpg", "date_comment": "ene 2023", "date_stayed": "enero de 2023", "author": "rmann5450", "title": "Great Place for the price. Can\u2019t complain.", "comment": "Great\n place for the price we loved it.  Foods fair and drinks are fair but as\n said for the price cant complain. Lots of options for food and drinks. \n Felt safe and overall clean.  No issues or complaints. ", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r876802123-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens6%20Archivos/default-avatar-2020-13.jpg", "date_comment": "ene 2023", "date_stayed": "enero de 2023", "author": "Martin Jensen", "title": "Overall nice stay\u2026", "comment": "I\n normally never write reviews, negative nor positive. But just wanted to\n post this one considering the many various posts.   We booked the Grand\n Oasis Palm (Not the Oasis Palm) but on arrival was given a  room at the\n Oasis Palm due to a bad smelling first room at the GOP. However on \ndealing with this the day after, Frontdesk Manager sorted it out and got\n us back to the right hotel.   Yes, it\u2019s a busy family hotel, but once \nyou get to know the system after a day or two, it works nicely and you \ncan get the most out of it. Friendly staff and nice pools, beaches, \nrestaurant and bars, even when traveling with young kids.  Deal with \ncomplaints on the day if needed - not  after your stay, otherwise hotel \ncannot make up for it and slow down and adjust - it\u2019s a holiday\u2026   We \nare still", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r874662400-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens6%20Archivos/default-avatar-2020-17.jpg", "date_comment": "ene 2023", "date_stayed": "diciembre de 2022", "author": "Jeremy L", "title": "Hervin", "comment": "The\n resort was good and we had a blast but one thing that stuck out in our \nmind was Our bartender Hervin. He is somebody that would have us coming \nback again for another visit. He took care of us on the beach almost \neveryday and had a personal flair and touch with every guest. we really \nappreciated having him take care of us the whole trip. We would love to \nsee the rooms updated but other than that the trip was very nice.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r873685930-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens6%20Archivos/default-avatar-2020-44.jpg", "date_comment": "ago 2022", "date_stayed": "agosto de 2022", "author": "Priscila S", "title": "Vacation", "comment": "We\n really enjoyed the beach and the pools as well. The staff is friendly. \nFor the bars, I think they need to improve the liquor selection. They \nhave a few restaurants that is adult only.  Maki Taco has a great \nservice- amazing food. Cristalle is amazing server. Lucy and Maria were \namazing as well.  La Palmas restaurant has delicious food and amazing \nstaff. Moises is very attentive and polite. Hostess are very \nprofessional and very friendly. Estrella was very polite and \nprofessional. Great service.", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r857629539-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "sens6%20Archivos/default-avatar-2020-66.jpg", "date_comment": "jul 2022", "date_stayed": "julio de 2022", "author": "Saray C", "title": "The bar by the grand oasis palm bar. I had the pleasure to meet many fantastic people like Enrique and Ram\u00f3n", "comment": "They\n made  our experience one of a kind. Thank you guys for the best \nanniversary experience ever we enjoyed our stay. I also want to give out\n tour guides Marcos who made our time spectacular going to the ruins", "comment_link": "https://www.tripadvisor.com.mxhttps://www.tripadvisor.com.mx/ShowUserReviews-g150807-d671741-r849853778-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
