INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f6/0d/default-avatar-2020-34.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Maria Cristina B", "title": "Viaje Familiar", "comment": "Me gusto el restaurante Tunkul, la comida estaba deliciosa. Me encant\u00f3 el show en el atrium fue prehisp\u00e1nico, divino. Muy buen ballet. De igual manera el Kids Club todo precioso, en especial las chicas Karla, Nancy, Mirian, Ana y Lili", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r892339223-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ec/3d/default-avatar-2020-1.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "H\u00e9ctor Manuel R", "title": "Coffee and me", "comment": "El comentario est\u00e1 ocasi\u00f3n es para 2 chicas que nos atendieron excelente, sus nombres son Andrea y Laura de Coffee and me dentro de la pir\u00e1mide, el caf\u00e9 sabe delicioso pero sin duda se hace m\u00e1s ameno con el buen trato de ellas definitivamente se ganaron el 10", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r892338546-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Lisbeth Elena O", "title": "Congreso Veterinario del Caribe", "comment": "Excelentes vacaciones, muy divertido, mi hija se la pas\u00f3 genial en el Kids Club en especial con Lili, Miriam, Nancy, Karla y Ana, excelente servicio y actividades. \ud83c\udf89\ud83c\udf89\ud83c\udf89\ud83c\udf89 gracias por su gran labor con los ni\u00f1os!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r892217669-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2022", "author": "Alan T", "title": "Excelente lugar!!", "comment": "Excelente habitacion vista al mar, la limpieza, la variedad de comidas en todos los restaurantes y el servicio. Sobre todo los lugares especiales para los huespedes de pyramide recomiendo 100% y sin duda que volver\u00eda", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r891675401-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Blanca Esmerald... G", "title": "Vacaciones familiares", "comment": "Pasamos unas excelentes vacaciones en The Pyramid. Una especial menci\u00f3n a las chicas del Kids club (Lilia, Miriam, Ana, Karla y Nancy) mi ni\u00f1a de 7 a\u00f1os se la paso incre\u00edble no se quer\u00eda ir\u2026 tienen muchas actividades y \u00e1rea de chapoteadero. Muchas gracias a las chicas por todas sus atenciones. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r891661356-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Edgard J", "title": "The Pyramid Cancun", "comment": "Excelente atenci\u00f3n desde los servicios de Limpieza hasta la Gerencia, un reconocimiento muy Especial para Tristan Gonzalez, quien manejo mi reserva y se ocupo de cada detalle, a gustavo tb de gerencia, que sin contemplar Horarios trataba de satisfacer el capricho de no haber reservado con tiempo un restaurant y/o Show. Recomendable 100% el Hotel y vivir la experiencia The Pyramid. Agradecido a Todos ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r891647752-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Mirtajerez19@gmail.com", "title": "Todo excelente", "comment": "Hola la atenci\u00f3n es excelente, fue muy amable nos  coment\u00f3 las diferentes  comidas, siempre muy atenta a lo que   necesitamos en Le Buffet ellas son Diana Torres y Heidi  Balam, Francisco Canch e Ignici\u00f3n Arbizu", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r891534907-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "berenice del je... c", "title": "Vacaciones en familia", "comment": "Me gust\u00f3 las manualidades, muy bien organizado \ud83d\ude0a Mirian Lili Ana y Karla muy buena atenci\u00f3n hacia los peque\u00f1os, se quedan muy pendientes de ellos y cuentas con unas instalaciones muy bonitas. Muy recomendado el Hotel Grand Oasis Canc\u00fan y el \u00c1rea de Kiddo Zone.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r891497847-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Karengalvan S", "title": "Cumplea\u00f1os", "comment": "Gracias por cuidar los ni\u00f1os, todo muy bonito. Pero falta dar informaci\u00f3n a los hu\u00e9spedes de eventos y lugares de ni\u00f1os. \u00c1rea kids Mirian, Lily, Ana, Karla y Nancy excelente servicio con los ni\u00f1os. . ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r891476304-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Daniel R", "title": "Excelente Atencion e instalaciones", "comment": "Muchas gracias la estancia y atenci\u00f3n estuvo perfecto la calidez y las recomendaciones de Natalia Beltran del happening en el lobby fue excelente!!!! Definitivamente volver\u00e9 con mi familia y amigos pronto.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r891371546-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "./pyr6_files/default-avatar-2020-1.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "jnickolichsells", "title": "Doesn't get better than this", "comment": "Fantastic trip again! Was now my 66th time to Cancun. The Pyramid is Always my go to place for soo many reasons. First before I get into all the particular points I just want to make sure all reading this know to Book Pyramid so you can have the black bracelet and be able to access all that it has to offer you!! I want to thank Carlos Cepeda for all he does for the guests in his position running the resort. Great person that does a great job! Oscar Olvera for directing the operations at night. Another great person that does a great job there. The restaurants are great, Careyes, ran by Hector great job! Sakura, (one of my Favs) ran by Olson and one of the 2 best waiters at pyramid Marco Great job, Dos Lunas, (my other Fav) ran by Ricardo, he is a Great manager and the other of", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r918180418-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/81/default-avatar-2020-43.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Jennifer O", "title": "Awesome vacation! 13 time returning guests :)", "comment": "My husband and I have been to the Pyramid grand oasis 13 times. We just love this resort we have an awesome experience every year. Some of our favorite restaurants  are the white box, the Maki taco, Dos Luna\u2019s. Hippy Chic has great fast food ready to go and a wide selection to pick from.  We took Two other couples with us one was celebrating her birthday and they decorated her room for her. ( Thanks Tristan ) We also enjoyed the Karaoke at the coyote loco. And the Oasis beach club\u2026 always something going on to entertain you. A big huge shout out to Tristan Gonzalez.. Guest service coordinator He goes above and beyond every year To make sure your stay is the best it can be.  They have guys coming to get your drink orders but the pool Which is also a big plus. We will be returning", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r877026360-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "August 2023", "author": "Rebecca D", "title": "Best Place to Stay!", "comment": "We love The Pyramid Cancun! This is our second time to stay and will continue to come back due to the OUTSTANDING staff that make this place great! Virgino Diaz and the entire Spa staff go above and beyond. Elaina and Guillermo provided relaxing massages on the beach while Oscar and Ricardo Perez made sure we had all the food and drinks we needed. The Temazcal staff, Felix & Jiovani, were very knowledgeable. Felipe De Jesus was our server at one of the new restaurants (Sakura) and did an excellent job attending to us. Another new restaurant that has opened is Black Sens. It is definitely worth the reservation! It has a delicious menu and is a truly immersive dining experience. Outside of the staff and food, the facility & views are fantastic and the landscaping is beautiful.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r913559544-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "Nicole K", "title": "AMAZING!!!!! Pay no attention to the negative reviews!!!!!", "comment": "We travelled from Scotland and had looked at many reviews, videos etc. Initially before we booked the negative reviews seemed to be making us very wary and were putting us off booking but we decided that by looking at the videos again and by going on their level of entertainment that we would give them a chance and we are SO glad we did! What an amazing hotel!!!  We booked to stay in the Pyramid which is the black bands and we were not disappointed whatsoever! Speedy VIP check in, daily spotless room that was cleaned perfectly well with fresh towels and mini bar stocked daily also and they always over stocked what you liked the most which was a lovely touch!   The food was outstanding with plenty of variety! Literally the best restaurants we\\'ve ate at in Cancun and we have been", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r911060896-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Haja M", "title": "Beautiful Resort !", "comment": "I just read a few reviews here, and I couldn\\'t believe it was the same resort. Pyramid Cancun is beautiful and very safe, with security at the door. My room was spotless, the pools were clean, and there were so many selections of food that were amazing. There were bars all around the pools, and the drinks were delicious. It\\'s a little big with many people, but not once did I feel unsafe. The staff was friendly, except for one rude encounter. We really enjoyed our stay; it was nice and relaxing.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r907594389-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Kay S", "title": "The Best Holiday Ever Spent with wonderful Friends", "comment": "We spent 10 magnificent days at the Grand Oasis\u2019s Resort at the Pyramid Cancun at the beginning of June with friends, our trip was truly wonderful. We had a fabulous room with an amazing view, very clean and comfortable.  The staff were friendly and helpful we could not fault the hotel in anyway. The minibar was replenished daily by Rodmso Rodrigues Diaz who was always smiling and very friendly.  The waiters/waitresses serving us daily were so friendly and helpful and again always with a smile.  Judith Betel,  Daniela Portilla and Janet Anzures were also very helpful and polite always willing to assist in anyway they could. It was really nice to see managers walking around the site very smartly dressed and always willing to help or assist in anyway and in particular we would", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r898551249-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "August 2022", "author": "\ud835\udd6e\ud835\udd86\ud835\udd92", "title": "Great all inclusive for couple and families", "comment": "Very good experience overall. The hotel is close to the airport (20min away) but an hour away from Playa del Carmen and almost 2hours from Tulum. The hotel theme is nice and the lobby unique. The staff is helpful even if you can\\'t reach the operator (dialing 0) at the very first place. The buffet at the Market Place restaurant is tasty. The view from the restaurant is amazing. The buffet at the restaurant Tul-Kul is also really good, you have to be patient on the line, that place is more crowded. We would like to thank Julio for his service there. The room was convenient and big enough for 3 persons. When we checked in, there were ants everywhere, from the ceiling down to the bedroom wall. They finally fixed it but you can still find some here and there. Our room was cleaned", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r895999722-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Lorenzo S", "title": "Happy hour for kids", "comment": "The kiddO Zone made our stay much better keeping our son busy with all the activities they do for our son. Our son wanted to be here all day and everyday. They took good care of our son and he was happy everyday.. thanks to Lili, Ana, Miriam, Bere, Karla and Nancy. Thanks see you soon ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r894244932-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Yesenia S", "title": "Amazing Care for your Little Ones", "comment": "The people at the kids club were beyond friendly . We never left our son with anyone before besides his grandparents or teachers and the staff members gave us the confidence. We loved the way they were. Since  the first second our son didn\u2019t even wanted to go.  They had fun shows and engaging activities for them at all times and more than 8 girls for a ratio of around 10-15 kids. Our son keeps asking when are we coming back and where there he didn\u2019t even wanted to leave.   We even had one of them as our nanny for a few hours he felt safe and happy . If you go there or if you ever need a nanny or planning of visiting their club at the hotel ask for Berenice as Nanny or look for her, Lili, Karla and Nancy all of them were great .   Thanks Berenice :) The Stirk\u2019s ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r894244550-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Fearless50482823034", "title": "Amazing", "comment": "We came for 9 days.Everything is amazing \ud83e\udd29 I recommend to everyone !!! If you are coming with kids they have a kids club.My kids was there every single day , they loved it \u2764\ufe0f\u2764\ufe0f\u2764\ufe0fSpecial thank you for girls lili,Ana,mirian,bere ,karla,Nancy ..They are very patient,kids had a lot of fun!The show was amazing for kids!Good is great.We enjoyed every second of our vacation!!! Definitely coming back next year!!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r894243720-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "./pyr1_files/default-avatar-2020-1.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "jnickolichsells", "title": "Doesn't get better than this", "comment": "Fantastic trip again! Was now my 66th time to Cancun. The Pyramid is Always my go to place for soo many reasons. First before I get into all the particular points I just want to make sure all reading this know to Book Pyramid so you can have the black bracelet and be able to access all that it has to offer you!! I want to thank Carlos Cepeda for all he does for the guests in his position running the resort. Great person that does a great job! Oscar Olvera for directing the operations at night. Another great person that does a great job there. The restaurants are great, Careyes, ran by Hector great job! Sakura, (one of my Favs) ran by Olson and one of the 2 best waiters at pyramid Marco Great job, Dos Lunas, (my other Fav) ran by Ricardo, he is a Great manager and the other of", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r918180418-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "./pyr1_files/default-avatar-2020-43.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Jennifer O", "title": "Awesome vacation! 13 time returning guests :)", "comment": "My husband and I have been to the Pyramid grand oasis 13 times. We just love this resort we have an awesome experience every year. Some of our favorite restaurants  are the white box, the Maki taco, Dos Luna\u2019s. Hippy Chic has great fast food ready to go and a wide selection to pick from.  We took Two other couples with us one was celebrating her birthday and they decorated her room for her. ( Thanks Tristan ) We also enjoyed the Karaoke at the coyote loco. And the Oasis beach club\u2026 always something going on to entertain you. A big huge shout out to Tristan Gonzalez.. Guest service coordinator He goes above and beyond every year To make sure your stay is the best it can be.  They have guys coming to get your drink orders but the pool Which is also a big plus. We will be returning", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r877026360-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "August 2023", "author": "Rebecca D", "title": "Best Place to Stay!", "comment": "We love The Pyramid Cancun! This is our second time to stay and will continue to come back due to the OUTSTANDING staff that make this place great! Virgino Diaz and the entire Spa staff go above and beyond. Elaina and Guillermo provided relaxing massages on the beach while Oscar and Ricardo Perez made sure we had all the food and drinks we needed. The Temazcal staff, Felix & Jiovani, were very knowledgeable. Felipe De Jesus was our server at one of the new restaurants (Sakura) and did an excellent job attending to us. Another new restaurant that has opened is Black Sens. It is definitely worth the reservation! It has a delicious menu and is a truly immersive dining experience. Outside of the staff and food, the facility & views are fantastic and the landscaping is beautiful.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r913559544-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "Nicole K", "title": "AMAZING!!!!! Pay no attention to the negative reviews!!!!!", "comment": "We travelled from Scotland and had looked at many reviews, videos etc. Initially before we booked the negative reviews seemed to be making us very wary and were putting us off booking but we decided that by looking at the videos again and by going on their level of entertainment that we would give them a chance and we are SO glad we did! What an amazing hotel!!!  We booked to stay in the Pyramid which is the black bands and we were not disappointed whatsoever! Speedy VIP check in, daily spotless room that was cleaned perfectly well with fresh towels and mini bar stocked daily also and they always over stocked what you liked the most which was a lovely touch!   The food was outstanding with plenty of variety! Literally the best restaurants we\\'ve ate at in Cancun and we have been", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r911060896-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Haja M", "title": "Beautiful Resort !", "comment": "I just read a few reviews here, and I couldn\\'t believe it was the same resort. Pyramid Cancun is beautiful and very safe, with security at the door. My room was spotless, the pools were clean, and there were so many selections of food that were amazing. There were bars all around the pools, and the drinks were delicious. It\\'s a little big with many people, but not once did I feel unsafe. The staff was friendly, except for one rude encounter. We really enjoyed our stay; it was nice and relaxing.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r907594389-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
