INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "./pyr2_files/default-avatar-2020-48.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Fadi K", "title": "Incredible Time", "comment": "Stayed here for a week at the Pyramid VIP, Great service provided by staff, especially Frank at the Pyramid bar (front of white box) Was very helpful with a lot of knowledge about the resort. Always smiling and amazing drinks. Will return for the experience", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r893409897-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "./pyr2_files/default-avatar-2020-47.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Raquel A", "title": "Spa Sensoria is great", "comment": "Spa Sensoria is one of the best spas I\u00b4ve ever been to. I got the chance to take my massage at the beach. Isabel was very kind and professional. I really enjoyed my stay. The massage was just amazing. I am pretty sure I will come back.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r893003098-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/42/default-avatar-2020-20.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Mikail adam Alakozai", "title": "Great Customer Service", "comment": "I had a very great experience at the pyramid. Mr Carlos Cepeda and his staff made sure I was taken care of at the time of my check in and check out . Him and his staff were very nice throughout my entire stay at the hotels. Lucelly was also very helpful when I had questions and concerns about hotel activities. There are alot of great people the resort ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r892337418-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ec/3d/default-avatar-2020-1.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "jnickolichsells", "title": "Best of the Pyramid", "comment": "My 65 trip to Cancun and I believe my28th to the Pyramid! Just fantastic as alwaysI Can\\'t get enough of that place! Matter of fact just booked today fpr Aug 2nd for another 2 weeks!! The upper management, Ricardo De La Pas, Carlos Cepeda and Oscar Overa are continuously working on upgrading the property and customer satisfaction. Ex. Putting in new spa area as we speak, Built a new huge gym with it ALL, New restaurant overlooking the gulf and the eating in the dark dining experience  just to mention a few of the changes you will see!! NOW FOR THE IMPORTANT PART... You MUST book Pyramid to access all these great things with Black bracelet!! The staff is the best,, From Humberto (Beto) at the cigar bar to the Fountain Bleau staff, Ernesto, Carlos, Nathaniel and Hector.A special", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r891770507-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/5d/default-avatar-2020-32.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Monika m", "title": "Awesome resort!!!", "comment": "Great hotel overall! Definitely do black band for access to most of the hotel. Food is far better then any Mexican resort I\\'ve stayed at thus far! Amazing atmosphere and so much to do. Erick at oasis Beach club is amazing and went way above and beyond to make our experience amazing! Definitely should get a raise! Would definitely come back! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r890086614-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ec/3d/default-avatar-2020-1.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Storyteller716446", "title": "The Miramar Beach Club and Restaurant/Grand  Oasis Hotel", "comment": "The hotel rooms  were a little dated and house keeping was lacking, they  did not restock refrig with waters daily but hotel grounds were beautiful. I would like to acknowledge The Miramar Restaurant and Bar for there excellent service and hospitality! We rented a private cabana for 4 days and it was well worth it! Jose was very attentive and friendly! We never went without food or drinks and it was always service with a smile! Edgar the manager and the rest of the staff was great too! Excellent food and drinks! We will be back!    ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r889712980-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/42/default-avatar-2020-20.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "LoveTravel596366", "title": "Amazing staff! Thanks for the experience.", "comment": "Amazing staff. Thanks Oscar by the kids pool. Oscar at Fountain blue. and Fielipe at Kinky. Best workers at the resort! Made the experience so much better.  The overall experience was much better. These guys made sure I have fun. Will definitely come back!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r889140042-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e3/1f/default-avatar-2020-46.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "CNC1980", "title": "Pool & Beach Paradise", "comment": "This was my 3rd time staying at Oasis Property in Cancun however this was my first time staying at Pyramid at Grand Oasis compared to Grand Oasis.  Pyramid had many more options for food and always had something great to eat. Always enjoyed the shows especially the Caribbean show and all the aerial art acts. The app is very useful as you can contact customer service and reserve restaurants. A huge shout out to Tristan Rocher, Oasis representative as he worked very closely with me via emails to make sure I had the best experience despite me being on crutches. He was always friendly and willingly to help.  I appreciate his dedication and work ethics. Overall, Oasis property is huge and offers a variety for all ages.  I loved my plunge pool and all the bali beds and pools on this", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r888853894-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "./pyr2_files/default-avatar-2020-48.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Whitney M", "title": "Love love love this hotel", "comment": "My husband and I love this hotel I surprise him last year for his birthday to this hotel and he just fell in love with it so we visited again this year in march 2023 he even visited some of his friends and they came along as well. The hotel as so many restaurants we didn\u2019t get bored of the food and also activities to do we would like to go again next year. The hotel and the staff is amazing on my last day I had a massage booked, but I forgot my necklace in the spa and only noticed when I was at the airport. I didn\u2019t want to risk it to go back to the hotel so I waited until I got back to england and I emailed them and Tristan Rocher made sure that I got back my necklace and I am so grateful. Would definitely recommend this hotel. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r887741016-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/9c/default-avatar-2020-14.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "jamesdhammal2", "title": "My own personal coffee shop \u2764\ufe0f", "comment": "Really enjoyed our stay, the coffe shop (Coffee and Me) was amazing and a real selling point of the resort. A big thank you to Guadalupe and Laura for keeping us caffeinated and happy, they were both extremely helpful and patient, always happy and always smiling. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r887136618-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "./pyr3_files/default-avatar-2020-11.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Sherpa686007", "title": "Our favorite resort", "comment": "We were dining at Cariyes restaurant  And Adrian, Johnny and Owen  Provided an excellent service! Thank you so much! We will be back! Food and drinks were also great! We love this resort and are regular  Costumers.  It\\'s our 7th visit  Larissa Harwood ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r886935182-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "./pyr3_files/default-avatar-2020-2.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Erika C", "title": "Nice place to stay .", "comment": "Kiddo Zone - best place for the kids all the staff very friendly . My daughter and her cousin had a great time always looking forward for the next day activities . Thank you to Mirian , Bere,lili, Karla , Nancy, Ana for all the attention to our girls .. also a bug thanks to Rafael Jarvio for the tennis lessons , they had a great time ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r886803994-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Brian W", "title": "Spring break", "comment": "Ricardo at the pool bar was the best, he took great care of us, and we will be ba5. Great time during \"happy hour\". We will be back. A great value in Cancun. A 5 day trip during spring break and the resort did not seem over crowed.Great location for us. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r884974067-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Jon S", "title": "Relaxing", "comment": "Excellent vacation. Food was very good. Buffet was tasty and sit down restaurants were plentiful and delicious. Pool was quiet on the sides and beach was perfect! We stayed in the Pyramid so we had more options than regular vacationers. Room was basic.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r884579473-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "E A", "title": "Great Family Destination!", "comment": "We had a wonderful trip for our first time as a family to Cancun and the Pyramids at Grand Oasis. We have a 3 year old and 5 year old and the kids club was amazing for them!  We met the sweetest women, Mirian,Lili, Ana, Bere, Nancy, Karla, and felt so comfortable with our children in their care.  They have wonderful activities for all ages and every day is different!  Some of our favorite activities have been the swimming games, trampoline time, the most incredible face painting, beautiful crafts and group games!    The pool is incredible and perfect for all ages and swimming ability!  We especially loved posting up near the zero depth entry! Very clean! Towel service included!  Plenty of chairs for everyone, claim chairs early if you want a certain spot!   The food variety was", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r884531011-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "castonplace", "title": "Loved this resort!", "comment": "My family stayed at the Grand Pyramid Oasis for 5 nights during spring break week this year.  I am so glad that we chose this hotel as we had a fabulous time!  We booked this vacation through Jetblue vacations so I don\\'t know if you get to pick which building you are in if you book directly thru the hotel but would strongly recommend staying in the main building (Sian Ka\\'an) .  We had 2 rooms booked and both were fabulous but be aware that it is very loud at night (esp during spring break) as doors close and the sounds carry/echo.  Our rooms were very clean and housekeeping came daily to clean the rooms and they were restocked with water/beer/snacks.  The only thing they didn\\'t restock was our beach towels which were wet on day 3 so we left them on the floor and they were taken", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r883494744-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2023", "author": "shaughnessy f", "title": "Fontainebleau", "comment": "We had a great stay at the pyramid! Carlos Dguez and H\u00e9ctor at the Fontainebleau bar were the best bartenders ever! They were very kind and speedy! Treated us like family and made the best mojitos and lemon drop martinis! We went to this bar every night of our 10 night trip. We already miss them! They deserve a raise!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r882962691-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2023", "author": "Vlm", "title": "Exceptional Beach Service", "comment": "On the whole, we had a great experience at The Pyramid. The shows and entertainment were top-tier, and the staff throughout the resort were phenomenal. If you end up by the beach near the Sian Ka\u2019an, Luis Garc\u00eda D\u00edaz is your guy! He is super attentive, friendly, and has the most exceptional service. Make sure to tip the wait staff!! They are super gracious and appreciative!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r879984190-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2023", "author": "Nancy L", "title": "Excellent maid!", "comment": "Our room cleaner was Guadalupe Yak and she did a great job at keeping our room clean and stocked. She was very friendly and since we speak Spanish we enjoyed talking to her every morning. We also enjoyed the Hippie Chick bar and went everyday to hang out with the bartender Pedro Can. He is a hard working guy and very friendly. He informed us of various activities around Cancun. Will actually miss talking to him everyday.  We stayed in the Pyramid Ocean Front room which was nice and quiet. The only noise was the ocean which was wonderful.!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r879355877-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2023", "author": "Chad T", "title": "Cigar corner", "comment": "Humberto aguirre is an excellent and knowledgable bartender and cigar conisseuer. We\\'ve been visiting for the last 2 years from the US and look forward to seeing Humberto every time we visit. I would highly recommend checking out the cigar corner, you won\\'t be disappointed!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r879231000-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/66/default-avatar-2020-53.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Jorge M", "title": "Second home.", "comment": "The best option for a friends vacation or a family vacation. Yanin junco, Diego P\u00e9rez and Brandon meneses are the best option and best staff. This hotel has been the to go place for many years. Would recommend this hotel to anyone who\u2019s looking for a quiet time or a fun one.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r873248827-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-o/0a/8d/01/ad/toby-s.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Toby S", "title": "A beautiful ocean view oasis", "comment": "This hotel is beautiful!! From the moment you enter the lush oasis courtyard,  to the stunning ocean views  off the spacious 2-level patio. The expansive pool meanders through the lush courtyard creating a sense of spaciousness and abundance of choice to find just the right spot to relax in the sun.   Whether sunny or stormy, the brilliant turquoise view from my room and patio was captivating. The staff were all helpful and friendly and the quality of food and presentation in each of the restaurants offered something to delight a variety of tastebuds.  ( I especially enjoyed Makitaco- the presentation of food and combination of flavors made it my go-to!)  I\u2019d like to acknowledge one person who really stood out to offer exceptional customer service, and give you a bit of", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r872848473-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Chris C", "title": "Great resort, great value.", "comment": "This place provided an excellent vacation.  My expectations were exceeded in every category.  I was a little nervous after reading some of the negative reviews and am so glad I didn\u2019t let them sway my decision.  I guess everyone has their own opinions of what a resort should be, but I feel that for what we paid, the value could not have been exceeded.  I was totally satisfied.  Everything was easy from start to finish.  We were picked up at the airport, checked in and given the basic run-down of the resort.  The information was excellent and the staff was very helpful to maximize our stay.  They made our reservations at the upscale restaurants for us and were always eager to help.    The room was nice. Bed very comfortable.  We stuck to the standard room, but still had a bit of", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r872752910-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "carolina c", "title": "Amazing spa experience", "comment": "Hotel is amazing, lots to do, great shows every night.  Many restaurants to choose from and the food is amazing!  I visited the Sensoria spa for a massage with Isabel and it was one of the best massage I\u2019ve ever had. She\u2019s experienced, very professional, and pleasant. Truly an amazing experience. Muchas gracias Isabel! We also enjoyed the coffee shop every day, great cappuccino\u2019s. I would suggest making the coffee available for sale we were very sad that we weren\u2019t able to take some bags of coffee back with us.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r872710251-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "jnickolichsells", "title": "Only the Pyramid !!", "comment": "First my 63rd time to cancun and my 24th to the Pyramid. A special thanks to Ricardo De La Pez for his excellent job in running the pyramid along with Carlos Cepeda and Oscar Olvera for being the oil that runs this fine machine, Their interactions with the customer base is done in a very caring and helpful way to make all feel welcome there.                             I can\\'t say enough about the staff of the Fontaine Bleau,  Humberto, Carlos, Ernesto, Hector and Nathaniel. These men are excellent in their jobs there. All with a smile and make you feel welcome by always going out or their way to make the customer happy, Just a perfect team!! Tip them !! The Pyramid beach Bar staff does a great job and always entertain customers with their flash with a  bottle tricks. The beach", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r872525453-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Sonya C", "title": "Phenomenal birthday trip", "comment": "I\u2019ve been going to grand oasis Cancun since 1994. The staff is amazing. My check in was quick and see less. They work extremely hard to make every guess, have a memorable stay. The grounds are clean and beautiful. The entertainment is wonderful. So many things to do. Never a dual moment. There are a lot of bars and restaurants available at all times. It was birthday and the staff went above and beyond to make be feel special with some surprises in my room. The resort has made several upgrades since my last visit, which was a really nice improvement. My room was phenomenal. It was spacious and very clean. It was very cool and refreshing, on the hot days. The beach is clean and the ocean is breathtaking. I\u2019m so familiar with resort, it feels like home.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r871653658-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Ashley", "title": "Bar Fontaine  bleau", "comment": " Humberto...natanael..hector... bar fontaine bleau is the best bartender ever! He made the best drinks and he made our reservations last minute for us. He was so attentive without needing to be. Made our stay absolutely amazing by adding that extra little bit! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r871645032-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Jerri D", "title": "Girls' Trip", "comment": "We traveled to Cancun in November and what a wonderful trip. The Pyramid at Grand Oasis offered a great experience.      The staff was amazing, so helpful and friendly.   Humberto, who carried our luggage to our rooms gave us \"must dos\" suggestions and a \"tour\" from our balcony...simply the best.  One of the friendliest employees we interacted with.  All the employees were super friendly and truly helpful.   There were many food choices offered and a wide variety of restaurants. I suggest making restaurant reservations as soon as you check in because they tend to fill up fast.  My favorite place was Sakura - offering an Asian Fusion cuisine .  It is a fairly new restaurant and the wait staff was attentive and provided anything we needed or wanted.  The grounds were beautiful,", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r871591950-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Emily B", "title": "Humberto..Nathanael", "comment": "Fontaine bleau bar Humberto natanel is the best!! Best drinks on the resort along with the best service. Will make anything you want! English is very good. Easy to talk to. Creative with drinks if you aren\u2019t sure what you want. Go find him at the pyramid and he is your man!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r871548892-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Kendra V", "title": "Bar Fontaine blues", "comment": "Humberto and Natanael at Fontaine bleau bar. Show was great!! The drinks were the best out of the entire resort. Absolutely loved all the drinks. Staff was awesome. Really good drinks and shots were super. Would confide a must see if you are staying or visiting around here.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r871548601-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/bb/default-avatar-2020-65.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Marisa W", "title": "fontaine bleau bar", "comment": "Humberto ..Natanael at..fontaine bleau bar  this was so cool & the best bar they treat you very nice the drinks are strong the hotel is nice tje food is Amazing i love cuncun im going to come back and bring my family all the staff was very accommodating \u2026 ooooh and i met Jeff lol", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r870665346-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "CrystAl N", "title": "Awesome resort!", "comment": "The resort was nice, great reviews but what really made the experience memorable was the bartenders Humberto and Nathanael at Fontainebleau they went above and beyond to make our stay special. Overall great place. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r870561444-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Carl B", "title": "Loved visiting the Fontainebleau. It had great live music every evening and amazing staff that were super friendly !", "comment": "The Fontainebleau bar is perfect for a drink and a good time. Humberto Aquiree and Natanael Quintal gave excellent bar service. Ever night there is entertainment and good vibes! You are bound to meet new people!  I would definitely be coming back here again.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r870371364-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Andres O", "title": "Nicole Capetillo did an Amazing job organizing the engagement package! :)", "comment": "I ended up proposing during my trip at the Pyramid at Grand Oasis Cancun. Coming from the US it\u2019s obviously super difficult to organize in a foreign country, but Nicole Capetillo did her due diligence, and exceeded expectations  from start to finish. Everybody at the hotel speaks both English and Spanish, so no worries! We had been communicating through WhatsApp at least 3 weeks before (although it\u2019s recommended at least 5 weeks in advanced) and was still perfectly executed!  Nicole did a good job maintaining the surprise (including the rest of the staff), and even though the weather wasn\u2019t as ideal on the day of the proposal, she had a plan B and C already lined up.  As the a whole, the service at the hotel is amazing.   My fianc\u00e9 and I really enjoyed our trip as a whole, with", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r870169971-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Levy R", "title": "Tapas de los bazos", "comment": "Todo perfecto. Guadalupe y Laura estaban genial \ud83d\ude00. I wouldn\u2019t survive without them. The placement was amazing with an immediate passage to the pools, beaches, and restaurant\u2026 im very grateful for my experience at coffee and me.  ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r870105920-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Julie P", "title": "Bar Fontaine bleau", "comment": "Bar Fontaine bleau..   Humberto..Natanael - amazing service - amazing staff - love this bar. They had the best tequila. Would recommend here to anyone coming to Mexico!!! Had a amazing time they are the best!!!. Came from Canada and met so many great people. Great pills. Great beach. Great drinks. 12/10", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r870065042-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Brianna S", "title": "Humberto..Nathanael", "comment": "Bar Fontaine Belau Humberto .. Natanael . Coming all the way from Chicago I feel at home. They serve Don Julio and other top shelf liquors. He was very attentive and informative.  I learned about the different liquors origin and taste. This was WAY ABOVE my expectations. I was looking high and low for a bar that served top shelf liquors and boy when I tell you I found this one! I was delighted. Great service. Liquor thy won\u2019t give me a hangover in the morning plus my Black VIP Wristband\u2026. I can\u2019t lose.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r870061818-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Amr", "title": "Magnificent is an understatement ! <3", "comment": "Our flight was delayed so we arrived late to the hotel. We found that our reservation at Pyramid grand Oasis was canceled as a result of no show on time. The checkin desk tried to accommodate us in one of the regular rooms until the next day so they can transfer us to the pyramid. Pamela Solis called us the following morning offering us a free upgrade to a master suite at the pyramid ocean front. She also provided us some complimentary drinks and food and delivered it to our upgraded master suite. Lastly, she made sure to extend our checkout for our convenience. Pamela has handled the situation perfectly and we couldn\\'t be happier. As for the hotel it self, its a great lively hotel with lots of amenities and activities! Thank you Pyramid Grand Oasis staff. Thank you Pamela,", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r869763554-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Pedro G", "title": "A Chef and Concierge Employee Made All The Difference!", "comment": "This resort can definitely be a bit overwhelming with all the amenities it has to offer. I have to admit that the first couple of days my partner and I attempted to navigate making dinner reservations etc. on our own but had a hard time. Major tip, download the hotel app for some guidance and quick references. We were lucky enough to have the help of Rafael at the Concierge desk in tower 4 I believe. Without Rafael our trip would not have been as pleasant. Throughout our entire stay he assisted with restaurant reservations, where to shop, activities recommendations, and helped us navigate our way through the resort. From something simple as to where to find beach towels to how much a taxi would be to take to the airport, Rafael was Amazing and the main reason why we would stay", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r869279272-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(1, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Marisa M", "title": "Bar fontainebleau", "comment": "Humberto Aguirre and Natanael Quintal were the BEST bartenders on all of the resort. They were respectful, kind and outgoing as well as having the best service on the entire resort. We\u2019ve had nothing but a good time and AMAZING drinks. I recommend to come by this bar and experience it for yourself, you would not regret it. Easy 10/10. Thank you!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d8009670-r869175500-The_Pyramid_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
