INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "October 2023", "author": "Adriana priscil... H", "title": "Ricardo. Excelente servicio", "comment": "Excelente servicio y la mejor atenci\u00f3n. Una persona muy profesional, carism\u00e1tico y con un servicio extraordinario. Sin duda una de las mejores atenciones que he recibido por parte de una persona .  Ricardo es un empleado ejemplar que se sabe ganar el carisma de la gente", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r921183051-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
