INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "October 2023", "author": "Darinel B", "title": "Mis vacaciones en Cancun", "comment": "IGNACIO FERNANDEZ  ES UN MUUUY BUENO EN SU ATENCION! Nos gusto mucho su peculiar forma de ser 10/10. Esta en rest. Tunkul Omelet Station  La experia en el restaurante fue lo que mas nos gusto, la comida es buenisima y variada ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r919907700-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
