INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "joselline klunder", "title": "EXCELENTE SERVICIO Y ATENCI\u00d3N PERSONALIZADA", "comment": "La atenci\u00f3n de todo el personal incre\u00edble ! En particular de la se\u00f1orita Erika Parrao y \u00c1ngel Cetz al igual que la concierge Dinnora , tuvimos una estancia particularmente c\u00f3moda  limpieza inigualable y un servicio de bienvenida espectacular para mi y mi esposo", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r917721026-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "pilar h", "title": "Una experiencia incre\u00edble", "comment": "Es una excelente experiencia, el lujo y el confort es inigualable, el servicio de sus colaboradores es inigualable, en especial del se\u00f1or Carlos Landa, la mejor recomendaci\u00f3n es buscar a Carlitos en la placita, el les garantizar\u00e1 de una noche incre\u00edble y muy bien atendidos....  La piscina impecable, los restaurantes tambi\u00e9n y las habitaciones s\u00faper c\u00f3modas...", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r917454738-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "Regina L", "title": "Mis vacaciones", "comment": "Excelente atenci\u00f3n en restaurante makitaco de pir\u00e1mide, en especial al mesero Roberto Gordillo y a la hostess Montserrat Jim\u00e9nez, nos trataron de maravilla, la habitaci\u00f3n incre\u00edble la vista al mar, definitivamente regresar\u00eda.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r917447347-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "V\u00edctor O", "title": "El mejor grito", "comment": "Hermosas vacaciones en familia ,la playa muy bonita, la comida muy buena, esperamos regresar, con m\u00e1s familia,especialmente la atenci\u00f3n muy buena de Carlos Landa, mesero de la placita. Gracias por su atenci\u00f3n, esperamos regresar ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r917285483-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Rene A", "title": "De vuelta al Oasis", "comment": "Maravillosa experiencia!!!! Todo el personal fue atento y amable, nos brindan siempre su apoyo incondicional, en Le Buffet el se\u00f1or, Arturo, Carlos y Luis nos dieron una atenci\u00f3n excelente. En l piscina Alonso, y mis bbs son cuidados excelentemente en el Kids club por Faviola, Karla, Zeidy y Jessica.  En especial queremos agradecer a la Se\u00f1orita Leidy, Judith y Monica, xq siempre est\u00e1n atentas cuando realizamos alguna observaci\u00f3n o requerimos apoyo.   El restaurante Careyes como siempre espectacular y delicioso, el at\u00fan que fue o recomendaci\u00f3n del Cheff exquisito.   Como siempre esperamos regresar pronto", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r917199693-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "Azul P", "title": "Desayuno en market place", "comment": "Desayunar en market es delicioso, variado, fresco y confortable. Hay opciones para todos los gustos. La atenci\u00f3n de Gabriela es destacable, impecable. Disfrutamos 7 ma\u00f1anas magn\u00edficas que jam\u00e1s olvidaremos. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r916876530-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "Ivan V", "title": "Cena", "comment": "La atenci\u00f3n de Ricardo Jim\u00e9nez exelente al igual que la de isabelino la atenci\u00f3n s\u00faper buena y recomendable la comida estubo de maravilla regresar\u00eda sin problema pero con la atenci\u00f3n de los maestros todos son muy agradables ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r916302665-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "August 2023", "author": "Ricardo E", "title": "GRAN OASIS CANCUN - ESTANCIA SATISFACTORIA-", "comment": "Muy buena ubicaci\u00f3n  A pesar de haber mucha gente la atenci\u00f3n fue buena  Buen servicio de todo incluido, ojo con los horarios de los desayunos , ya que terminan a las 11am , desayuno , comida y cena Bufet variado y rico, varios restaurantes sin tener que reservar.  Nunca pude reservar en los restaurantes que lo requer\u00edan. Buen servicio de bebidas, con alcohol b\u00e1sico, pero pod\u00edas pedir otros cocteles.   La fila para las toallas de alberca y playa algo larga.  Fuimos en temporada alta , el check in lo hicimos a las 10:30 pm y solo hab\u00eda 2 personas antes que nosotros, en otros horarios si vimos muuucha gente.  Las habitaciones antiguas , limpias y funcionales , me hicieron la limpieza todos los d\u00edas que estuvimos , no necesit\u00e9 que me dejaran m\u00e1s jabones ni papel higi\u00e9nico , pero lo", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r915201890-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/91/default-avatar-2020-62.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Faustina T", "title": "Coffee and you", "comment": "Muy lindo el servicio y muy amables!!!!  Todos los d\u00edas muy amables, ya nos conoc\u00edan al segundo d\u00eda en coffee and you las chicas magali escobar y clara torres!!!!  Nos indicaron que hacer y nos recomendaron actividades, siempre a disposici\u00f3n!!!! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r914322374-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "./goc3_files/default-avatar-2020-35.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Luciano I", "title": "Market Place Pyramid Oasis", "comment": "Excelente atenci\u00f3n de Sarah\u00ed y Alejandro, siempre bien predispuestos y con una sonrisa, solicit\u00e1bamos cosas que que no estaban dentro del bufet como leche caliente y todos los d\u00edas nos esperaban con eso. Muy buena experiencia.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r914254204-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "August 2023", "author": "Vacation18035737891", "title": "Las mejores vacaciones y celebraci\u00f3n de cumplea\u00f1os!!!", "comment": "Reconocemos y agradecemos inmensamente al Chef Jair Ordo\u00f1ez Aguilar, a Ignacio Arbisu Rodriguez y a Morales Dalila, mesero y cocinera B de Market y a David L\u00f3pez  Buend\u00eda Sushero A de Makitaco. Fue una experiencia incre\u00edble!!! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r912717413-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "salvador s", "title": "Regresar\u00eda solo por el impecable trato del bartender. Hace muy agusto la estancia.", "comment": "El bartender Eder Rodr\u00edguez castillo que esta en bar Villa Paola tiene una atenci\u00f3n excelente a sus clientes, un trato espectacular a la hora de servir, y es s\u00faper educado, la verdad es que hace un trabajo con much\u00edsima pasi\u00f3n, y se preocupa por los que no sabemos mucho, para poder hacernos recomendaciones a nuestro gusto, algo que no me habia tocado en otros lugares y la verdad es excelente eso!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r912505112-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "August 2023", "author": "Miriam P", "title": "Excelente experiencia", "comment": "Excelente servicio de todo el resort. Personal muy amable. Destaco principalmente a Sarahi, anfitriona del restaurante \"Market Place\". Habitaciones c\u00f3modas, excelentes piscinas y servicio en general. Si vas en familia lo pasar\u00e1s bien, igualmente si vas con amigos.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r911733275-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "August 2023", "author": "Blanca D", "title": "Excelente fusi\u00f3n", "comment": "La comida es riqu\u00edsima, el servicio es muy bueno, son porciones peque\u00f1as para que pruebes muchas cosas pero sales satisfecho, casi prob\u00e9 todo lo del men\u00fa, mi favorito fueron los conos y el wakame con pulpo, pero en general todo me gust\u00f3.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r910620510-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Elizabeth Herrera", "title": "Regresamos a donde fuimos felices", "comment": "Es un excelente hotel muy grande con muchas opciones para todos los gustos, en esta ocasi\u00f3n fui con mis hijos y hay como siempre muchas actividades para ellos, las chicas del Kids club dan tanta seguridad que me pude relajar mientras ellos estaban bien entretenidos, pero sobre todo muy bien cuidados. Siempre encuentras a lo largo del d\u00eda muchas actividades tanto para ni\u00f1os, j\u00f3venes y adultos. Muy completo el hotel, pero sin duda lo que hace la mejor de las estancias es el personal siempre atento y amable. Resuelven cada situaci\u00f3n tan r\u00e1pido como les es posible.  Tomamos 1 clase de tenis y el encargado muy atento, amable y sobre todo empatico, ya que ni mi hija ni yo ten\u00edamos ni idea del deporte. Fue muy amable. Sin duda volveremos a tomar m\u00e1s clases en nuestros pr\u00f3ximos viajes. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r910051003-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
