INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "./goc5_files/default-avatar-2020-29.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Alfredo C", "title": "Pedida de matrimonio.", "comment": "Excelente servicio para pedidas de matrimonio, mi novia ahora prometida qued\u00f3 maravillada, todo organizado por Samantha Rodr\u00edguez y Mariana L\u00f3pez, gracias infinitas, todo excelente, nos llevaremos en el coraz\u00f3n el recuerdo.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r909599932-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "./goc5_files/default-avatar-2020-63.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Omar R", "title": "Health Bar", "comment": "El mejor bar, las bebidas preparadas por Oscar Serafin as\u00ed como su servicio es de lo mejor, recomiendo ampliamente asistir al bar y solicitar cualquier servicio ofrecido por Oscar, bar a pie de playa, con hermosa vista. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r909104773-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ea/2e/default-avatar-2020-67.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "susana s", "title": "En general todo bien, destac\u00f3 la atenci\u00f3n del chico que sirve los tragos en villa Paola,  Adri\u00e1n Hern\u00e1ndez Castillo.", "comment": "Excelente servicio, todo muy limpio y en orden, cabe destacar la atenci\u00f3n que nos brindo el chico de villa Paola Buffet, su nombre es Eder Adri\u00e1n Hern\u00e1ndez Castillo, vayan con el los atender\u00e1 de maravilla.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r908651405-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/9f/default-avatar-2020-16.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Jacqueline M", "title": "Excelente servicio \ud83d\ude0d", "comment": "Excelente servicio, es la segunda vez que me hosped\u00f3 en este hotel y segunda vez que mi ni\u00f1o sale encantado del kid club, es incre\u00edble las chicas que est\u00e1n ah\u00ed ( Ana, Lilia, Miriam y Berenice) les ponen a hacer actividades. Juegos guiados y las muchachas son super atentas con ellos, les recomiendo ampliamente traer a sus peque\u00f1os aqui, fuimos por su cumplea\u00f1os de mi ni\u00f1o y le cantaron las ma\u00f1anitas y tuvieron mucha atenci\u00f3n con el lo hicieron sentir especial en su cumplea\u00f1os.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r908391285-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/9f/default-avatar-2020-16.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Ortiz velazquez V", "title": "Muy atento a los servicios", "comment": "En el bar Heard Una persona  muy amable excelente servicio con \u00d3scar 100% recomendable las bebidas con Oscar, muy amable y accesible, nos encant\u00f3 a mi y a mi familia, m\u00e1s personas as\u00ed. Gracias por todo !!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r907868894-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ee/10/default-avatar-2020-8.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Nicole G", "title": "Excelente Servicio", "comment": "Me pareci\u00f3 excelente, muy buen servicio y muy amables. Servicio de parte de un mesero llamado  Edwin Fern\u00e1ndez y de la cocinera Yasmin Bautista del restaurante Akeru. Me pareci\u00f3 genial la comida, excelente todo.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r907464374-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
