INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/d0/default-avatar-2020-33.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Sara A", "title": "Excelente cena, muy recomendado!", "comment": "Excelente Servicio y la comida exquisita, los alimentos frescos y muy gourmet ! La atenci\u00f3n estupenda  Edwin Fern\u00e1ndez fue mi mesero el cual nos brind\u00f3 una atenci\u00f3n incre\u00edble y Monserrat Gongora en el restaurante Akeru nos ubic\u00f3 muy r\u00e1pido! Muchas gracias ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r907086827-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/5d/default-avatar-2020-32.jpg", "date_comment": null, "date_stayed": "December 2023", "author": "G B", "title": "Awesome stay", "comment": "My stay was great as always , Dinorah the concierge is awesome she help us with everything we need make reservations and treat me and my friends really kind , she really  make us a better and easy stay , I have a great stay every time I come to oasis is my favourite hotel ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r928179298-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/bb/default-avatar-2020-65.jpg", "date_comment": null, "date_stayed": "March 2022", "author": "Douglass W", "title": "My EXCELLENT, REPEAT, great stay at the Grand Oasis!", "comment": "Now people, by reading my review of the GRAND OASIS HOTEL in Cancun, I have been asked\u2026 IS THIS A 5 star hotel? To which, I answer \u2026 no\u2026 TO ME ITS A 10 STAR HOTEL!!! Why, you ask?? Well, from the moment you\u2019re greeted at the front door, you\u2019re treated as family\u2026.a returning guest\u2026.then whisked into the check in area\u2026the utmost of courtesy\u2026receive your bracelets, then await your luggage in a gorgeous room overlooking the vast pools and ocean.  After unpacking, WE prefer a fine luncheon in the MAKI TACO restaurant\u2026.fine Japanese sushi with tempuras , fried rices, noodles\u2026.and with a Mexican touch!  The bar choices and house wine selections are getting better with each trip\u2026. Seriously!  Then to the pool or beach to unwind\u2026.next a little practice siesta outside to dream about", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r833439036-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "Mar D", "title": "Villa Paola - Francisco", "comment": "If you ever visit Grand Oasis, you must go to Villa Paola. There is a one of a kind person. Person who knows his craft. His name is Francisco. Not only he will make you best drinks, he will make you feel very welcome, and he will teach you a thing or two about good drinks. Thank you Francisco for giving us amazing experience in Mexico. Thank you from your Croatian friends.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r928013092-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Jordan C", "title": "Cocao Grill: 5+ Stars for Breakfast!", "comment": "Cocao Grill is the best kept secret at The Pyramid, Grand Oasis Cancun Resort. Clearly 5+ Stars for this luxurious restaurant serving the best breakfast you\\'ll ever have in the Caribbean. Right on the ocean, picture perfect view, and the most unbelievable food and service for breakfast, lunch or dinner. You have to try the homemade waffles! A big shout-out for sure to the 3 Luis\\'s. Luis Tuz, Luis Guzman, Luis Carrillo, and great service from Yudith Antonio, Anci Imelda, and Aracly! See you again November 19-26 celebrating my birthday once again! Cheers all! Jordan & Linda from Sudbury, Ontario, Canada.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r926412099-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "October 2023", "author": "Amber R", "title": "Amazing Resort!", "comment": "I just don\\'t understand the bad reviews.. we had the most amazing time! Our room was clean. Everything worked great. AC was cold, shower was warm. Our fridge was restocked for us without asking.  The restaurants were great. The steak house was literally amazing. Even the buffets were great. I ate sooo much food while I was there! Javier at TunKul is my favorite person ever. We went to see him every day. The bars weren\\'t that busy during the day. Night time was a little more of a wait. The swim up pool was awesome and I\\'ve never had a better pina colada than at this resort! The staff were friendly. They helped us everytime we asked. The app could use some work. It was hard to follow the directions on it as it kept freezing up. Better off using the maps posted.  The shows in the", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r925181511-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "Mhyk Mhyk", "title": "Thank you for remembering my Birthday :)", "comment": "So far so good currently we are in house guest in room 1454. So far our experienced was great! Despite of a lot of negative reviews I saw online I guess on our case was different and it\u2019s positive experienced so far and hoping for the best until we check-out! Service was incredible from Guest Service to all different departments. I want to commend Fatima of GS Pablo of Bahia Steak house and to all the staff. I will continue my review after we check out. But so far it was a great experienced :)  PS : I am the one who put the Cake topper and sticks on the cake lol:) But thank you so much for cake and champagne surprise:)", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r924478184-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "Mel", "title": "Grand Oasis was 10/10", "comment": "All the staff members here were all so lovely and provided excellent service. In particular Juan who is the bartender at the Gecko bar was super friendly, had excellent customer service and made the best drinks!  All the food at the restaurants I tried were amazing. The daily shows and entertainment was spectacular along with the stunning beaches/pools that Grand Oasis offers.  I highly recommend this place. When you arrive stop by Gecko bar and Juan will make you any amazing drink. Each cocktail just gets better and better :)", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r919159745-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "Yostena A", "title": "Great experience", "comment": "It was a great experience, dinorah uribe was a great concierge and help us in everything we need , she was great and the hotel too ,I will plan my next vacations here next year and bring my friends,  we had lots of fun", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r917224059-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2023", "author": "shakera a", "title": "Miramar restaurant was excellent and the service was the best. This place had the best food on the entire island.", "comment": "Thank you Edy, eloy, Fausto, Roberto, Omar, Jose Cepeda, Jony, Suemil, Josue, Cocina and Jorge.. You guys gave top service and the food was EXCELLENT .. Best food and service on the Entire island. Thank you", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r916264551-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "T Marie", "title": "An Unforgettably Awesome Experience. Highly Recommend! A+!", "comment": "Had the most wonderful experience at this resort. It was so great, my mother and her friend booked a trip to visit a few months later; they loved their time here too! Not only were the drinks fantastic, the beach parties super fun, and the food excellent, but service by management & staff is elite. I\\'d like to thank Mr. Santiago Munoz for providing such excellent care and service during all of our stays. He took care of us with the highest level of hospitality and quality care. Any problems that we faced, he was there to help resolve. He also offered useful insight as to things to do and try at the resort (always proving to have the best recommendations). You will meet so many great new friends by staying at this resort. It really is a fabulous place. I highly recommend it. You", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r915039680-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/2d/default-avatar-2020-48.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Audrey K", "title": "Excellent service", "comment": "Beautiful resort! The staff is extremely helpful and friendly.   Heribierto De La Cruz was amazing! He provided excellent service, answered all our questions and made us feel welcomed.   Food is great, plenty of pools and beautiful views. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r908650942-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/81/default-avatar-2020-43.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Lili S", "title": "Great experience, will be back", "comment": "Great experience, location, the staff was extremely friendly and helpful. I also truly enjoyed the live entertainment every day and night with amazing performers! Great restaurants on the property as well. They could improve the check-in process but otherwise, 10 out of 10! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r905984469-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "Rebecca K", "title": "We love Mexico", "comment": "David and Victor were the best!!! They always took care of us! Professional, friendly and knowledgable. We felt so cared for and could relax to have so much fun. They were great. We will be back to visit again!0", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r904764658-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "Emily M", "title": "Cocoa Bar", "comment": "Judith y Ernesto from Cocoa bar were the bomb! Best customer service ever! They were awesome! Let\u2019s goooo!!! Took goood care of us shots were in constant. They were so attentive and they made our lunch so fun. The personal has been so rude but they have made us feel special and made our vacation real! They always have a smile on their face and they hype us up to have a great time !! Shout out to both of them! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r904605787-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "Giancarlos L", "title": "Chocolatherapy package deal - fantastic!", "comment": "My wife and I tried the chocolatherapy with massage, sauna, and jacuzzi, and the massage was honestly the best I\\'ve received. Note, I\\'ve been to 22 countries of which I\\'ve gotten several massages in. Belen was absolutely fantastic, and my wife truly loved Diana. Virginio at the front desk was also very accommodating and excellent with starting us off.  Mi esposa y yo probamos la chocolaterapia con masaje, sauna y jacuzzi, y honestamente el masaje fue el mejor que he recibido. Noten que he viajado a 22 pa\u00edses y me han dado masajes en varios de ellos. Belen fue absolutamente fant\u00e1stica, y mi esposa realmente ador\u00f3 a Diana. Virginio en recepci\u00f3n tambi\u00e9n fue sumamente acogedor y excelente para darnos a empezar el servicio", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r901557159-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(2, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "wanttobetheir", "title": "We had another great time THANK YOU Pyramid", "comment": "Back to my home away from home. Awesome as always,  we stayed down in Playa, which was very good. But then came to the Pyramid to make it a perfect vacation. We finally got to the Grand Oasis, and as always were greeted by the best bagage handlers ever, with hello Douglas your back. These people have been greeting me for 20 years, that way. Juan, Remondo, and all the others. Off we went to check in, quick and easy. Got our Black door key bands took us to our great room in the Pyramid and off we went for a great finish to our vacation.  Went to the Fontainbleau, for some awesome drinks from Roberto, Luis, and Nathanial. We spent a good amount of time there for the 7nites. Ate at the White Box the 1st nite. They remembered me and brought me my favorite drink. Thanks Delfina,", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154444-r898253626-Grand_Oasis_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
