INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/a9/default-avatar-2020-50.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Juan M", "title": "Hotel muy recomendable para familias", "comment": "Restaurant Hacienda con muy buen servicio y varuedad de comidad, muy bien atendido por Milta. La  playa muy segura para los ni\u00f1os, varios bares porbtodo el hotel. EL BARCO PIRATA TAMBIEN  esta al lado del hotel , en general hemos venido varias ocasiones muy recomendable", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r902962171-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
