INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/3d/default-avatar-2020-55.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Ury D", "title": "Vacaciones familiares", "comment": "Fueron unas fant\u00e1sticas vacaciones en familia pudimos disfrutar de hermosas playas y alimentos y todas su gastronom\u00eda, se los recomiendo ampliamente para venir en familia  sobre todo para los ni\u00f1os ya que sus instalaciones son bonitas y su servicio sin duda el mejor nos toco la atenci\u00f3n del mesero Luis castro muy servicial a lo que le ped\u00edamos nos vamos muy contentos en familia", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r894743691-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/bb/default-avatar-2020-65.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Viajemos C", "title": "EXCELENTE EVENTO!", "comment": "Debo hacer mencion especial al deprtamento de grupos y eventos, en especial a la se\u00f1orita Gaby Escalante, que con su apoyo, disposicion y seguimiento no hubiera sido posible organizar tan hermoso evento. Tambien agradecer a nuestro mesero estrella Johan & a nuestra organizadora cynthia , gracias chicos muy buen trabajo!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r894713861-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
