INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Walter G", "title": "Mi estancia en el oasis", "comment": "La cafeter\u00eda est\u00e1 excelente y el servicio igual de las personas encargadas del servicio emily y Sara uy en general todo el hotel la comida queda mucho a deber las albercas muy bien las bebidas tambi\u00e9n", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r894004928-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Dreamer20033285978", "title": "Vacaciones", "comment": "De todos los restaurantes del hotel  Grand Osasis palm fue el Maki taco en la reserva del mi\u00e9rcoles 7 de junio en el horario de las 9 nos encant\u00f3 los 7 tiempos el mesero s\u00faper atento y muy bueno en la explicaci\u00f3n de cada platillo llamado Jafet, tambi\u00e9n las felicitaciones al chefGamaliet que todo estuvo exquisito. Las instalaciones del hotel muy c\u00f3modas  ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r893948921-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1b/c3/94/33/caption.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Downtown Burgers", "title": "Great staff!", "comment": "Amazing welcoming by lady at the front desk, rooms were clean, Wilber the at the pool bar was amazing,  always attentive to the very little details. Very accommodating. I really appreciate that! Also my server AIDE was always attentive, cordial and that smile that makes you feel welcomed every day! Thank you all for a great time!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r891798817-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/6d/default-avatar-2020-19.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Natascha H", "title": "Best bartender!", "comment": "This hotel was so fun! We had a blast with the bartender Uziel, he was so fun and good at making drinks! He definitely made sure that we were having a good time. His English was also great, so we talked with him a lot. If you\u2019re going to stay at the hotel, definitely check which bar he\u2019s at, and go see him! You won\u2019t be disappointed!\ud83e\udd29", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r891032491-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "New L", "title": "Nice people and place :)", "comment": "The restaurants are good and the beach was nice. They have hammocks you can lay on. The drinks are really nice too. There was a worker named Geraldine who spoke to and helped me and my family. She was nice!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r891015306-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "John D", "title": "Baby blue crab Kids Zone", "comment": "BABY BLUE CRAB  Kids Zone.  Open from morning till night so parents can drop their kids off anytime, but must remain close and on property to use the kids club. Lots of activities like arts and crafts, dancing, swimming and movie night, while other activities have an age limit (7-8 an older) just to be allowed to use the video game room. The club takes the kids to the kids buffet for lunch and dinner. My kids loved the club and the women running the kids club. During swim time there\\'s a lifeguard on duty. My little guys met a new friend from Florida there (Ian was his name) and they still talk about him from time to time. Hoping to be back again soon.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r890989716-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Culture40503380416", "title": "A great time!", "comment": "What a great experience. I visited the location in Cancun (Oasis Palm). It was entertaining and the food was very good! Victor, Alejandro, Francisco, Bartolime & Chef Jesus did an excellent job! I would definitely come again and bring my friends. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r890929936-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Dillan P", "title": "Sakura is top notch. Paired with Alejandro as your waiter, it\u2019s impossible to go wrong.", "comment": "Sakura has world class fare and even better service. Alejandro was superb. We couldn\u2019t have been more impressed by his knowledge of each and every dish. Alejandro was very attentive and friendly. Couldn\u2019t be happier.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r890532926-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(5, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Klaire M", "title": "So much fun", "comment": "This is our third time at this resort and we always love it. Sakura especially was amazing. Well worth the additional cost. The food was incredible and the entertainment was wonderful. Alejandro was the best server and took amazing care of us! Jesus and Evelyn were also great. A wonderful night to celebrate my husband\u2019s birthday with some of our closest friends!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d566509-r890532416-Grand_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
