INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ea/2e/default-avatar-2020-67.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Amira C", "title": "Excelente lugar", "comment": "Nos gusto mucho, las intalaciones siempre est\u00e1n limpias, el personal muy amable siempre. Tienen el club kids, una excelene idea para que los ni\u00f1os tengan tambi\u00e9n una linda experiencia con actividades bien organizadas y con instalaciones epeciales para ellos. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r893619627-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
