INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/6d/default-avatar-2020-19.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Alejo R", "title": "Calidad... excelencia y muy buena atenci\u00f3n", "comment": "Muy bueno todo!!! En especial la atenci\u00f3n de Itzel De La Cruz en el restaurante Terrazas Dos Lunas... su simpat\u00eda hace que quieras volver... es un hotel muy recomendado ... buena ubicaci\u00f3n... playa muy tranquila... y muy buenos espect\u00e1culos por las noches!!! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r885442911-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
