INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Carlos R", "title": "Vacaciones familiares", "comment": "El restaurante Cocoa fue excelente, un agradecimiento en especial a nuestro mesero V\u00edctor, que nos atendi\u00f3 como se debe, es el primer restaurante en Canc\u00fan donde ped\u00ed un corte de carne t\u00e9rmino medio y as\u00ed fue como me lo trajo, excelente comida y excelente atenci\u00f3n , mis felicitaciones al Chef Rafael Meneses, toda la comida fue excelente!!!! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r884838898-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
