INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/7a/default-avatar-2020-25.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "Jorge Astudillo", "title": "Muy tranquilo y bien atendido", "comment": "Habitaciones standar muy comodas sus comidas desde el desayuno muy variados,muy bien atendido por todo su personal,se extra\u00f1a el minifrigobar no incluido.linda zona de playa para ni\u00f1os .Todo limpio y ordenado", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r877743594-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/df/99/default-avatar-2020-40.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "projasmon", "title": "Vacaciones en familia", "comment": "Muy buena atenci\u00f3n, especialmente de Edgard Lopez nuestro garz\u00f3n favorito del restaurante Las Palmas. El unico problema, como las habitaciones daban hacia la playa, se sentia el ruido en la noche de las animaciones. Tenia buena oferta de restaurantes, hermosa y gran piscina, juegos arcade, etc. Si andan con personas mayores, se debe tener mucha precauci\u00f3n en la bajada de algunas escaleras, porque puede volverse muy resbaloso.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r877725719-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ec/a2/default-avatar-2020-3.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Mario V", "title": "Maravilloso hotel", "comment": "Maravilloso hotel! Maravillosa atenci\u00f3n en el Restaurante Las Palmas por parte de Andr\u00e9s L\u00f3pez, nos salv\u00f3 la jornada del d\u00eda. Hasta jugu\u00e9 videojuegos cl\u00e1sicos en el sports bar. Los desayunos deliciosos y con opci\u00f3n mexicana. Volveremos sin pensarlo.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r875864803-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/3d/default-avatar-2020-55.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Jaime C", "title": "Excelente Servicio", "comment": "Excelente Servicio  Nos Hospedamos del d\u00eda 11 al 15 de enero de 2023, fue un viaje familiar con un excelente trato en especial de Omar Salgado y Frida Moreno del departamento de atenci\u00f3n a hu\u00e9spedes siempre teniendo un trato amable y ayudando a resolver algunas cuestiones log\u00edsticas, tambi\u00e9n resaltar el servicio meseros en especial del Andr\u00e9s del restaurante Dos Lunas y la atenci\u00f3n en los bares por la tarde. Recomendamos ampliamente el hotel.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r875452956-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
