INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "./op3_files/default-avatar-2020-31.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "juan carlos f", "title": "Exelente servicio", "comment": "Exelente servicio y atenci\u00f3n en especial a anayatzin Los restaurantes excepcionales y atenci\u00f3n al cuarto  Limpieza en todos lugares tanto en albercas y la playa, muy recomendado apmpleamente. Pronto regreso ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r872343865-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
