INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/d0/default-avatar-2020-33.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Emy E", "title": "Excelentes meseros", "comment": "Excelente servicio, habitaciones muy bonitas, la comida en los restaurantes bites y dos lunas est\u00e1 riqu\u00edsima, el personal de recepci\u00f3n y meseros muy buenos, espacialmente el mesero Andr\u00e9s en bites que atiende muy amable y atentamente, se hace muy amena la estancia con este tipo de servicio ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r871283815-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/a9/default-avatar-2020-50.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "David", "title": "Excelente servicio", "comment": "Me encant\u00f3 el lugar, especialmente los restaurantes bites y dos lunas, excelente servicio, comida muy rica, muy amable y atento el mesero Andr\u00e9s, quien los atendi\u00f3 e hizo muy agradable la visita . Las habitaciones muy bonitas y limpias. Volver\u00eda a venir sin dudas", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r871281617-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ee/68/default-avatar-2020-9.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Alan M", "title": "Vacaciones", "comment": "Viaje con la familia excellente hotel buena vista todo chido nos vamos muy contentos con estas vacaciones mis hijos muy felices por todo toda la atencion por parte de todos pero en especial la playa y la mesera Anayatzin con un servicio de 10 con la atencion para mi esposa y mis hijos sin duda espero regresar a este hotel \u2606\u2606\u2606\u2606\u2606", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r871273575-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/43/default-avatar-2020-11.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "dani.gv-", "title": "Buena estad\u00eda.", "comment": "Excelente estad\u00eda en Oasis Palm , peque\u00f1o detalle del atraso para la entrega de habitaci\u00f3n,  pero el resto muy bien , limpio , buenos restaurantes obviamente con ricas y variadas comidas ,  lindas habitaci\u00f3n y grandes ,  buenos show , excelente \ud83d\udc4c  , adem\u00e1s una excelente atenci\u00f3n de parte de nuestros amigos Jos\u00e9 del bar de la piscina principal, muy atento y nos ayudo mucho con sus datos y consejos para conocer Cancun \ud83d\ude09  , tambi\u00e9n a nuestro amigo Hernan de la Cantina , muy simp\u00e1tico feliz con su trabajo y amable .Recomiendo Oasis .", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r869583873-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f6/0d/default-avatar-2020-34.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Karina Monserra... G", "title": "excelente lugar familiar,el mejor hotel de Canc\u00fan", "comment": "Ingrid la mesera de la alberca,excelente empleada,atenta en todo momento a nuestras necesidades. Los chicos de actividades siempre con todo el animo,INVOLUCRANDO al hu\u00e9sped. El hotel en general es muy bueno si vas en un ambiente completamente familiar", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r868034959-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/26/c3/6f/3b/h6352uvmiguele.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Miguel Estrada", "title": "EXCELENTE SERVICIO RESTAURANTE BITES", "comment": "Quiero agradecer al mesero Jos\u00e9 Sosa porque nos ha regalado una maravillosa experiencia al cenar en el restaurante Bites. Mis hijos estaban cansados y de mal humor, y cuando lleg\u00f3 el Sr Jos\u00e9 con su alegr\u00eda y sencillez nos hizo la mejor experiencia en nuestra estancia en el Grand Oasis Palm. Al d\u00eda siguiente fuimos a desayunar ah\u00ed mismo y apenas vio a mi hijo lo salud\u00f3 por su nombre con emoci\u00f3n. Incre\u00edble!! Felicitaciones al hotel por tan bella experiencia", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r866753028-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "./op4_files/default-avatar-2020-36.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Manuel yanez avila", "title": "Lo mejor de aquaaerovics", "comment": "Estuve del1 al7 de octubre del 2022  y quiero agradecer en actividades aquaaerovics  a los j\u00f3venes el de Cancun y  ella de tabasco edwin y valeria q junto con Serena e Iv\u00f3n nos hicieron pasar unas vaca ciones Inolvidable abrazos muchachos y si un d\u00eda visitan Quer\u00e9taro aqu\u00ed estamos", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r863840611-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "./op4_files/default-avatar-2020-31.jpg", "date_comment": null, "date_stayed": "September 2022", "author": "cesar i", "title": "Mi visita al hotel Gran oasis palma", "comment": "El lugar est\u00e1 muy bien los restaurantes con muy buena comida variada para los gustos de cada quien. En las barras un exelente servicio en la barra de la alberca bien atendidos por V\u00edctor pech y Alejandro Loria. Las habitaciones son amplias y confortables. En general una muy buena experiencia con los distintos shows que hay en le lugar.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r861736728-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": null, "date_stayed": "September 2022", "author": "Cvazquez@gmail.com", "title": "Visita septiembre 2022", "comment": "Vivimos una experiencia muy agradable y c\u00f3moda, los restaurantes est\u00e1n muy buenos y muy completos, los meseros la mayor\u00eda muy atentos y agradables, hoy no sentimos distinci\u00f3n entre turistas extranjeros y naci\u00f3nales. El mesero valtencior del restaurante la hacienda super amable, el mejor que nos atendi\u00f3 en la estadia", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r859743134-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/ef/default-avatar-2020-66.jpg", "date_comment": null, "date_stayed": "August 2022", "author": "Isa G", "title": "Vacaciones divertidas", "comment": "Excelente atenci\u00f3n, ideal para unas maravillosas vacaciones en familia. Gran variedad de restaurantes con diversos y deliciosos alimentos. Playa, albercas, espacios para toda la familia, buenos espect\u00e1culos y amenidades.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r852793370-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/de/e7/default-avatar-2020-37.jpg", "date_comment": null, "date_stayed": "July 2022", "author": "Ulises_Mart\u00ednez29", "title": "Vacaciones perfectas", "comment": "Recomendamos el hotel grand oasis palm por su atenci\u00f3n y amabilidad de todo el personal, muy bonitas instalaciones y bonito ambiente quiero recomendar al p\u00fablico en general que quieran disfrutar sus vacaciones, agradecemos a la mesera Guadalupe de sisal por sus atenciones muy profesional en su trabajo y nos vamos muy satisfechos con los tratos de este hotel a Daniel, Rony y Alberto muy recomendado", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r847735492-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e3/1f/default-avatar-2020-46.jpg", "date_comment": null, "date_stayed": "June 2022", "author": "andrea b", "title": "Excelentes vacaciones en este hotel el oasis", "comment": "Me gusto mucho la atenci\u00f3n de su personal el ingreso fueron muy atentos,habitacion con vista al mar excelente  muy c\u00f3modas, las piscinas muy amplias, excelente limpieza la comida excelente  los meseros muy buena atenci\u00f3n especial la del mesero Francisco  chul muchas gracias muy amable", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r845511334-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ea/74/default-avatar-2020-68.jpg", "date_comment": null, "date_stayed": "June 2022", "author": "Mia D", "title": "Vacaciones", "comment": "Exelente vacaciones Francisco CHSC muy atento ,muy profesional exelente sus habitaciones la comida muy rica La playa muy bonita la alberca muy amplia la instalaci\u00f3n es muy perfecta Me gust\u00f3 todo, todo est\u00e1 muy bonito", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r845280021-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/e6/default-avatar-2020-45.jpg", "date_comment": null, "date_stayed": "May 2022", "author": "Paty P", "title": "Mozos", "comment": "Visitamos esta semana el hotel Oasis Palm de Canc\u00fan y queremos destacar la atenci\u00f3n de Jes\u00fas L\u00e1zaro en el restor\u00e1n \u201cLa hacienda\u201d que est\u00e1 dentro del hotel. Nos sentimos muy a gusto por su excelente disposici\u00f3n, \u00a1gracias, Jes\u00fas!  Patricia y Alejandro \ud83c\uddfa\ud83c\uddfe", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r840891615-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/3d/default-avatar-2020-55.jpg", "date_comment": null, "date_stayed": "May 2022", "author": "David Alejandro F", "title": "Restaurante dos a lunas y albercas", "comment": "Para m\u00ed fue mi primera vez en el hotel y todo incre\u00edble el trato de los meseros en todos los aspectos en el restaurante dos lunas (Andr\u00e9s L\u00f3pez ) nos atendi\u00f3 todo muy bien a mi esposa e hijos al igual que la Mesera de la alberca (Ruth)  La comida estuvo muy bien las instalaciones todas limpias en mi opini\u00f3n todo respecto al hotel estuvo muy bien", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r840106514-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/42/default-avatar-2020-20.jpg", "date_comment": null, "date_stayed": "September 2021", "author": "Safari782691", "title": "Club Kids", "comment": "Excelente trato y seguridad en el Club Kids de Oasis Palm, mi peque\u00f1o se la pas\u00f3 super.  En cuanto a la comida en general,  estuvo en un sabor medio, la verdad no era lo esperado, pero de ah\u00ed en fuera todo lo dem\u00e1s muy bien.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r808391222-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "./op5_files/alfredolr.jpg", "date_comment": null, "date_stayed": "September 2021", "author": "Alfredo L. R", "title": "Restaurant La Hacienda  del Hotel Oasis Palm", "comment": "Las siguientes personas que son:  Abimael, Luis y Angel ( meseros) nos brindaron a mi esposa y este servidor un exelente servicio a nosotros y el Sr. Ivan (cocinero) nos brind\u00f3 una calidad de servicio muy humana y muy atento.   Todo esto cabe en recalcar que por el exelente servicio que todos ellos an  brindado cuando volvamos de nuevo a Cancum nos volvemos a quedar de nuevo el hotel Oasis Palm.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r808159429-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "./op5_files/default-avatar-2020-22.jpg", "date_comment": null, "date_stayed": "September 2021", "author": "SERGIO RENAN HERNANDEZ POOT", "title": "Excelente hotel", "comment": "Excelente servicio y hotel playas muy limpias los restaurantes excelentes Sisal , La Hacienda maravilloso la atenci\u00f3n con \u00c1ngel muy recomendable y en Bites tambi\u00e9n muy bueno con Mar\u00eda si atenci\u00f3n excelente   Familia Hern\u00e1ndez", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r807985125-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2021", "author": "Rodrigo V", "title": "Un aniversario so\u00f1ado", "comment": "Desde el ingreso al hotel todos muy amables, empezando con Maribel en recepci\u00f3n. Pedro el encargado de la puerta muy servicial y atento. Las ni\u00f1as de servicio al cliente (Guille y Leidy Rosado) muy colaboradoras y amables. La habitaci\u00f3n muy limpia y ordenada. Los Restaurantes muy agradables y organizados. Excelente comida. Felicitaciones y muchas gracias por habernos hecho sentir como en casa.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r784106166-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2020", "author": "Mimi Zamarripa", "title": "Vacaciones patrias \ud83c\uddf2\ud83c\uddfd\ud83d\udc96", "comment": "Excelente experiencia en el hotel,todo el personal s\u00faper amable y atento en todo momento,tanto en los restaurantes, sala de juegos,un gusto coincidir con willbert,la mejor atenci\u00f3n en el \u00e1rea de juegos una persona muy amable. Adem\u00e1s de los bartenders y todo el personal de animaci\u00f3n,importante destacar al personal de limpieza,en estos tiempos de contingencia siempre atentos con la sanitizaci\u00f3n! Sin duda espero volver pronto! \ud83e\udd7a", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r770933243-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "August 2020", "author": "Maribel Juarez ruiz", "title": "Hotel oasis palm", "comment": "Exelente hotel, trato, comida servicio, estuvimos del 22 al 26 de agosto. El personal muy amable siempre quedamos encantados, mis ni\u00f1os quedaron encantados ya que es su primera vez que visitan el mar, lo recomendaremos ampliamente y esperamos volver al siguiente a\u00f1o \ud83d\ude03 ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r767167293-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2020", "author": "DonHumberto", "title": "Viaje familiar", "comment": "En oasis palm Cancun, el hotel es bonito, las albercas grandes y de todos tama\u00f1os, con una playa incre\u00edble y en el restaurante sisal, encontrar\u00e1s sabores incre\u00edbles y con la atenci\u00f3n del buen Alberto Tuz, disfrutas a\u00fan m\u00e1s la comida y tu estancia en general.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r761095707-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2020", "author": "Veronica O", "title": "Vacaciones", "comment": "Mi opinion sobre el hotel grand oasis palm es que el servicio es muy bueno el personal en todo momento es que son muy amables la comida de.los restaurantes es buena el que casi no me gusto es makitaco de ahi es fuera los otros muy bien  la limpieza de  la habitacion muy buena todo muy limpio  tambien lo que nos  gusto mucho es la atencion de las chicas que hacen actividades con los ni\u00f1os son amables con ellos las actividades que hacen son divertidas para los ni\u00f1os eso nos gusto mucho GRACIAS A MOY,VALERIA Y ARIANA (kids club)", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r752161912-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2020", "author": "Carla A", "title": "Vacaciones", "comment": "Quiero expresar mis felicitaciones al Bar La Isla, en especial a Adri\u00e1n, Ceballos y Laura, por su excelente atenci\u00f3n y compromiso, siempre con buena disposici\u00f3n y una sonrisa en su rostro, sus preparaciones son maravillosas, siempre dando lo mejor de si mismos para hacer de nuestra estad\u00eda un momento maravilloso y agradable.  Agradezco esto ya que en paralelo, tuve un momento horrible el d\u00eda de hoy 7 de febrero del 2020, en el cual se me falt\u00f3 el respeto por parte del personal de entretenimiento, ya que mientras cambiaba mis toallas de playa, me acosaron por mi acento chileno, y adem\u00e1s me dijeron groser\u00edas. Me sent\u00ed vulnerada y violentada. Solicite a Andr\u00e9s jefe de calidad su ayuda para comunicarme con la jefatura de este personal y no tuve respuesta positiva, la jefa nunca", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r743641764-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "January 2020", "author": "Erney Gonz\u00e1lez", "title": "Excelentes vacaciones en el gran oasis", "comment": "Visitamos la playa, el restaurante la Bamba, el Bites food Hall, el Sisal. Adem\u00e1s cuenta con un excelente espacio para los ni\u00f1os KIddO zone d\u00f3nde se divierten por mont\u00f3n, un espacio reserva solo para ellos.  Atendidos por Bartolome y Rosendo en BITES FOOD HALL muy buen servicio \ud83d\udc4d", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r741820368-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "January 2020", "author": "Lilian B", "title": "Vacaciones en familia", "comment": "Hermoso hotel y la atencion expetacular , en el restaurante sisal una atencion muy cordial, nos sentimos como en casa, vamos a volver a este hotel\u00a1!\u00a1 agradecimiento a Alberto Tuzz , gracias por todo\u00a1!\u00a1\u00a1!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r737708650-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "./op1_files/default-avatar-2020-59.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Carlos.are199@gmail.com", "title": "Great Service", "comment": "Anayatzin was an amazing server, she was very on top of her service and made sure we were taken care of. She was very polite and very professional. We will be back to this hotel just because of her. Thank you Anayatzin", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r874304223-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "./op1_files/default-avatar-2020-43.jpg", "date_comment": null, "date_stayed": "February 2022", "author": "alin m", "title": "Great time", "comment": "We had a great time at the Oasis and more than anything the Bar tenders at the The Cantina made our stay even better \u201c Adela  And Hernan are so friendly  and great service !!thank you again Adela and Hernan for make our stay awesome !", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r828114733-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 5.0, '{"profile_picture": "./op1_files/default-avatar-2020-8.jpg", "date_comment": null, "date_stayed": "August 2019", "author": "deepak m", "title": "Vacation", "comment": "One of the best hotels and the staff is great and the sports bar is amazing. The staff members work hard. Julian Carrillo and Jesus are the best. They were were helpful and always taking care of everything.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r696987222-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(6, 4.0, '{"profile_picture": "./op2_files/default-avatar-2020-63.jpg", "date_comment": null, "date_stayed": "May 2019", "author": "Brian", "title": "Had a great time at the Oasis Grand Palm", "comment": "The Bartenders at the resort were fantastic, especially Esau, Javier, Bartolome and Jorge. They were very friendly and made sure you always had a drink in your hand. My wife and I went May 7th to the 11th. We went on the Pirate Cruise our last night there.        The little person that played Tattoo was great. He played his part to a  tee! He was very entertaining and almost stole the show! I would go back to see this show again!      Only one issue, You could feel the thumping of the bass from the music played somewhere outside on the resort. We called the front desk at 12:30 am. They called back and said the manger took care of it. The noise went on till 1:30 am.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d17165483-r673745920-Hotel_Oasis_Palm-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart1_files/default-avatar-2020-7.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Joaquin M", "title": "Amabilidad", "comment": "Quiero resaltar la amabilidad de los empleados, especialmente en el restaurante. Muchas gracias a Jorge, Eric, Melisa, Bel\u00e9n por su excelente servicio. Tambi\u00e9n resaltar que la comida del hotel est\u00e1 muy bien y la relaci\u00f3n precio&calidad excelente.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r907420221-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart1_files/default-avatar-2020-28.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Alejandra P", "title": "El Desayuno empresarial APQ, se llev\u00f3 a cabo con \u00e9xito; gracias Ariana, Wilberth, Ra\u00fal, Adriano.", "comment": "El Desayuno empresarial APQ, se llev\u00f3 a cabo con \u00e9xito. Gracias Ariana, Wilberth, Ra\u00fal, Adriano, meseros.  Los alimentos estuvieron muy bien, felicitaciones al chef y colaboradores del \u00e1rea.  Hicimos uso de la pantalla, estaba instalada y me ocup\u00e9 antes de poder comentarte que quiz\u00e1 no tuvieron tiempo de retirarla del evento anterior.  Respecto al color de los manteles, de momento le gust\u00f3 a mi superior el blanco, pero al final me dijo que prefer\u00eda los de color negro, as\u00ed lo solicitaremos para nuestro pr\u00f3ximo evento.  El personal servicial, presto, en general, todo muy bien, muchas gracias. Tenemos 8 meses de contratar con ustedes y ya reservamos para el pr\u00f3ximo, gracias por el esmero en su servicio.  Nota: lamentamos algunos inconvenientes que tuvieron los asistentes al evento", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r896968958-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/20/default-avatar-2020-31.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Zaida H", "title": "Todo ok...excelente", "comment": "Chef Jorge Petul y su equipo de trabajo en el restaurante, tienen un excelente servicio, Eric y Jos\u00e9, nos atendieron super bien, full recomendados.  Las comidas que nos ofrecieron super ricas...sus padres a otro nivel.  Esperamos volverlos a ver en nuestra pr\u00f3xima visita.   ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r893900399-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Divanny", "title": "Mi humilde opini\u00f3n", "comment": "Bueno contando la experiencia en este maravilloso hotel me sent\u00ed muy complacida por los servicios en mi estad\u00eda sobre todo recalcando el servicio de la comida excelente qued\u00e9 maravillada por tantas cosas exquisidas.  En el \u00e1rea de la comida del restaurante obtuve una excelente atenci\u00f3n de los se\u00f1ores Erick y Jos\u00e9.  Y les damos las gracias por tratar de darnos esa excelente atenci\u00f3n tanto as\u00ed que nos cambiaron el men\u00fa con tal de complacer nos.  En cuanto a las habitaciones creo que est\u00e1 muy bien equipada pero sugiero las s\u00e1banas gruesas por el fr\u00edo que hace dentro.  Bueno para concluir me pareci\u00f3 que el servicio en este hotel fue algo excelente Adem\u00e1s de que todo el personal es muy amable y atentos con todas mis compa\u00f1eras y conmigo Les recomiendo 100% visitar este hotel aqu\u00ed en", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r893899371-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ea/2e/default-avatar-2020-67.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Alison P", "title": "Mi opini\u00f3n", "comment": "Todo estuvo incre\u00edble, la comida muy rica, a mi y a mi grupo nos atendi\u00f3 Jos\u00e9 y Erick que eran super amables y brindaban buen servicio. Los cuartos bastante limpios y diria yo espaciosos. La recepci\u00f3n atendia r\u00e1pido y eficiente, un lugar donde puedes descansar y tener linda hospedia.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r893898348-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/29/65/58/79/lesslie-itzel-s.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "lesslie itzel s", "title": "Rese\u00f1a", "comment": "De principio hubo algunos inconvenientes, la se\u00f1orita Aketzali desde un principio ayudo a resolvernos todo, esto hizo que nos adapt\u00e1ramos m\u00e1s, nos hizo sentir muy bien, es una excelente persona y le deseamos lo mejor, excelente persona y hace su trabajo de maravilla \ud83e\udd0d muy buen lugar con buena personas, gracias a la se\u00f1orita Aketzali por sus buenas atenciones, por apoyarnos en toda nuestra estancia. Buenas instalaciones y muy bonita la piscina \ud83d\udc95 Atte; familia salinas Gutierrez ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r893619645-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/9c/default-avatar-2020-14.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "YIRELYS R", "title": "Realizamos 2 desayunos uno por el d\u00eda de las madres y otro para celebrar el d\u00eda de los maestros, la mejor elecci\u00f3n.", "comment": "Excelente atenci\u00f3n de Ra\u00fal, Ariana y Wilbert, contamos con su acompa\u00f1amiento en todo momento y siempre con la disposici\u00f3n de ayudarnos. Nuestros eventos salieron de maravilla. Quedamos muy contentos al igual que nuestros invitados. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r891488843-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/e6/default-avatar-2020-45.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "facundo b", "title": "Maravillosas vacaciones", "comment": "La atenci\u00f3n es excelente por parte de todo el staff pero en especial por Brenda y todos los del restaurante, las habitaciones son excelentes y la recepci\u00f3n el d\u00eda que llegamos nos la dio antes de tiempo!!! Las instalaciones est\u00e1n en \u00f3ptimas condiciones, y loa seguridad del estacionamiento tienen toda la onda! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r891133601-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Grazia C", "title": "Mi maravilliosa vacaciones en Cancun", "comment": "Maravillioso, todo es excellente la piscina la comida , los chico Jorge, Brenda, Jose, Emanuel muy disponibles y cordiales siempre con sus fantasticos sonrias, la habitacion siempre limpia, la pase super ... Muy relajada musica tranquilla very good Gracias Smart Cancun", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r891028327-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/00/default-avatar-2020-4.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Max V", "title": "Excelente servicio 10/10", "comment": "Excelente servicio, es la segunda ocasi\u00f3n en la que nos hospedamos en este hotel. Espero regresar en diciembre con la familia. La atenci\u00f3n de todo el personal es muy buena, en especial de Michelle, Aketzali, David y Adri\u00e1n", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r885482417-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart2_files/default-avatar-2020-46.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Discover45187160125", "title": "Gran desayuno", "comment": " Quede encantado con la buena antencion que nos dieron el personal oasis muchas gracias ariana, wilbert y Raul en todo momento muy amables y serviciales, el desayuno exquisito felicitaciones al chef.  El hotel pides lo recomiendo ya que el espacio est\u00e1 excelente para eventos como este y est\u00e1 en buenas condiciones Con gusto regresare a este Hotel y lo recomiendo en alto grado.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r885319937-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart2_files/default-avatar-2020-16.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Daniel K", "title": "BUEN SERVICIO", "comment": "El personal que atendieron en el evento  sabe con precisi\u00f3n la manera de como se debe atender bien sobre todo Wilbert Ariana y raul, siempre est\u00e1n dispuestos a ayudar y a brindarnos  confort y comodidad, su actitud de servir es su mejor aliada. Felicitaciones!!!!!! De otra parte las instalaciones del  del Hotel y su limpieza son dos factores positivos adicionales que inclinan la preferencia por este del mismo, al igual que buen sabor de los alimentos , por lo que a la gran atenci\u00f3n y servios del hotel Oasis si regresaria para futuros eventos y servicios..", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r885101011-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/2d/default-avatar-2020-48.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Karen B", "title": "MARAVILLOSO SERVICIO", "comment": "La atenci\u00f3n por parte de la Lic. Adriana y los encargados Wilver y Raul, fue de primera.  En general,nos apoyaron con todo. Una excelente opci\u00f3n para viajes de negocios, sin duda ser\u00e1 nuestra sede para cursos en Canc\u00fan.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r884970813-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/32/default-avatar-2020-12.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Auxiliar M", "title": "Capacitaci\u00f3n", "comment": "Buena atenci\u00f3n, se realiz\u00f3 evento con ellos y todo se sigui\u00f3 de manera correcta como se solicito previamente, la respuesta del equipo de eventos y banquetes fue muy atenta y r\u00e1pida.   Muchas gracias a Ariana Morales, Ra\u00fal Ruiz, Wilbert y Adriano, por su atenci\u00f3n y profesionalismo. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r884791843-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "pamela a", "title": "Desayuno", "comment": "Muy buena atenci\u00f3n de Wilber en nuestro evento , es una persona muy dedicada a su trabajo y sabe hacerlo a la perfecci\u00f3n, tambi\u00e9n excelente profesionalismo de Ra\u00fal y Ariana , de igual manera hicieron que nuestra visita al hotel fuera muy agradable y satisfactoria, recomiendo las instalaciones y el servicio de desayuno por lo que tambi\u00e9n mando mis mayores agradecimientos al equipo de cocina.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r884188650-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/77/ae/default-avatar-2020-2.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Cristell G", "title": "DESAYUNO EMPRESARIAL", "comment": "El Hotel Oasis Smart cumpli\u00f3 con nuestras necesidades para nuestra visita de negocios, todos los trabajadores muy atentos y salones  amplios para nuestras actividades necesarias, los meseros muy movidos y atentos en especial el mesero wilbert , y el personal de eventos Ariana y Ra\u00fal, personas muy atentas y coordinadas, todo excelente, mejor no pudo ser.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r884086586-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/99/default-avatar-2020-57.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "israel josafad c", "title": "EXCELENTE SERVICIO", "comment": "Muchas gracias por todas la atenci\u00f3n que nos ofreci\u00f3 el hotel, el desayuno s\u00faper rico y bien servido , felicidades al chef , tambi\u00e9n nos agrado las \u00e1renos en buen estado y con la higiene necesaria muy limpio todo, eso habla bien del hotel , igual Gracias al personal muy amables todo pero en especial a ariana , raul , wilbert y Adriano ,muy atentos y serviciales durante todo el evento. Tendr\u00e9 en cuenta el hotel para pr\u00f3ximos eventos.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r883455477-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/b8/default-avatar-2020-22.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Adriana C", "title": "Capacitaci\u00f3n", "comment": "En mi visita empresarial, disfrutamos mucho del servicio del hotel , todos muy bien coordinados y atentos, en especial quiero mencionar a Ra\u00fal  Ruiz  ,, Ariana Morales y a sus asistentes por sus atenciones.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r883411682-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/a7/default-avatar-2020-44.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Gerencia C", "title": "Exclente servicios para eventos corporativos", "comment": "Usualmente hacemos todos nuestros eventos corporativos en el sal\u00f3n Tulum, son muy c\u00f3modos con buen mobiliario y equipo, el desayuno es muy bueno con excelente servicio y acompa\u00f1amiento del staff, ARIANA MORALES , RAUL RUIZ , WILBERT Y  ADRIANO de banquetes simempre nos apoyan para que nuestros eventos sean un \u00e9xito!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r883402977-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/a3/default-avatar-2020-38.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Fernanda M", "title": "Excelente!", "comment": "Es una buena opci\u00f3n para quedarse, tiene f\u00e1cil acceso a transporte y a movilizarse por Canc\u00fan, se encuentra a cuatro minutos en cami\u00f3n de la playa m\u00e1s cercana.  Tiene buenas opciones de desayuno.  La atenci\u00f3n de la Se\u00f1orita Kenia y del Se\u00f1or Jos\u00e9, fue muy atenta, servicial, muy amables.   Es una buena opci\u00f3n para hospedarse.  El cuarto tiene buen tama\u00f1o, almohadas c\u00f3modas. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r883155245-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart3_files/default-avatar-2020-14.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Blanca Estela G", "title": "La verdad muy buen ambiente familiar y limpio  agradable", "comment": "Las habitaciones son c\u00f3modas y muy agradable, en el restaurante erick, Emmanuel, angel, jorge, Yar\u00ed, Pablo son muy amables y atentos en general nos encant\u00f3  y si regresar\u00edamos con gusto al siguiente a\u00f1o ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r883028202-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/eb/default-avatar-2020-27.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Jes\u00fas Germ\u00e1n U", "title": "Industrias", "comment": "Todo bien , el sal\u00f3n akumal es peque\u00f1o pero muy c\u00f3modo para 15 personas , tiene ventanas y el caf\u00e9 es muy bueno , gracias por las atenciones wilbert , Ra\u00fal y Ariana .  Buen servicio  Buena instalaci\u00f3n  Limpio  Organizaciones todo bien", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882817070-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/77/ae/default-avatar-2020-2.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Miguel", "title": "Atenci\u00f3n de 100", "comment": "  Gracias a los de \u00e1rea de eventos por brindar informaci\u00f3n y hacer de nuestro desayuno muy grato , rico y abundante la calidad se siente y percibe buen servicio de Ra\u00fal.   Adriano y wilbert son serviciales en todo momento , Ariana Morales nos coordino el evento y fue un \u00e9xito", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882760157-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/00/default-avatar-2020-4.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Raul M", "title": "Desayuno exquisito empresarial", "comment": "  Es grato acudir a reuniones donde los alimentos son muy buenos y la atenci\u00f3n del personal es amable , la instalaci\u00f3n desde estacionamiento es amplio , y el area de negocios cuenta con varios salones para realizar todo tipo de eventos de negocios , atendidos por el capit\u00e1n wilber , Srita Ariana , Adriano. Gracias por formar un equipo que se recomiende.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882749080-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart3_files/default-avatar-2020-14.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Christian V", "title": "Evento Industrias", "comment": "Muchas gracias por el buen trato concedido por el equipo de trabajo del hotel, igual quiero resaltar que el espacio estaba en las mejores condiciones, bastante limpio y tambi\u00e9n con buen espacio, en cuanto a la comida estuvo bien servida y con un buen  sabor nos encant\u00f3, \tagradecemos mucho los servicios de la se\u00f1orita Ariana, Ra\u00fal , Adriano y Wilbert que en todo momento estuvieron pendientes y muy gentiles.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882316698-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/9f/default-avatar-2020-16.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Yesmin U", "title": "hotel buen\u00edsimo", "comment": "Nuestro evento en el hotel smart estuvo bien , todo en \u00f3rden en instalaciones y alimentos el coffe break se mantuvo y cambi\u00f3 durante todos nuestros d\u00edas aunque contratamos uno en especial . Ariana atenta y el equipo que la acompa\u00f1a raul y wilbert muchas gracias por cumplir con los requerimientos en nuestro evento", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882302188-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/20/default-avatar-2020-31.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Frida A", "title": "ENCANTADA!", "comment": "Excelente atenci\u00f3n de Ra\u00fal Ruiz, Ariana y Wilbert del departamento de eventos en el hotel Smart , la verdad nos vamos muy contentos con todos los servicios brindados por parte del hotel, la comida muy rica (excelente equipo de cocina) , comodidad con instalaciones y muy c\u00e9ntrico.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882301124-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e3/1f/default-avatar-2020-46.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Antonio S", "title": "GRAN SERVICIO", "comment": "Nos sentimos contentos con la atenci\u00f3n otorgada por el hotel, los espacios en perfectas condiciones, el trata muy amable la comida con buen sabor en especial el cafe muy rico , y quiero darle las gracias a los chicos ariana , Raul y wibert", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882213807-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/91/default-avatar-2020-62.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Roberto C", "title": "GRAN  SERVICIO", "comment": "  No brindaron un gran servicio por el personal del hotel , el espacio estuvo ideal y que nos sentimos bastante c\u00f3modos, los alimentos estuvieron muy ricos y contaban con buen  higiene  Queremos agradecer las atenciones de Ariana , Raul y wilvert  Recomendamos  el hotel para futuros eventos", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882184179-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/e3/default-avatar-2020-59.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Catherine P", "title": "Evento Omega", "comment": "Cumpli\u00f3 con los est\u00e1ndares y la promesa de salon , son como en sus fotos , est\u00e1n muy amplios y limpios , el hotel tiene instalaciones muy agradables es peque\u00f1o pero tiene todo lo importante , colorido , bien ubicado , tiene el area de la alberca agradable y el personal tambi\u00e9n es importante mencionar en el area de eventos , wilbert y los se\u00f1ores raul y adriano atentos siempre en especial Ariana, muy grata atenci\u00f3n ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r882108574-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "January 2023", "author": "laura v", "title": "Excelente Servicio de Eventos.", "comment": "La comida, deliciosa, siempre super atentos a alguna necesidad de nuestra parte, es nuestro segundo evento llevado acabo en sus instalaciones y todo ha salido maravilloso. El servicio de los meseros fue genial, todo excelente.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r875641901-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "January 2023", "author": "Cristian R", "title": "Viaje Cancun", "comment": "El servicio es excelente, no tengo palabras de agradecimiento para con todo M\u00e9xico, cancun y el hotel. Muy buena ubicaci\u00f3n, limpieza, desayuno, piscina. 5stars*  A Leticia,nuestra gu\u00eda de viajes caribe maya, un abrazo gracias por todo.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r874157738-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "January 2023", "author": "Camila A", "title": "La mejor visita a Canc\u00fan de primera vez", "comment": "Hotel oasis smat,desde la primera entrada al hotel todo el personal muy atento las habitaciones limpias y buen servicio,la atenci\u00f3n de Leticia Arriaga y las recomendaciones de los tures que pod\u00edamos hacer super,falt\u00f3 m\u00e1s tiempo para seguir conociendo.En verdad muchas gracias me voy super feliz de recibir el nuevo a\u00f1o 2023 con grata actitud y muy buena energ\u00eda  Mil y mil gracias.Definitivamente volver\u00e9 para seguir conociendo y disfrutando m\u00e1s. ATT:CAMILA.\ud83e\udd17\ud83d\ude0a\ud83c\udf0c", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r874157430-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
