INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart6_files/alex-e.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Alex Estrella", "title": "\"Excelente Atenci\u00f3n\"", "comment": "Me encant\u00f3 la atenci\u00f3n otorgada por el \u00e1rea de banquetes liderada por ARIANA MORALES , RAUL RUIZ , WILBERT Y  ADRIANO. En una primera ocasi\u00f3n realice un desayuno con colaboradres y Director General de la empresa para cu\u00e1l trabajo y el trato fue expectacular, generando con ello la confianza de regresar y as\u00ed fue realic\u00e9 un foro en el cual el personal siempre estuvo atento a nuestras necesidades.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r872315615-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Gio P", "title": "Excelente servicio", "comment": "Me encant\u00f3 la atenci\u00f3n de Ra\u00fal , Ariana y Wilbert, son muy atentos al igual que los chicos de mantenimiento, nos ayudaron a acomodar nuestro stand y su servicio de primera calidad, siempre al pendiente!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r871073616-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Ubalmi H", "title": "Excelente servicio", "comment": "La habitaci\u00f3n era muy c\u00f3moda. El desayuno era variado, estuvimos 6 d\u00edas y siempre hab\u00eda comida diferente. Muy c\u00e9ntrico y c\u00f3modo, cerca de otras localidades. Sergio siempre en el desayuno pendiente. Y Filem\u00f3n en el \u00e1rea de la barra. Son los m\u00e1s simp\u00e1ticos, atentos y familiares. Ha sido una experiencia incre\u00edble", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870979669-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Maria S", "title": "Me encanto!!!vuelvo sin dudarlo", "comment": "Es una opci\u00f3n genial para plan de conocer alquilando carro como hicimos nosotros por toda la riviera Maya ,el hotel cuenta con todas las coberturas ,comodidades su habitaci\u00f3n es genial ,grande, con aire ,y muy organizado  Todos los dias nos organizaban la habitacion con todo limpio y renovado ,la encontrabamos impecable y la cama espectacular para descansar despues de haber recorrido tanto.  El personal de todas las areas muy amables ,el desayuno delicioso ,tengan en cuenta que es un hotel que no es todo incluido ,solo para adultos y la opcion del desayuno fue la mejor para nosotros que saliamos despues de las 7 am  La atencion calida de don Jose ,siempre sonriente y atento , Antonio siempre pendiente de como me gustaba mi cafe ,Ivan muy atento y en las noches con emmanuel con", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870681327-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Mike M", "title": "Sastisfecho", "comment": "Excelente servicio de parte de todo el personal que labora en el hotel y excelente trato de la supervisora Rub\u00ed Herrera, mi esposa est\u00e1 muy satisfecha con el servicio fue su primera vez en venir acompa\u00f1arme", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870631973-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "December 2022", "author": "Jean-Arnaud T", "title": "Muy satisfecho", "comment": "Estamos c\u00f3modos y felices nos encanta la habitaci\u00f3n amplia limpia y atendidos por el personal de supervisi\u00f3n por parte de Rub\u00ed Herrera. Recomiendo el hotel c\u00e9ntrico y elegante y listo para plan de trabajo y paseo de amigos", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870556817-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Pilar A", "title": "Hotel fabuloso en negocios", "comment": "Hotel fabuloso en negocios   Gracias por las atenciones y por apoyarnos antes y en el evento que tuvimos en el hotel , contar con estacionamiento es un plus todos los participantes estuvieron encantados , la comida fue muy buena ,igual que los bocadillos gracias a wilbert , Ariana y Ra\u00fal por el tiempo y por cuidar los detalles para que fuera un \u00e9xito", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870064296-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Pamela L", "title": "Muy satisfecha", "comment": "Excellence estoy Feliz y muy agradecida con el hotel smart. La genre me ha dado un gran bienvenida en mi llegada. Cama comoda y lumpia. Lugar ameno y placentero. Lo recomiendo para futures parejas, familia y amigos. Gracias Rubi Herrera por ser parte de este hotel.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870046373-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Dolly A", "title": "Viaje cancun", "comment": "Muy agosto!!! La llegada el servicio de restaurante,  la encargada de camaristas. Todo muy agustino. No pase coraje alguno. Lo recomiendo,  la atenci\u00f3n  es buena. Esperemos regresar pronto y volver a visitarlos. No pierdan esa gentileza al recibir.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870039067-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart1_files/default-avatar-2020-32.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Eric Mercado Pennsylvania usa", "title": "5 star hotel", "comment": "This is a 5 star hotel. Love it !. Hotel smell SO GOOD ! Rooms are beautiful and very clean, comfortable, Food is delicious. Breakfast is the best with a lot of selection. Pool are very nice. Hot tubs. Very good location close to park las palapas, shops , places to eat. In this Hotel you feel safe all the time. Easy to take taxi. I will go again to this place. The staff is friendly and always smile", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r913357838-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart1_files/default-avatar-2020-29.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "Buddha7", "title": "Another great stay!", "comment": "This is our second stay here (first was 2019). Both times a great deal with Sunwing. I have only good things to say about the hotel. Staff are friendly and helpful. We are currently in 608 and have a great housekeeper... impeccable cleaning, always replenishes all the room amenities. She is a sweetheart! Always a smile and a Buenas Dias. Her name is Eva Lucas Ramirez. (just checked the housekeeping business card). The room is quiet and the air conditioning works great. Always hot water when you need it. The staff keep the hotel very clean and I notice they\\'ve been painting lately. I absolutely love the location of the hotel...it is central and close to everything. We haven\\'t used a taxi once... always walk or take the local bus. It\\'s close to a major mall, grocery stores, great", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r879209035-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/b8/default-avatar-2020-22.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Rodney P", "title": "Great Staff!!", "comment": "Check in was easy, clean rooms each day while we were there,food was great, staff is amazing, 24/7 coffee bar open, lots of options from the pool, Jacuzzi,  walks with wife on the pool deck, security is on 24/7 ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r898277419-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/df/99/default-avatar-2020-40.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Sarah T", "title": "Wonderfully relaxed stay", "comment": "Flew home on Monday after having had 12 days of  the most chilled enjoyable time ever, this holiday was a big  treat and was a very very much needed time away. My time here was \u2018exactly\u2019 what I needed, from beginning to end.  I am a very open chatty kinda girl and I was very pleased to have this reciprocated by ALL staff from arrival at the reception to cleaning staff, security, breakfast staff, bar staff, DJ and, well, everyone else. I\u2019m not brilliant at remembering names which is a shame because I\u2019d love to mention everyone but here are just a few \u2026 Belan, the most beautiful girl in every way and a good laugh,  Emmanuel very  easy to communicate with and interested in what you have to say, very pleasant and fun!! I never saw anyone slacking in their work and always saw people", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r895543913-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "alimack77", "title": "Excellent Oasis in downtown Canc\u00fan!", "comment": "We have just stayed at the hotel for a week. We thoroughly enjoyed our time here. Our time in Cancun was a mixture of sightseeing with a couple of days relaxing by the pool. Rooms were absolutely fine and had everything you need for a short stay. Pool and terrace was lovely. The staff were exceptionally nice and helpful from the waiters eg Emmanuel and Jose, to the reception staff and the security staff at the door to help with taxis etc. The location is great for local restaurants and easy walking distance to Parque de la Palapas for authentic food trucks! If you want to stay away from the Hotel Zone and stay downtown, I would highly recommend this hotel!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r891269721-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2023", "author": "Stephen M", "title": "It is like visiting family.", "comment": "Like visiting family. Except better. Everyone was exceptional. Security, reception, housekeeping, all amazing. Room was great, food was excellent and staff wonderful. Shout outs to el capitan, Ivan, and, Juan, Jose, Angel, Emmanuel, Mesero, Erick and Belen. These folks are why we have visited this hotel 6 times in the last 5 years. My wife Janice is never happier than when we are here.  Thank you all!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r889215891-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Brendan C", "title": "Great location and value. Comfortable hotel. Wonderful, caring staff.", "comment": "I made my fourth trip to Smart Cancun for three weeks in February/March, 2023. Oasis has two hotels on the same site in downtown Cancun (about 8km from the nearest part of the hotel strip.) \"Oh Cancun\" and \"Smart Cancun\" share a very nice terrace area  with a well-maintained pool,  small gym, a good restaurant and a poolside bar. They also share some staff. The rooms are not opulent but they are clean and well-equipped, with a room safe, television, fridge and a comfortable bed and bathroom. The location is close, and within walking distance, to the downtown eateries, a sizeable supermarket, a traditional Mercado, the town square and a large shopping mall. I go there for a quiet and restful winter break from the Calgary, Alberta winters. Other hotel guests come from Europe,", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r884309110-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Carmel C", "title": "Great service, bottomless coffee and fantastic banter.", "comment": "Bites restaurant staff were brilliant. Such great service from Jose, Emmanuel, Jorge, Kenia and Belen in particular. Aldo at the tourist desk was so helpful, interesting, chatty and very very funny! We appreciate all the great service and your kind understanding of our very poor Spanish. From 2 Australian women in Cancun! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r884259322-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Monika S", "title": "Amazing stay", "comment": "We had the best time in Cancun and lovely stay in this hotel. Everyone was super nice, helpful and welcomed. We want to mention especially Emmanuel and other staff: Angel, Kenia, Belan, Jose and Erick who made our stay even more pleasant. Hotel is in a very good location downtown, easy to take a bus to the beach or as pick up location for guide tours. I was very impressed with facilities like sport bar and outdoor hot baths. Breakfast was very good. Pool area was very comfortable where you could relax. We would highly recommended Smart by Oasis Hotel.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r881678707-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2023", "author": "WalshDog", "title": "Great experience and worth the price", "comment": "Really enjoyed my 2nd visit to Smart Oasis and will likely go back again. 2nd visit was a lot better as I was a few floors higher and not facing the traffic so got a better sleep. First time was facing the traffic and right below the coaches that pull up at 5am and lots of shouting cause of this, so be sure to take ear plugs. Breakfast was decent with wide selection of light and big breakfasts. Gym was decent and can get a good workout done, with free weights and benches. Pool area is so chilled and could stay there a while. Shops across the road and buses nearby that take you to beach. Thank you to all the staff there and had a great time.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r881670927-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart2_files/default-avatar-2020-6.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Dan C", "title": "Best holiday ever", "comment": "Good hotel, nice rooms, nice food, good music by pool and the staff are so nice, all so friendly and helpful and never stop working. We had problems with transport and the receptionist helped us and sorted the problem. They also helped us book different activities and really made the holiday so much better.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r881460292-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart2_files/default-avatar-2020-18.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Christine E", "title": "Great place to stay", "comment": "Wonderful place to stay, very clean, great food, wonderful pool, and fantastic location. Easy to get anywhere in cancun. The food here is really good, the staff are fantastic, we had some issues with Sunwing as they didn\u2019t send reps to the hotel for us to book excursions and Raquel was amazing at helping us out.   Highly recommend staying here:)", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r877448261-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/79/default-avatar-2020-21.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Roberts S", "title": "Raguel", "comment": "Very nice stuff and helpful is wel they will help with everything.And I enjoy staying in here and stuf will try to help you with everything if you have missed flights or canceled they will always found a way how to help you and never will leave you.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r877025277-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/07/a7/78/8c/misteretraveller.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "MisterEtraveller", "title": "Excellent Choice in Downtown Canc\u00fan", "comment": "Smart Canc\u00fan / Oh! Canc\u00fan are sister hotels run by the Oasis group. My wife and I have stayed at one of these hotels every year for the last several years. The best feature of either hotel is the professionalism of the staff. Not only are they very well trained in the art of hospitality but each staff member, from the front desk to housekeeping, seems happily committed to customer service. This makes for a welcoming atmosphere at all times while enjoying the property. The hotel is a solid 3.5 star that features an excellent and large pool area that is entirely secluded from the city that surrounds it, earning its name - urban oasis. The restaurant serves good food at competitive prices. The rooms are large and comfortable with exceptional symmetrical WiFi (40-90Mbps up and down)", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r877001779-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e3/6a/default-avatar-2020-47.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Maressa O", "title": "Excent experience, higly recomend!", "comment": "The hotel is nice and the staff is amazing! I want to thanks everyone, specially Sergio, Filemon, Ivan and Kenia for their excepcional services. I dont remember the name of everyone that I interected during my staying but all of them deserve compliments, all sthe staff of bytes restaurant, the recepcionist who were very welcoming and friendly, the are all great! I will certainly stay here again if I come back to Cancun in the future.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870319792-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/5d/default-avatar-2020-32.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Rachael S", "title": "Vacation", "comment": "Filemon was very helpful and awesome bartender. His drinks were delicious and I would recommend anyone looking to go to Cancun to stay at this establishment. Sergio and Eric were awesome also. Really helpful staff!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r870066265-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/b8/default-avatar-2020-22.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Ziyan Z", "title": "Nice service!", "comment": "Comfortable stay, nice people. Locations are nice. It\u2019s closed to restaurants and shopping malls. Staffs at the front desk are helpful with explaining things and providing services! It might not be a good choice if you\u2019re looking for fancy hotels and meals. But it\u2019s pretty good choice if you look for comfy stay and reasonable prices", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r865660988-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/77/ae/default-avatar-2020-2.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Shoxrux E", "title": "Vacations", "comment": "Good hotel,good ubicacion and the persons from the front desk are very helpful specialky Raquel the concierge Raquel,and also Jaanai,Andrea,Nash,Nazario,Michel, Paco and Francisco and Bryan i like the breakfast actually is very good", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r864510797-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
