INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ec/3d/default-avatar-2020-1.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Maddie A", "title": "Lovely!", "comment": "Loved our stay here, breakfast was awesome with a huge selection and the staff were super friendly and attentive :) Sad the weather was rainy as we would have loved to swim in the gorgeous pool! Absolutely would recommend", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r863989346-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
