INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart5_files/default-avatar-2020-21.jpg", "date_comment": null, "date_stayed": "July 2022", "author": "Rachel", "title": "Review", "comment": "Honestly place is great for the price, the pool was great staff was pretty Racquel Michelle and Kristian were great the tours were great came in front. Breakfast lunch was good as well I definitely recommend to come. It\u2019s also near the bus R1/R2 if you want to go the nightlife spot in cancun (mini Vegas) near Walmart , Costco  and Las America mall. There\u2019s a lot of Mexican restaurants near by literally 2/3 min walk. I\u2019d recommend if you\u2019re on a budget coming here", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r850661195-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
