INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/20/default-avatar-2020-31.jpg", "date_comment": null, "date_stayed": "June 2022", "author": "Kathy L", "title": "Ms Kathy Lynn", "comment": "The wait staff at Bites restaurant were amazing! Ivan Jose Erim and Johanan Sargio. They were very friendly, and attentive and took very good care of us. They always smiled and made sure we had fresh coffee! Give them a raise!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r844636823-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/01/f3/7a/a0/kazza-a.jpg", "date_comment": null, "date_stayed": "February 2022", "author": "Karen A", "title": "Loved the atmosphere in this hotel", "comment": "Rooms could do with improving/renovating maybe. Rooms are a good size with tea and coffee facilities and a fridge. Wi-fi is very good. Located just outside of the Hotel Zone, bus stop is near to the hotel so its easy to go to the beach. A shopping mall is approx. 5 mins walk away. Hotel is very sociable with friendly staff. DJ starts around late afternoon by the pool - good atmosphere. Other reviews say that pool view rooms are quite noisy but I didn\\'t think they were and the view is nicer than the road view.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r840474492-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/06/default-avatar-2020-18.jpg", "date_comment": null, "date_stayed": "May 2022", "author": "Guusje N", "title": "Estoy feliz en este hotel!", "comment": "Thank you for the good service. I immediately felt very welcome and I needed that! My food got delivered to my room and Carlos, Filemon and Raul were muy excellente. Muchisimas gracias por eso! Voy a volver :)", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r837518520-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
