INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/df/99/default-avatar-2020-40.jpg", "date_comment": null, "date_stayed": "March 2022", "author": "Kristhal Vergara", "title": "Great hotel, GREAT POOL AREA!", "comment": "Really good hotel, great pool area. Price is very good for what rooms are. I stayed with some friends for 6 days and we honestly had a great time.  One little thing, they charged my friend\\'s credit card twice, and when they reimbursed they were short over $50. They seem to have a problem with their atms/payment system because at the bar we also had issues with ALL our cards so just watch out for a double charge.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r829949944-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "./smart6_files/default-avatar-2020-2.jpg", "date_comment": null, "date_stayed": "February 2022", "author": "Will S", "title": "Oasis smart hotel cancun", "comment": "Stayed here cor 10 days great hotel perfect location in downtown staff where great Daniel in the dining room and poolside was very helpfull also the security on the front door where great will definitely come back and recomend to anyone", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r829144219-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/59/default-avatar-2020-24.jpg", "date_comment": null, "date_stayed": "February 2022", "author": "Leonor Alarcon Mendez", "title": "BREAKFAST - PRESENTATION BY PETRA OPERADORA.", "comment": "Excellent service in food, assembly and first class food. Thank you very much Frinne Soda for your support to achieve the success of this event. Ra\u00fal Ruiz, we appreciate the attention in each of the services.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r828224216-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/d0/default-avatar-2020-33.jpg", "date_comment": null, "date_stayed": "January 2022", "author": "Adam M", "title": "Mr.", "comment": "Absolutely fantastic service from Filemon, Erick, Sergio, Javier and the rest of the staff. Fantastic drinks , fantastic food, very clean , and excellent facilities. Definitely the best hotel I\\'ve been to in Cancun.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r825289819-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/11/default-avatar-2020-41.jpg", "date_comment": null, "date_stayed": "December 2021", "author": "Jamaica-Beaches", "title": "Hospitality Experience", "comment": "Aketzali Lugo, Filem\u00f3n and other team members were very helpful during our time in the cafeteria. They warmly greeted us and ensured that our needs in the cafeteria were taken care of.  Aketzali demonstrated excellent leadership qualities when she carried out her duties. She liaised well with her team when the caferira became packed and helped to maintain a chaos free and safe environment (maintaining standard COVOID- protocols).", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r823618899-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/81/default-avatar-2020-43.jpg", "date_comment": null, "date_stayed": "December 2021", "author": "Billy Mitchell", "title": "Christmas/New Year\u2019s Vacation", "comment": "The Poolside bar was very nice. Our server Filemon was delightful. The elevator broke but they had a backup. It\\'s cool they have hot tubs, cabanas, and a nice lounge area. Music is solid too! \ud83e\udd42\ud83c\udf7b very nice.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r823163010-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/11/default-avatar-2020-23.jpg", "date_comment": null, "date_stayed": "September 2021", "author": "Stefan S", "title": "Just Perfect !", "comment": "We were at the Oasis Smart on a Business trip for 3 days. The hotel is perfectly situated in the middle of Cancun. The meeting room was nice equipped and had perfect A/C which was important. The rooms were super clean and we loved the pool area for some of our breaks just to relax in between meetings. Truly an Oasis for business or vacation ! \uf04a Thank you Magdiel for your hospitality and of course thank you so much Ariana Morales \u2013 our Event Manager for all your assistance and kind gestures. BRAVO !", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r812756978-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/9c/default-avatar-2020-14.jpg", "date_comment": null, "date_stayed": "September 2021", "author": "Zainab Ahmed", "title": "4 days visit", "comment": "We were actually glad to have visited this hotel. Right in downtown with access to all stores right away when in  need! Moises was our receptionist/ concierge and he was delightful and extremely helpful for all our needs. He got us a gorgeous room with an amazing view. He is a patient listener. He went the extra mile to even ask for an early breakfast for us because we had our tours way before breakfast time. The rooms were regularly cleaned and hygiene was maintained. We never once complained for any issues. We just wished we had slippers though! Everyone at the hotel was helpful and extremely polite. If we had any questions Moises and other staff would just guide us right away to any place at any time. We highly recommend urban oasis", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r810588845-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/bb/default-avatar-2020-65.jpg", "date_comment": null, "date_stayed": "September 2021", "author": "miss j", "title": "Nice vacations", "comment": "I want to thank the front desk staff again as they made my days very enjoyable. They helped me at all times and always so kind. Thank you very much David, Moises, Nazario, Enrique, Angel. I hope tu visit again", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r809138835-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/42/default-avatar-2020-20.jpg", "date_comment": null, "date_stayed": "September 2021", "author": "erin", "title": "Great service", "comment": "Marco, David, Jos\u00e9, Eric, y Javier, de ellos recibimos servicio maravilloso, gracias! El desayuno fue bien rico.   The food was great and service even better. Thank you to marco, David Jose and Eric and Xavier, the pool area is lovely and the breakfast is great", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r809048964-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(9, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "September 2021", "author": "mike w", "title": "TOP NOTCH!!!!!!!", "comment": "felt in love with the place!! everything is good in the hotel!!! comfy beds, professional staff from front desk (angel, moises, nazario, enrique, david, ana) would i stay here again?? for sure, every single time i come to cancun... flawless hotel for 75 usd, good deal", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d154429-r808046813-Smart_Cancun_The_Urban_Oasis-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 3.0, '{"profile_picture": "./oh1_files/default-avatar-2020-12.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Karla Plaza", "title": "No reserven con Super", "comment": "Reservamos mi novio y yo una habitaci\u00f3n por recomendaci\u00f3n de TripAdvisor, a trav\u00e9s de la agencia S\u00faper aqu\u00ed sugerida.   Cuando llegamos no la ten\u00edan en sistema y no hab\u00eda habitaciones disponibles. En el hotel no nos hicieron caso de nada y un poco groseros.  Argumentaron que no es la primera vez el tema con reservaciones de terceros.   La agencia resolvi\u00f3 3 horas despu\u00e9s con un reembolso ( despu\u00e9s de 3 llamadas en ingl\u00e9s, cabe mencionar que no tienen agentes que hablen espa\u00f1ol) Tuvimos que buscar otro hotel para el mismo d\u00eda, a \u00faltima hora y muy caro.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r908644450-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 3.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/11/default-avatar-2020-23.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Ver\u00f3nica A", "title": "Muy bien excepci\u00f3n del restaurante", "comment": "Muy buena experiencia, desde la amabilidad del personal, hasta las instalaciones y ubicaci\u00f3n, lo \u00fanico que no me gust\u00f3 fue el trato del personal en restaurante, en dos ocasiones estuve en el hotel, la segunda fui a acompa\u00f1ar a familiares que se quedaron ah\u00ed y quer\u00eda pagar el desayuno extra para mi y se portaron muy groseros, lleg\u00f3 el gerente de restaurante y se Porto igual de grosero", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r905608885-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/99/default-avatar-2020-57.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Monifer", "title": "Bien Ubicado, Cerca a todo, Bus cerca para ir a la playa, Moderno, Desayuno Buffet Delicioso", "comment": "Excelente ubicaci\u00f3n en el centro, el bus que va a la playa pasa en la esquina, cerca a centros comerciales, restaurantes, y a la terminal del bus a playa del carmen, etc. el lugar en muy moderno y completo en sus habitaciones. el desayuno buffet incluido es delicioso y variado, los platos a la carta no son tan costosos. el \u00e1rea de piscina espectacular. Solo mejorar\u00eda la  actitud de servicio en recepci\u00f3n, la persona que nos atendi\u00f3 (de  lentes) poco formal; el resto muy formales como don Jos\u00e9 en el restaurante.  igualmente deber\u00edan tener extractor de olor en los ba\u00f1os. en general una excelente opci\u00f3n para los que quieren estar en el centro de Canc\u00fan. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r905125963-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 3.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/de/5a/default-avatar-2020-36.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Jonatan P", "title": "Estancia corta en 3 habs 2 d\u00edas", "comment": "Estuve hospedado del 18 al 20 de Julio en el Oh. Lo eleg\u00ed por cercan\u00eda a un hospital y por ser \u00fanicamente adultos y gran decepci\u00f3n porque vi varias familias. La primera noche me quede en hab 615 y muy bonita. La atenci\u00f3n de la recepci\u00f3n fue buena y la habitaci\u00f3n estaba impecable. Conexiones a lado de la cama, todo a la mano.  Habitaci\u00f3n moderna y muy limpia. de abajo por lo cual no enfriaba igual. Una l\u00e1mpara no estaba conectada y el enchufe estaba en malas condiciones, lo intente conectar pero se zafaba y  Hice mi salida y tuve que quedarme una noche m\u00e1s as\u00ed que llegue ese mismo d\u00eda en la noche a volver a hacer mi check in.  Me dieron habitaci\u00f3n 027 al entrar apestaba a cigarro y decid\u00ed salirme de inmediato a pedir mi cambio de habitaci\u00f3n. Muy amables me dieron la 011 qu\u00e9 a mi", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r904366849-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 3.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/2b/default-avatar-2020-52.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Armando  A", "title": "Gracias", "comment": "La atenci\u00f3n en el hotel es de familia, desde la persona que te recibe hasta la persona que te despide , todos tienen muy claro el concepto de atenci\u00f3n al cliente, y se ve la sinceridad de su atenci\u00f3n, espero regresar pronto y no buscar otros lugares , Gracias a todo el personal Aketzali y todos en general los voy a extra\u00f1ar ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r898274167-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/81/default-avatar-2020-43.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "dramos", "title": "Exquisito todo", "comment": "C\u00e9ntrico para la b\u00fasqueda de los tours y otros acceso de la cuidad. El hotel cuenta con \u00e1reas divinas, relajantes, todo el personal es amable, atentos y servicial. Enmanuel en la barra y en el desayuno estuvo full pendiente de nosotros. Nos brindaron una atenci\u00f3n incre\u00edble \ud83d\ude0a mejor que estar en casa. Si necesitabamos alg\u00fan asesor\u00eda pod\u00edamos contar con el y su equipo. Me ayudo a darle una sorpresa a mi pareja en su cumplea\u00f1os \ud83c\udf82 y todos los del restaurante participaron cant\u00e1ndole. Fue incre\u00edble al servirnos tragos en el a\u00e9rea de la piscina. \ud83d\udcaf recomendado   La comida saludable con variedad de opciones y super super rica. Con opciones locales y tambi\u00e9n de otras al estilo americano. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r895446344-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/9c/default-avatar-2020-14.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Margarita G", "title": "Otra magn\u00edfica estad\u00eda", "comment": "Muy buena estad\u00eda. Ubicaci\u00f3n muy conveniente y el servicio fenomenal desde que llegamos con la amable Raquel, quien nos explic\u00f3 a cabalistas todo y contest\u00f3 todas nuestras preguntas. Tomamos ventaja de el pase del d\u00eda a Grand Palm oasis donde la pasamos muy bien en la playa. Esta es la segunda vez en este hotel y no ser\u00e1 la \u00faltima. Much\u00edsimas gracias por todo. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r893746201-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/df/2b/default-avatar-2020-39.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Miguel A", "title": "Excelente atenci\u00f3n servicio y trato de todos resaltando a la se\u00f1orita Aketzali y la se\u00f1orita Brenda en el comedor", "comment": "Tuve una experiencia con mi habitaci\u00f3n y la se\u00f1orita Aketzali luego me ayudo de inmediato con un trato y eficiencia de primera  El los desayunos fue atendido toda mi estancia por la se\u00f1orita Brenda y mi opini\u00f3n es no de 10 si no mil para ella en general quede muy satisfecho con todo gracias ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r892318459-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "./oh1_files/default-avatar-2020-12.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Agus E", "title": "Muy buen hotel, s\u00faper recomendable!", "comment": "Muy lindo hotel, tal cual como se muestra en las im\u00e1genes.  Emanuel, Jos\u00e9 y \u00c1ngel nos brindaron una excelente atenci\u00f3n en el desayuno  Sin duda volveremos Lo \u00fanico a tener en cuenta es que dan cierta cantidad de toallas para la pileta, si llegas despu\u00e9s de las 17 hs al hotel posiblemente no consigas. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r892299480-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/eb/default-avatar-2020-27.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Israel s", "title": "Excelente servicio  muy agradecido", "comment": "Excelente la atenci\u00f3n  de raquel Gomez  todo muy bien la atenci\u00f3n de el personal el estacionamiento muy amplio las albercas camas balinesas la comida buena est\u00e1 muy cerca de todo los Day pas muy econ\u00f3mico ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r891889289-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh2_files/default-avatar-2020-64.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "MANNY M", "title": "Excelente hotel", "comment": "Fue una muy buena experiencia estar en este Hotel. Personal muy amable, en especial  Brenda, Jos\u00e9, Erik, Kenia y Angel.  El joven encargado del Cheking en la noche, no recordamos su nombre, pero fue completamente amable. Sobre todo cuando no entend\u00edamos c\u00f3mo conectarnos a internet y nos ayud\u00f3 a pesar de andar ocupado .S\u00ed volver\u00edamos a hospedarnos en este hotel. Una nota: ser\u00eda bueno ampliar la variedad de alimentos en el buffet . Gracias.  ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r891676364-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/ce/default-avatar-2020-7.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "kevin c", "title": "Muy confortable y recomendable!!", "comment": "Hola qu\u00e9 tal recomiendo mucho este hotel desde su ubicaci\u00f3n, instalaciones y comodidad; sin dejar de mencionar a su personal s\u00faper atento y c\u00e1lido la chica de servicio Meyita que hac\u00eda mi habitaci\u00f3n siempre con mucha dedicaci\u00f3n a su trabajo, los chicos de el desayunador que eran 4 un Sr y tres chicos j\u00f3venes  que nos serv\u00edan muy atentos y siempre dispuestos en su trabajo y a las chicas de consierge Raquel &  adirah muy atentas siempre dispuestas a solucionar los problemas o consultas", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r891627686-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
