INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Jose L", "title": "Excelente servicio", "comment": "Muy comodas las habitaciones la Srita. Aquetzali Lugo Muy atenta con su servicio  El comedor muy rico todo de muy buena calidad  Todo muy limpio y c\u00f3modo las albercas excelente el agua muy limpia  En general todo muy bien ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r891306490-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
