INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/66/default-avatar-2020-53.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Elsa Mar\u00eda M", "title": "Divino", "comment": "Divinos los muchachos raque y Nazario el hotel muy lindo y las personas muyy amables muy c\u00e9ntrico las camas c\u00f3modas, la habitaci\u00f3n muy confortable  y el caf\u00e9 espectacular y el ba\u00f1o muy limpio  la piscina est\u00e1 limpia y muy hermosa era sobre todo la amabilidad de las personas es excepcional ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r886515317-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
