INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "marlene m", "title": "Excelente Servicio del personal", "comment": "Excelente servicio me atendieron Anselmo el d\u00eda de llegada y me apoyo con el registro a\u00fan cuando hab\u00eda perdido mi hoja de confirmaci\u00f3n.  Tuve problemas con internet y de inmediato Michelle me apoy\u00f3 y se soluciono problema en la habitaci\u00f3n .   Gracias :)   Muy buen servicio ! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r885711251-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
