INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Martin A", "title": "Oh cancun", "comment": "Estuve en una estancia tranquila ya es la cuarta vez que vengo al hotel mi favorito de preferencia las instalaciones muy lindas y agradables la piscina la mejor de todas en el centro no obstante un agradable recibimiento por parte de nuestras amigas recepcionistas aketzali michelle y su supervisor david como siempre tan amables gran atenci\u00f3n por parte de todos en general", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r885504853-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh4_files/default-avatar-2020-43.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Marco B", "title": "Hotel viaje", "comment": "Las instalaciones son muy lindas por dentro y por fuera las habitaciones amplias y lindas con una gran vista las atenciones en el hotel las mejores apoy\u00e1ndonos con un ambiente lindo y agradable gracias a las recepci\u00f3nista aketzaly Michelle y David que me han ayudado con tener una linda vista ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r885503986-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh4_files/default-avatar-2020-41.jpg", "date_comment": null, "date_stayed": "August 2022", "author": "Culture298298", "title": "Un buen hotel para pasar un par de d\u00edas...lejos de la playa, pero hay movilizaci\u00f3n  a la salida del Hotel.", "comment": "Calidad y precio en su justa medida..lo recomiendo 100 % La atenci\u00f3n de Kenia, Gabriel, Antonio, Angel, Gary, extraordinario...me cantaron las ma\u00f1anitas..30 de Agosto d\u00eda de mi cumplea\u00f1os.  Un poco tarde mi reconocimiento,  no encontraba la tarjeta con los nombres", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r884300913-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/2b/default-avatar-2020-52.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Federico C", "title": "MUY BUENA", "comment": "Muy buena atenci\u00f3n, especialmente Raquel. La pileta  y los hidromasaje Muy buena. Lo recomiendo. Cerca de shopping Las Am\u00e9ricas  y monumentos Ceviche. A minutos del Centro y yendo en colectivo a minutos de toda la zona hotelera.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r884161248-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e6/ea/default-avatar-2020-54.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Evelyn L", "title": "Buena Experiencia", "comment": "Muy buen servicio, la comida es muy rica y variada.  Vinimos por el cumplea\u00f1os de mi hijo y al ingresar a la habitaci\u00f3n hab\u00eda un detalle de parte del hotel, eso fue muy  lindo. Lo \u00fanico que no nos gusto fue el ba\u00f1o de la  habitaci\u00f3n y el de la piscina.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r883780256-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/60/default-avatar-2020-64.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Sofia C", "title": "Muy muy bueno!", "comment": "Muy buen hotel!  Los chicos del servicio de desayuno muy amables, Erik, Emanuel, \u00c1ngel y Jorge!  Todo el hotel es muy lindo, amplio y cuidado. La Habitaci\u00f3n es enorme, se nota que era buena y la han restaurado  muy bien. La piscina es gigante y tiene un dise\u00f1o muy bueno. Lo recomiendo.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r883021350-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/7c/01/default-avatar-2020-5.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "\u0420\u0443\u0441\u0442\u0430\u043c\u0431\u0435\u043a \u0410", "title": "Tourism", "comment": "Nash, muchas gracias, eres una chica hermosa e inteligente. haces bien tu trabajo, muchas gracias, estoy agradecido. te invito a un hermoso y soleado Uzbekist\u00e1n...Y los uvas est\u00e1n bien alimentados. la piscina tambi\u00e9n es genial . me encanta bosein.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r881984199-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
