INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2023", "author": "Fabian Sandi", "title": "Luna de miel", "comment": "Excelente servicio y atenci\u00f3n de todo el personal, nos vamos satisfechos y contentos para costa Rica. Excelente por parte de: Angel, Emmanuel, Jos\u00e9, Erick, Kenia y Belen. Quedamos encantados.. VOLVEREMOS", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r877409801-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh5_files/default-avatar-2020-65.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Harold M", "title": "Visita al hotel Oasis", "comment": "Un agradecimiento al equipo de trabajo del restaurante Erick, Jos\u00e9, Emmanuel, Quenia, por su buen servicio y disposici\u00f3n, la alimentaci\u00f3n muy buena, la alberca espectacular en general el servicio del hotel excelente. La pasamos genial  Bendiciones", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r877098678-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f3/e4/default-avatar-2020-30.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Rodrigo G", "title": "Vacaciones", "comment": "Me atendi\u00f3 Eric y Filemon. Da gusto tener a personas tan profesionales y encantadoras para que pases una estancia espectacular. Sin duda repetiremos. Es un hotel em el centro de canc\u00fan muy recomendable con todo tipo de comodidades. 100 % recomendable, repetir\u00eda 1000 veces (:", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r874390720-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/4a/default-avatar-2020-42.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "nicolasrpuertomontt", "title": "Excelente servicio en restaurante y en general del Hotel", "comment": "Solo para decir buenas cosas del servicio del Hotel. En especial servicio en restaurante. Muy preocupados en general de nuestra comodidad, buena comida y buenos tragos. Felicitar especialmente al equipo compuesto por Antonio, Iv\u00e1n, Jos\u00e9 y Emmanuel, que hicieron muy grata nuestra estad\u00eda de 1 semana . Definitivamente recomendar\u00eda de nuevo este Hotel y volveremos seguro con mi pareja. Agregar adem\u00e1s muy buena habitaci\u00f3n, c\u00f3moda y grande. La cama excelente y siempre todo limpio y ordenado.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r872114931-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-o/09/ed/c0/ca/angelica-u.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Ang\u00e9lica U", "title": "Buen\u00edsima opci\u00f3n!", "comment": "Buen hotel, relaci\u00f3n calidad precio adecuada. Buena ubicaci\u00f3n si tus planes son m\u00e1s culturales que de playa. El servicio es excelente y el desayuno muy variado y delicioso. Todo el personal de apoyo es muy amable. Si quieres un d\u00eda de playa, el hotel tiene la opci\u00f3n de un pasa d\u00eda en uno de sus hoteles ubicados en la zona hotelera por un precio muy accessible y es all inclusive.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r872109757-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/fe/default-avatar-2020-58.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "ANDREA LIZBETH L", "title": "Experiencia", "comment": "Me encanto el hotel, desde la habitaci\u00f3n, la piscina, los desayunos deliciosos y la atenci\u00f3n del personal de cocina, muy amables el Sr Jos\u00e9 , Ivan, Sergio y Antonio. Sin duda regresar\u00eda nuevamente.\u263a\ufe0f\u263a\ufe0f", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r871157102-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/73/default-avatar-2020-13.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Samuel R", "title": "Luna de Miel \ud83c\udf19 \ud83c\udf6f", "comment": "Un servicio excelente. Sergio es el mejor mesero de por la ma\u00f1ana en el desayuno y Filem\u00f3n es el mejor bartender. Una experiencia inolvidable. Recomiendo el hotel Oh Canc\u00fan Urban Oasis por encima de muchos.\u2764\ufe0f\ud83d\udc4c\ud83c\udffd", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r870979944-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/20/default-avatar-2020-31.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Iris Ileana P", "title": "Muy buen servicio", "comment": "Excelente servicio de Sergio, Jos\u00e9, Emannuel y \u00e1ngel en el restaurante, los previos muy accesibles y los alimentos deliciosos... las instalaciones del hotel son c\u00f3modas y limpias al igual que las habitaciones. Bien ubicado a dem\u00e1s", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r870536602-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ee/10/default-avatar-2020-8.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "RUSBEL V", "title": "Vacaciones", "comment": "Exelente trato de la s\u00f1. Raquel.  muy buen hotel, todo el personal bien alegre. La estructura  del establecimiento muy bien  equipada y muy limpia. Gracias  por ser parte de mis buenas vacaciones, gracias.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r870136369-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "FRANCISCO A", "title": "Urban oasis cancun", "comment": "El hotel es excelente , limpio , instalaciones muy buenas , la piscina s\u00faper nice y en el bar de la piscina hay 3 empleados maravillosos . La mejor atension que he recibido comparado con otros hoteles . Los empleados son muy amables . Pero quiero recalcar que en el bar de la piscina est\u00e1n . FILEM\u00d3N , IVAN, y SERGIO quien me dejaron asombrados con su servicio . Esas 3 personas son lo mejor que he visto en atender a un cliente. Mis felicitaciones al hotel URBAN OASIS CANCUN , por tener esos 3 personajes trabajando para ustedes. Est\u00e1n haciendo quedar en alto el hotel y adem\u00e1s , cuando vuelva a Canc\u00fan no dudar\u00eda en hospedarme de nuevo all\u00e1 . Una experiencia genial.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r869959607-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/df/2b/default-avatar-2020-39.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Constanza64", "title": "10 d\u00edas en Oh! The Urban Cancun Oasis", "comment": "Excelente atenci\u00f3n. A la hora del desayuno don Jos\u00e9 estaba pendiente de todo, siempre cordial, muy atento y nos ayudaba con informaci\u00f3n del lugar. Junto con sus compa\u00f1eros Antonio, Iv\u00e1n, Yari y Emmanuel, con quienes compartimos cada ma\u00f1ana, comenzamos todos los d\u00edas felices.  El desayuno es variado, pero recomendamos bajar antes de las 10:00, ya que los chilenos no acostumbramos a comer tanta fruta o almuerzo (frijoles, tamales, verduras) tan temprano jajaja.  Las instalaciones tienen una bonita piscina y un jacuzzi que invita al relajo, rodeado de sof\u00e1s y palmeras por toda la alberca, tambi\u00e9n un bar afuera por si quieren pasar la tarde.  La ubicaci\u00f3n es maravillosa, en plena Avenida, con locomoci\u00f3n todo el d\u00eda para ir a las playas (menos de 1 dolar), isla, tours, etc. Cerca de", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r869907688-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh6_files/default-avatar-2020-21.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Agustina N", "title": "Vacaciones", "comment": "Hermosa atenci\u00f3n, the White terrace es un hermoso lugar para disfrutaral lado de la piscina, la atenci\u00f3n de Filemon, Sergio, Ivan y Juan excelente, buen\u00edsima atenci\u00f3n de su parte, super recomendable para visitar", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r869710164-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e6/ea/default-avatar-2020-54.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Manuel Cardona", "title": "Mejor servicio los de la Barra y el Restauran", "comment": "Felimon, Sergio,  Ivan, Jose, Janet, Erick y Kenia. Dieron el mejor servicio, muy atentos y bien servicial. Todos muy lindos, amables y con muy buena actitud.  Saludos a los panchitos desde Mexicaly/California, Gracias por todo Felimon y Sergio de A y B ustedes hicieron nuestra experiencia mucho mas mmejo.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r869591598-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh6_files/default-avatar-2020-21.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Antonio L", "title": "viaje cancun", "comment": "sus gentes muy amables,  la habitacion amplia y limpia, la comida abumdate y variada asi como servicio de maletas con Bryan fue mut bueno y servicial, gracias.  Recomiendo el hotel oh! cancun,  asi como sus actividades. hasta la vista", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r869157884-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Roshon S", "title": "Perfect for solo traveling", "comment": "I had a wonderful time at the hotel when I say all I mean all of the staff were very welcoming. The hotel rooms were very nice with wonderful views to the Courtyard. The food was wonderful breakfast as well as the food thru out the day. This happened to be my first international trip I was by myself the location of the hotel was perfect busy with locals during the day and quiet at night. I felt super safe roaming around at night. Thank you very much Emanuel, Jose, Kenia,Raquel, Antonio, Yari and The young lady I encountered when I first entered the hotel you got busy had to go but the compliment on my hair made me feel extremely welcomed sorry I didn\u2019t get your name but thank you. I definitely recommend this hotel if you want to feel like you are not surrounded by other tourists", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868837216-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Ignacio G", "title": "Oh ten  urban oasis cancun", "comment": "Todo muy limpio ameno buena atenci\u00f3n sin ning\u00fan inconveniente, pude disfrutar toda mi estad\u00eda. Raquel la recepcionista es muy amable pero todo el Personal tambi\u00e9n. Regresar\u00eda sin ninguna duda. Perfecto para disfrutar sin ni\u00f1os ruidosos", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868798146-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Juan V", "title": "Excelentes servicios en general", "comment": "M\u00e1s que excelente el servicio proporcionado por todo el personal del hotel Urban Oasis, en especial la Srita. Raquel en recepci\u00f3n e Iv\u00e1n en el restaurante, muchas felicidades para todas y todos y gracias por sus atenciones, hasta pronto.  Atte. Juan Villanueva Sotelo y Rosa Elia S\u00e1nchez Martinez", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868775422-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Daniel Su\u00e1rez", "title": "Excelente atenci\u00f3n,  excelente lugar.", "comment": "Toda el equipo del hotel hace un gran trabajo y se esmera porque todo salga bien. Gracias a Emmanuel, Antonio, Angel, Juan y Kenia por hacernos sentir como en casa durante nuestra estancia en el hotel, su trato amable y cordial hace que la experiencia sea muy grata y te olvidas de muchas cosas. La comida en el restaurante es muy rica. La piscina, el cuarto y todo en el hotel. La ubicaci\u00f3n es excelente para estar en el centro. Recomiendo el lugar y su gente en un 100%. Gracias por todo. Volveremos.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868553298-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Valeria E", "title": "Estad\u00eda en Oh! Canc\u00fan, The Urban Oasis", "comment": "Excelente hotel, muy buena atenci\u00f3n, durante toda nuestra estad\u00eda la pasamos muy bien, el cuarto y el hotel en general s\u00faper limpios, la piscina y la zona de jacuzzi muy agradables, el equipo de trabajo de la cocina fue excelente. Muy buena atenci\u00f3n de parte de Antonio, Juan, Kevin, Emanuel y \u00c1ngel. La comida es buen\u00edsima. \u00a1Esperamos volver a este para\u00edso! \ud83c\udf0a\ud83c\uddf2\ud83c\uddfd", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868552813-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Pablo", "title": "Vacaciones", "comment": "Filemon, Erik, Sergio y todo el staff del hotel nos trat\u00f3 estupendamente! La alberca , las habitaciones,la comida, todo super recomendable!! El hotel cuenta con un parqueadero cerrado y muy seguro, Su ubicaci\u00f3n cerca de supermercados, Oxxo, casa de cambio y restaurantes. Volveremos..", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868499741-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Ernesto D", "title": "Agradable", "comment": "Un hotel moderno y c\u00f3modo, la atenci\u00f3n de Raquel en recepci\u00f3n fue muy amable, recomendado, la ubicaci\u00f3n se encuentra muy c\u00e9ntrica, y pueden tomar los transportes econ\u00f3micos a la zona hotelera, el hotel cuenta con alberca y bares para degustar bebidas. Un hotel peque\u00f1o con buen servicio ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868434848-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh1_files/default-avatar-2020-55.jpg", "date_comment": null, "date_stayed": "December 2023", "author": "Jehudi H", "title": "Great place to stay in Cancun \ud83d\ude03\ud83c\udf39", "comment": "Very nice hotel in a great spot off the center of Cancun city. Although it isn\\'t a new hotel everything is done to make your stay most comfertable and enjoyable as possible \ud83d\ude00 The facilities, swimming pool and gym are perfect and a top variety breakfast is available from 7-11+.  Not to foget the most friendly, professional and helpful staff. Especially Mr. David Cruz,  the Oasis plus\\'s supervisor \ud83e\udd47 THANK YOU \ud83d\ude4f\ud83c\udf39", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r928272582-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh1_files/guestess.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "Marilyn L", "title": "Nice hotel!", "comment": "We booked one night at the Oh! but were given rooms at their adjacent property. The pool area, restaurant and our room were all attractive and well thought out. We enjoyed the in-room coffee maker and they were generous with the coffee packets and set up.  The breakfast was included and the buffet had a nice variety of fruits and hot and cold items. There are also plenty of restaurants within walking distance and our room was quiet so no complaints here!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r878815662-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/05/dc/ab/4f/joe-hern.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Joe_Hern", "title": "Very good hotel", "comment": "This is a real oasis in downtown Canc\u00fan. The staff is very friendly and helpful. The suite was clean and comfortable.The pool was nice and clean and it had a bar with a good menu. There are restaurants in the hotel and nearby. The breakfast menu is standard, a buffet, but with limited options.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r912513298-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/7a/default-avatar-2020-25.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Donald R", "title": "Great customer service", "comment": "The whole experience was very good from the taxi drivers and the whole staff was great especially my friends at the Bites Restaurant they were very kind from the hostess to all the wait staff and kitchen staff, the security team. I mean I was treated like a King  great service was provided and they made me feel very welcome. From Emmanuel,  Belum, Melissa, all the workers at the Front desk can\\'t forger the tour agent Letica she was very kind to Mr and provided me with excellent  service. Thank all of you I had a great vacation ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r911695173-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 1.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/d5/default-avatar-2020-17.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Frank S", "title": "Not a 4 star hotel.", "comment": "Not a 4 star hotel.  Google pls fix that!  No towels in room.  Had to go down to ask, and noticed others come down for towels at the same time.  The towels I received were heavily stained. Im from Florida, so i know normal humid; these rooms have horrible mold that wrecked havoc on my allergies and i could not sleep.  Bathrooms showers don\u2019t drain; but at least water was hot.    Just hope i dont see roaches like others cited.  I was hoping to come back and do a day pass at their resort with family but thats not happening.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r905200139-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/a9/default-avatar-2020-50.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Michelle Hee C", "title": "Delicious breakfast, some special  friendly staff", "comment": "Generally good, we had to ask for towels because they provided for 2 when we were 3. Breakfast was delicious. Raquel, the concierge  Emmanuel & Carlos, our waiters, were amazing, friendly and absolutely wonderful", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r902987834-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/ef/default-avatar-2020-66.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "CARLOS P", "title": "Wonderful, great value hotel!", "comment": "Aketzali in reception excellent attention! Congratulations to all hotel staff! Breakfast very good, nice rooms, very good value for the money! Location is excellent, you can get a bus to the hotel zone in 5 minutes walking and it is reliable and fast.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r895965121-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/de/0a/default-avatar-2020-35.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Ana Vargas", "title": "Amazing experience!", "comment": "The breakfast is buffet and it was veeery good, I\u2019m vegetarian and had plenty of good options! Fruits, baked goods, yogurt, beans and local food (try the chilaquiles!), locations is amazing, there\u2019s plenty of restaurants, bars, souvenirs and places to book tours around, as well as public transportation and Parque Las Palapas which is a nice park to check out. Service is amazing, specially from our friend Emmanuel which was very nice and friendly during the whole stay here. Bed is big and clean, plenty of toiletries\u2026 nothing but good things to say about here.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r895446481-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/5b/default-avatar-2020-61.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Jimbo G", "title": "Excellent!", "comment": "Aketzali checked us in! Very friendly, good experience. Room was clean, breakfast was good. Not part of hotel zone, things like restaurants and convenient. Stores are very accessible. Overall good experience, but highly recommend.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r891300936-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 3.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/7c/01/default-avatar-2020-5.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Jonathan H", "title": "Not the very best", "comment": "Stayed here within the first week of May 2023.  Pros:  Lication is close to various food places, money exchange and a grocery.  All the staff are very friendly, helpful and professional. Particularly Raquel at the front desk who answered all or questions and gave great directions to take the bus around the hotel zone.   Cons:  Sheets were stained and towels worn out.   The space under your door allows you to hear every sound out in the hall.   From the qypsum wall behind my head board I could hear clothes hangers shifting around in the room next door.   The bathtub does not have a shower curtain so shower carefully or else your floor gets wet.   The hotel generally requires some maintenance but the staff are hard working and try to make your stay comfortable.   Thanks again to", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r889237579-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 3.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh2_files/default-avatar-2020-49.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Jamie R", "title": "CANCUN, amazing trip worth taking!", "comment": "The urban oasis turned out to be an amazing stay. The hotel is very clean and the staff is very helpful. Raquel at the reception helped us with all of our requests, and we even got a complimentary all inclusive day pass to the Grand Palm Oasis.   At first the area appeared to be devoid of any activities but after exploring the area we could easily get a 10 minute bus journey to any of Cancuns hot spots including the beach resort for as little as a dollar. Overall our stay at the hotel was very relaxing. The pool area is perfect. We topped off our trip with a visit to Play Tortuga (1 dollar bus journey from outside hotel), and booked a day trip on a ferry to go to the famous Isle Mujures. This is a MUST visit! It is literally paradise. We got to snorkel along the Mexican ocean.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r888707084-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/06/default-avatar-2020-18.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Reanne R", "title": "The urban oasis- for a relaxing stay in Cancun", "comment": "We had an amazing stay at this hotel, staying in the junior suite which was definitely worth the extra money as the view of the pool side is beautgul,, it\u2019s been a week of relaxation. We took full advantage of the complimentary one day pass to the sister hotel grand palm oasis which was definitely worth it. The staff were friendly and all communal facilities were clean and well kept. I would definitely recommend this stay if you\u2019re looking for a chilled out stay whilst in Mexico. The pool area is beautiful at night with the lights and music. Raquel was so helpful during our stay and made sure we had the best experience. We will be back.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r888705755-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 1.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1d/c4/d8/e8/lumoo22.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Lucy M", "title": "Awful - avoid if you can", "comment": "I don\\'t normally leave bad reviews on here, but our stay here was terrible and I recommend avoiding a stay here.   - When we arrived to check in (just before 3.30, which was the earliest we could check in), they said they had no rooms. They were fully booked. Even though we\\'d already paid for a room.   - They offered to \\'upgrade\\' us to a sister hotel but we had to make the decision on where we would be taken there and then. We took a punt on the Grand Oasis Palm, which wasn\\'t adults-only, but we didn\\'t have much choice. There was no apology and the staff treated us like they were doing us a favour.   And while this isn\\'t a review of the Grand Oasis Palm, it\\'s worth mentioning that it wasn\\'t an upgrade. It is a vast hotel with kids everywhere, the rooms were old and tired and", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r888533350-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/2b/default-avatar-2020-52.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "stuckincarn", "title": "Nice place", "comment": "Nice, modern hotel in the town of Cancun. Within the grounds it does feel like you\\'re at the beach. It is indeed an oasis. The pool and surrounding area is nice to relax in.  This hotel has an excellent location if you are just staying for a night and want to be close to a grocery store and the ADO bus station.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r885251600-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/ef/default-avatar-2020-66.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "FabioEdi", "title": "Excellent experience, with a bit of room for improvement.", "comment": "My wife and I stayed at Oh by Oasis for 14 nights.  Pros: staff working inside the hotel, especially waiters and housekeepers, room was spacious and clean, location was excellent (right in the city centre, next to the entrance of the hotel zone), free secured car park, pool area.  Cons: toilet in the bedroom was tired as well as the gym, reception and concierge staff were not very friendly and quite robotics (one morning at 6.50 I approached a gentleman at reception, possibly the night manager, reporting that the water dispenser in the gym was not working as well the A/C so I asked him for a bottle of water, he laughed and told me that I should buy it in the caf\u00e9. So bad he lost an opportunity to recover the issue)  I cannot thank enough Jos\u00e9 and Emmanuel from the breakfast team", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r881703773-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 2.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/ce/default-avatar-2020-7.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Geeta S", "title": "Guest", "comment": "We stayed at the Oh Cancun! Urban Oasis, the service in the reception area and housekeeping is very disappointing, first of all, we were greeted by the most unfriendly staff upon check-in by the name Raquel Macuno.  She lacked understanding and compassion and never smiled.  This hotel made a mistake putting her in this position to greet people and check them in. We weren\\'t even offered a cold glass of water after a long journey of travelling here. If you need anything, do NOT go to Raquel.  The otherside; Oasis Smart, had better customer service and were able to answer our questions. We even arranged a day trip on the \"smart side\"  to the Tulum ruins, a cenote & Playa del Carmen, which was FANTASTIC.  Overall, their hotel rules and policies are inconsistent.  The hotel rooms are", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r881207792-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 2.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-o/14/1f/3e/6f/le-vicomte-n.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "le vicomte n", "title": "Oh! Cancun Oasis for a quiet, budget friendly escape", "comment": "The Oh! Cancun resort was a small urban hotel that had all the amenities for a relaxing and quiet week\\'s vacation. The hotel is centrally located, easy to get to from the airport. My room was large, with a balcony overlooking the pool area with a bathroom, a large TV and a clock-radio that has a port for iPhone. The king size bed was super comfortable and the balcony is large enough to have dinner on.  The best part of my stay was the staff. The front desk staff were amazing and helpful. Raquel and Aretzali started and ended my stay with kindness and informative answer to all my questions. (taxis, uber, restaurants, shops, etc.) The service staff were incredibly friendly and attentive and kind. Jorge, Ivan, Elizabeth, Juan, Jos\u00e9, Emmanuel, Kenia and Erik were all gracious,", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r881078711-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/06/ce/37/0c/maxmacdonald.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "MaxMacDonald", "title": "Not Good for Bad Leg", "comment": "We liked everything about this place except for dealing with the motor traffic on foot. Our suite was clean and cool, the staff were friendly and helpful and the pool area was quiet and spacious. We opted to include breakfast and especially enjoyed the selection of fresh fruit. There are tons of great taquerias nearby and lots of other restaurants as well. Our problem was getting to them. I have a bad knee and crossing the busy streets of fast traffic was really scary for us. If you are quick on your feet and want to stay in Centro, this is a great option.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r879473508-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/16/4d/d7/dc/caption.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "Mike M", "title": "I would definitely come back to the oasis!", "comment": "I would like to say that the staff was excellent, pool and gym were great, check in was smooth. My only negative was that the pillows were not that soft and the beds don\u2019t have a blanket, they have sheets but no blankets. The breakfast was awesome and was included with my reservation. It was definitely worth the price I paid under $90 a night. Thanks Oasis , will recommend and come back to stay.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r878843776-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "February 2023", "author": "Dianna W", "title": "I loved staying here!", "comment": " No complaints about the hotel at all. All the staff were very friendly and helpful. Spacious rooms, very clean. The pool was very good.  The breakfasts were excellent and the staff, Ivan, Kenia, Jose, Emanuel and Belen were very friendly and  gave very good service.  If I ever come back to Cancun, I would be sure to stay at this hotel. It is  very close to other places to eat and very close to the ADO station.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r878159296-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh3_files/default-avatar-2020-50.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "Ana", "title": "\ud83d\udc4d\ud83c\udffc", "comment": "Jose Luis was great and helped us out alot! He did maintainens at the hotel. He did an amazing job. The hotell was good, and I loves the pool as well. The outside area is the best part because there are sofas with a bar/restaurant and a big pool. When the weather is good, you can Really enjoy the poolside.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r878094734-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/b8/default-avatar-2020-26.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "Latifa", "title": "Very enjoyable stay", "comment": "Iv\u00e1n was very welcoming and kind. Kenia was always warm to me. The staff at this hotel made my experience wonderful. I was travelling solo and Raquel was very helpful regarding my questions about Cancun. I would come back here. It\\'s truly an Oasis in the middle of the city.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r877676742-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/e6/default-avatar-2020-45.jpg", "date_comment": null, "date_stayed": "February 2023", "author": "Hailey S", "title": "Best hotel ive ever stayed at!", "comment": "The hotel is wonderful. There isnt a ton of tourists and the staff are amazing. Very attentive and provide great service. Jos\u00e9, emanuel, eric, ivan, kenia are my favourites! They made the trip so memorable", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r877094257-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/43/default-avatar-2020-11.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "NorthStar41754590219", "title": "Very hotel. Very good service.of Aketzali", "comment": "Aketzali provides very good services. I like the hotel.  I will recommend the hotel to my friends.  Very convince location.  I went to the beach and enjoy sun, swimming.  i.like this hotel so much gracias", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r876673307-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/91/default-avatar-2020-62.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "mindjissoui m", "title": "Oh! Amazing Hotel Experience", "comment": "I spent two vacation periods at Oh Cancun. The first time, I have been amazed by the service, especially at the restaurant. I thought that it was an accident or the fruit of chance but when I came back for my honeymoon it was the same experience. The team working at the restaurant took care of us, always going the extra mile to ensure that we enjoy our stay, and giving us recommendations. Ivan was always checking if everything was ok and encouraged us to try new foods. The entire team at the restaurant worked very well together. I am thinking about Kenia, Jose, and Antonio, they are excellent professionals, they deserve a shoutout and I am taking this opportunity to say thank you for the quality of service.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r873933664-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-o/0e/77/cc/db/jason-n.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Jason N", "title": "Amazing Experience during my stay at this hotel!", "comment": "Oh Cancun is now my go-to hotel when I am in Cancun.  Thanks to the team working at the restaurant, I have the impression of eating at home. Thanks to Ivan, Kenia, Jose, Antonio, and the rest of the team, the menu provide enough variety for me to try local foods like tamales. They quickly learned my habits and they were able to provide top-notch service at the restaurant and also at the bar. They were so friendly that I remember their names. Looking forward to coming back for my next vacation.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r873932685-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/06/default-avatar-2020-18.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Joe M", "title": "Hotel Urban Oasis", "comment": "I had a wonderful stay at the Urban Oasis. The staff are very professional but also warm and friendly. The rooms are spacious. The pool area is superb and relaxing with palm trees and greenery. All staff are great. A special mention to Emanuel - best customer service. Others include Jose, Juan, Erick, Kenia, Sergio, Angel among others. The hotel is about 12 minutes drive to the Hotel Zone. Truly an urban Oasis.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r873889833-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/ea/default-avatar-2020-63.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Rhita H", "title": "Amazing, amazing, amazing!", "comment": "Amazing experience! The service was A1, from the beginning to the end. They are all really happy to serve you, and the hotel is super clean! It\u2019s really a quiet hotel, where you can relax, enjoy really good food, with music and sun! If you expect party, that\u2019s not the right hotel. The service was really outstanding and you won\u2019t regret it! 4 stars well deserved! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r872244934-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/2b/default-avatar-2020-52.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Reiner Schweizer", "title": "Oh Oasis", "comment": "Best service , very Clean , Emanuel, Angel, Belen,Jose, Kenia was very frendly and give me the best service. On the bar they prepare the drinks very well . They take care the guests. They explane me a lot .", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r872184996-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/de/5a/default-avatar-2020-36.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Anil P", "title": "Oh The Urban Hotel Oasis service", "comment": "The hotel is clean and tidy and the service from all staff is excellent. I would definitely come again. Special thanks to the following staff. Aketzali logo Concige, Kenya, Antonio, Emmanuel, Jose, Juan, Belen and Angel.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r871691912-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 1.0, '{"profile_picture": "./oh4_files/default-avatar-2020-35.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Alison K", "title": "Terrible service", "comment": "Not only did we book a room which they suddenly didn\u2019t have the booking for but we were told the hotel was fully booked. It was only after when we agreed that we would pay again, suddenly rooms are available. They said we would receive a refund for the second payment after 24hrs and it has been over two weeks and still no refund. They were unapologetic about their service and were not answering any of the questions that we asked. This was even when speaking to the manager.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r871569666-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 3.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/59/default-avatar-2020-24.jpg", "date_comment": null, "date_stayed": "December 2022", "author": "Charley J", "title": "Adults only hotel..but with kids?", "comment": "Overall not a bad hotel, all the staff are so lovely & a big Thankyou to Luis the handyman for fixing our window when it wouldn\u2019t close! The pool area is great with a great range of food and drink on offer all day. One thing I didn\u2019t understand which kind of really annoyed me is that this is described as an adults only hotel but every single time I\u2019ve been to the pool there were kids..I think maybe the two hotels are connected and one half is adults only and the other isn\u2019t but that doesn\u2019t help when you want to relax without kids splashing about in the pool and making noise when you specifically book a hotel because it\u2019s adults only..? ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r871310169-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 3.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-o/15/24/36/56/gabriela-p.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Gabriela P", "title": "Good place to stay", "comment": "It was awesome to stay at this hotel. Aketzsalli at main reception, and the rest of the staff was very informative and helpful, no matter question or request. My room was always clean and neat. Only minus was that my shower overflow every time I used it, didn\u2019t drowned though :)  Breakfast buffet was good and diverse everyday. Pastries could be more varied but it was good. Nice size pool. Location very good in Cancun centrum, close to everything and easy access to the beach by bus or taxi at a reasonable price. Overall a great stay, great hotel. Will definitely stay again here.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r870762557-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 2.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "venus310", "title": "Disappointed thought we were getting a 4 star hotel", "comment": "We went with Sunwing who rated this a 4 star hotel, it is a 2 star at best.  The rooms are dated and very small.  Our mattress was very old, cheap furniture in the room.  Our first breakfast we saw lunch meat that was starting to turn green.  It has curb appeal and nice lobby, but the rest is dated.  We were booked in for a week, we spent 3 nights there and took the hit for the other 4 and went and stayed at an all inclusive in the hotel zone.  If you only wish to be poolside and have no activities and just want to sleep there then this might be the place for you.  The staff is excellent and helpful.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r870567011-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 2.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Eve T", "title": "Vacaciones", "comment": "Filemon es el meillor . El hotel es bonito. La comida es buena. Amazing time in Cancun. I recommend. Playa is close. Staff is super nice. Pool setting is amazing. Thank you to everybody that crossed our trip! We will be back!!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r870142379-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Lydia S", "title": "Vacation", "comment": "Filemon at the pool bar became like family!  He knew our drinks \ud83c\udf79 by day 2 and was extremely pleasant. God bless him. Sergio and Eric were attentive and polite.   5 stars Filemon! This guy is the life of the pool area. Believe me when l say l missed him on his day off.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r870066231-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Ange Malpas", "title": "Amazing hotel, fantastic staff \ud83e\udd70", "comment": "Just got back from our trip. Absolutely amazing holiday. I cannot wait to return. Everything you need is in walking distance and door staff and taxis are there whenever you need. The place is so clean and staff are so so friendly. Always very helpful with big smiles on there places. Our room was always clean with fresh linen and towels an top ups on all we needed. Filimon, Ivan, Sargio, Jose, Antonio, Hugo, Emmanuel & Yari treated us so nice at breakfast and round the pool bar. Cocktails always made beautiful. We were very sad to leave. The staff definitely made our time here. We will be back early next year. Thank you so much guys. We love you. See you all in the new year \u2764\u2764\u2764\u2764", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r869779569-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "David A", "title": "Oh urban oasis cancun downtown", "comment": "Filemon Sergio Ivan Jose Antonio Emmaual Yani. Really looked after us. Made what ever drink we asked for with a smile on face. Always on hand when we needed them. We don\\'t speak Spanish but they understood our questions we were asking. Really great bunch we will go back to the hotel to see them again. Ps the lifeguard is a top bloke very helpful.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r869685177-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "C&K", "title": "10/10", "comment": "Check in was great and quick. The receptionist was very nice. We were given a room on the top floor facing the pool area which left us very suprised and delighted. Fair play to them for giving us such a nice room. Bryan later helped us with our bags and recommended a few places to visit. Many thanks to Bryan. Room was very big and spacious, kept to a very good standard. Nicosio was our housekeeper and he made sure we felt like home, always provided us with clean towels and perfectly made bed. Ivan at breakfast and pool was lovely. So friendly and polite. We are sorry to have missed him on our last day. Very attentive and hard worker. Jose during breakfast was very lovely and welcoming as well. They would both always recognise us and offer a friendly smile. Breakfast food is", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r869677391-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2022", "author": "Mo Po", "title": "High level of hospitalityGood serviceClean roomsDelicious breakfast", "comment": "We were here for 1 week as our first time in Cancun. Great location and close to park Palapas where you find lots of good food and dance and music of local vibes. The reception was fast and fluent. Ivan, Jose and Antonio at breakfast were very welcoming and friendly. We loved our stay at urban oasis and it\\'s for sure worth it.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868787245-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh5_files/caption(3).jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Ian M", "title": "Fantastic place,great staff.I'm coming again", "comment": "I\\'d like to write something separate for Raquel.She\\'s one of the best in hotel management .She has gone over the top with her professionalism.She was there for any help,questions or problems I had.Thank you again.Awesome hotel.Muchos Gracias.Ian from Toronto Canada", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868682603-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "./oh5_files/default-avatar-2020-50.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Kyu C", "title": "Nice hotel", "comment": "Good hotel,good location,good service and everything it\u2019s good, specially Raquel she help me with my pick up with Sunwing, I recommend this hotel too other persons, the tour it was good, the weather it\u2019s amazing it\u2019s not cool, there\u2019s stores very close walking, the supermarket is nearby, the convinces store very close, I recommend go to the parque de las papalas it\u2019s like 5 min, and also market 28 they have good exchange rate", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868575693-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/06/default-avatar-2020-18.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "H. H.", "title": "Enamored with Cancun", "comment": "One of the best parts of my trips was the service at the hotel. The staff has all been so self-sacrificing. This place really felt safe, and I am already looking forward to my return visit.   Raquel at concierge helped me book a trip and was very knowledgeable of the city. She is very kind and positive every time I spoke with her. Always willing to help and very highly competent in her activity without losing enthusiasm.   Andrea at front desk waited on a call for 30 minutes just to confirm a meeting. I am so grateful for her patience. She was able to advocate on my behalf.  Susanna helped with letting me know all the exciting excursions;  she can help book for you at the hotel. I highly recommend Chichen Itza; it was a highlight for me.  I did not expect to be enamored with", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r868575576-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/01/2e/70/9a/avatar026.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "jr1709", "title": "Good hotel, great service", "comment": "First of all, service was amazing. We had difficulty with our reservation, but Aketzali (receptionist) sorted it out with a lot of effort!  That was great!  Breakfast is okay. Sometimes better, sometimes worse.  Rooms are Standard. We had a room in first night in smart oasis and the others in oh the urban oasis. If you have a choice get urban oasis. It is a bit more new.   Music gets louder in the afternoon at the pool.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r867714979-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f3/23/default-avatar-2020-28.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "Breanne F", "title": "Vacation", "comment": "Service staff is very accommodating. Fi lemon hooked us up! Great value hotel. Breakfast buffet is awesome. Good central location if you\u2019re flying into Cancun and need a place to stay a night or two. Bueno!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r867504282-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 1.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/eb/6d/default-avatar-2020-19.jpg", "date_comment": null, "date_stayed": "November 2022", "author": "mekanoid", "title": "Underwhelming", "comment": "the check in process took 2 hours. I had booked and pre-paid online but it took them 2 hours to validate my booking while I sat waiting in the lobby until midnight when they finally were willing to give me a room key.   I left early the next day.   Definitely not recommended, it was very stressful and the only reason I booked was for a peaceful night prior to flying out which I didn\u2019t get.   The city view room view was of a wall.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r866917553-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ec/a2/default-avatar-2020-3.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Julie L", "title": "Vacation", "comment": "Very good service. Filemon was very funny, professionnal and gave us a very very good service! We had a very good night because of him! Thanks so much! We will return for sure eventually! Very good service!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r866348280-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e8/ea/default-avatar-2020-63.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Kevin B", "title": "Viaje en Cancun", "comment": "Sergio y Ivan y Philamon have been amazing. Defenetly enjoyed my stay. Would come back 100%. Great service and i enjoyed every moment here. Great place for familys and also a great place to work from home", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r866211821-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/9c/default-avatar-2020-14.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Vicky", "title": "Great facilities , even better staff", "comment": "The Staff at the Oh, make the easy pampered life you are going on holiday for. All have been helpful and friendly with a special mention to Antonio at the restaurant, who provided great service cheerfully and left me smiling after each interaction. His partners Sergio and Kenia help make a thriving high function team that benefits the patrons.  The hotel is spotlessly clean. Go to the Oh! You won\u2019t be disappointed", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r865919018-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/11/default-avatar-2020-23.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Keon L", "title": "Here for vacation", "comment": "Great service, highly recommend this place food,drinks great! Was serve by filemon also break was second to none Erick at the bar by the pool side, not for getting the chief eyint was great all round..", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r865716910-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "./oh6_files/default-avatar-2020-13.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "catclearh2o", "title": "Will definitely stay again", "comment": "No complaints! Staff was great especially my waiter Filemon!! Fantastic value for the price!! Close to everything, clean and beautiful pool area!!!!! Food was decent. Easy check and check out, parking is secure", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r865452608-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/42/default-avatar-2020-20.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Normand D", "title": "Days at oh oasis", "comment": "Bartender Filemon was great Nice pool area  Hot tub were nice The new regulations on non smoking were a bummer  Nobody at the bar Filemon always gave a great service which help with our stay Will come back to see Filemon He treated us as real customers", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r865084632-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/b8/default-avatar-2020-26.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Amy L", "title": "Vacation", "comment": "I came to the hotel to spend one day and I ended up staying for three and extending my flight stuff is so wonderful here it feels like home anything you need to get for you to grounds are perfectly cabs and it\u2019s very very clean the pool area is unbelievable I would recommend this place to anyone wants to come. Filemon is the first bartender I\u2019ve met in Cancun that knew how to make a lemon drop.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r865076347-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/20/default-avatar-2020-31.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Mack F", "title": "Good value for money", "comment": "Nice place rooms and hotel are to a good clean standard the bathroom in our room could have done with a freshen up but was still clean. The staff are friendly and helpful and facilities are all good. Breakfast is a bit random more like dinner but a good selection definitely a good place for the price", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r864799661-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/01/2e/70/79/avatar060.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Mary C", "title": "Vacation", "comment": "Filemon was an amazing waiter and Sergio was an awesome bartender. They treated us like royalty. Beautiful hotel with lovely pools and many private Jacuzzis. The rooms are large have everything you need. Including robes to use, none of the other 5 Star hotels included robes to use.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r864547619-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
