INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(10, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/de/e7/default-avatar-2020-37.jpg", "date_comment": null, "date_stayed": "October 2022", "author": "Gary D", "title": "Vacation", "comment": "Great location and fair prices. The service is excellent especially Filemomon and Sergio at the pool area. The rooms are comfortable and you never have to wait for hot water. The food at all areas whether the pool or the restaurants is always ver good", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d7229684-r864424668-Oh_Cancun_The_Urban_Oasis_Beach_Club-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
