INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/00/default-avatar-2020-4.jpg", "date_comment": null, "date_stayed": "November 2023", "author": "lenny g", "title": "LA ESTANCIA FUE AGRADABLE, MEJORAR LA LIMPIEZA DE LAS PISCINAS", "comment": "LAS INSTALACIONES BUENAS, MEJORAR UN POCO LA LIMPIEZA DE LAS PISICNAS Y PERMITIR LA MUISCA YA QUE HACE UN POCO MAS AMENA LA ESTANCUA AHI.  ADICIONAR OTROS RIPOS DE SNACKS Y EL PERSONAL DEBERIA SER UN POCO MAS ATENTO", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925305655-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "robert m", "title": "Excelente, pero un poco m\u00e1s de actividades en el d\u00eda no vendr\u00eda mal para la diversi\u00f3n.", "comment": "Excelente atenci\u00f3n, todo muy bien, el personal muy accesible, solo la comida en dos restaurantes me hizo da\u00f1o pero fuera de ah\u00ed todo bien. Lo que m\u00e1s me gusto fueron las vistas excelentes y aunque estuve en piso 10 estuvo genial. Gracias!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925305197-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "Climber672991", "title": "Restaurantes", "comment": "Tiene buena variedad de comida y restaurantes y una buena selecci\u00f3n de vinos con que acompa\u00f1ar. En cuesti\u00f3n de los ba\u00f1os las llaves si son un poco viejas y por lo tanto est\u00e1n un poco ya barridas y al momento de templar el agua hay que darle muchas vueltas o cuesta trabajo cerrarlas ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925291015-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "Yazmin J", "title": "Recomendado", "comment": "Incre\u00edble lugar para relajarse unos d\u00edas con excelentes opciones gastron\u00f3micas, una muy buena atenci\u00f3n por parte del personal, servicios exclusivos de alta calidad para los hu\u00e9spedes, as\u00ed como opciones de entretenimiento. Sin duda alguna un incre\u00edble lugar para pasar unas vacaciones.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925285486-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "NATALIA GUADALU... A", "title": "Experiencia de hospedaje en the sens Canc\u00fan", "comment": "Excente opci\u00f3n para descansar y pasar un rato agradable, the white the box riquisimo, solo en el buffet falto m\u00e1s variedad. De resto toda la estanc\u00eda estuvo muy bien habitaciones confortables y c\u00f3modas.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925285416-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "Ernesto R", "title": "Una visita agradable y buena atenci\u00f3n", "comment": "Fuimos de visita de inspecci\u00f3n del hotel en y tengo que reconocer que tiene mucha variedad en comida que fue lo que m\u00e1s me encant\u00f3, el hotel por cosas de ocupaci\u00f3n siempre se llena y las colas son inc\u00f3modas pero el servicio es muy bueno y aparte tienes acceso al Grand Oasis Palm y sus instalaciones as\u00ed como tour y actividades incluidas como el barco de noche que se reserva con concierge, es un hotel muy bueno yo dir\u00eda que lo \u00fanico que falta es un poco m\u00e1s de atenci\u00f3n al hotel ya que las habitaciones algunas se ven un poco viejas ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925283849-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 3.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "Trip46948089609", "title": "Aguas con el impuesto", "comment": "Buen ambiente, muy familiar, comida agradable, los restaurantes de especialidad s\u00faper bien, pero para reservar es una gran bronca, adem\u00e1s de la fila que se hace para el buffet, fuera de ah\u00ed creo que est\u00e1 aceptable  En general est\u00e1 bien y es recomendable. Pero su punto m\u00e1s malo es el Resort fee que cobran. Es un gran dinero gastado a lo tonto por un impuesto que se les ocurri\u00f3 cobrar as\u00ed de la nada. No se vale ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925282372-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 3.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "November 2023", "author": "Ruben G", "title": "Trabajo y placer van de la mano en Oasis Sens", "comment": "Hotel recomendado para todo tipo de viajero ya sea de trabajo por la cercan\u00eda a la ciudad o de placer ya que se encuentra en la zona hotelera con una excelente playa, las instalaciones y centros de consumo est\u00e1n de 10, en cuanto a las habitaciones, varias cuentan con una hermosa vista al mar Caribe. En conclusi\u00f3n super recomendado \ud83d\ude01", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925281262-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
