INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens3_files/default-avatar-2020-32.jpg", "date_comment": null, "date_stayed": "November 2023", "author": "Stephanie  C", "title": "UNA ESTANCIA AMENA", "comment": "Habitaciones limpias y camas confortables, fue mi lugar favorito en mi estancia pod\u00eda descansar s\u00faper bien Gracias a sus camas bien suaves. La comida poco variable pero deliciosa y las bebidas s\u00faper riqu\u00edsimas. Adem\u00e1s disfrute el c\u00f3mico show del paseo en el Barco Capit\u00e1n Hook.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925273920-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens3_files/default-avatar-2020-50.jpg", "date_comment": null, "date_stayed": "November 2023", "author": "sugey castillo", "title": "Es un hotel muy bonito y sus fiestas son los mejores sin duda alguno regreso", "comment": "Cambiar constantemente las s\u00e1banas del habitacion.  Cambiar las llaves de la regadera puesto que es muy complicado abrirlas ya est\u00e1n un poco deterioradas.  Todo excelente en cuanto a sus instalaciones ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925271688-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f3/7c/default-avatar-2020-29.jpg", "date_comment": null, "date_stayed": "November 2023", "author": "Manuel V", "title": "Comida y entretenimiento", "comment": "El restaurante de especialidad Careyes es deliciosa la comida, igualmente la comida del bufet tiene variedad. El show del barco pirata , es bastante entretenido y la zona de playa est\u00e1 bastante muy padre.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925268539-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/9f/default-avatar-2020-16.jpg", "date_comment": null, "date_stayed": "November 2023", "author": "Travel54850185764", "title": "The Sens Canc\u00fan", "comment": "Excelente hotel, comida deliciosa, habitaci\u00f3n amplia y limpia, los restaurantes de especialidad muy buenos, el \u00e1rea de solo adultos muy relajante. Si te hospedas en The Sens o Grand Oasis Palm tienes incluido la experiencia del Capit\u00e1n Hook, incluido bebidas y aperitivo.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925266666-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 4.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/b8/default-avatar-2020-22.jpg", "date_comment": null, "date_stayed": "November 2023", "author": "ciria o", "title": "Excelente, buen ambiente", "comment": "Habitaci\u00f3n linda, la atenci\u00f3n del personal excelente. Se deber\u00eda fe realizar Cambio de s\u00e1banas y fundas de almohadas. Ser\u00eda excelente que hubieran vestidores fuera del edificio del lobby cerca de piscina. Gracias por todo. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925245770-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 4.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/bb/default-avatar-2020-65.jpg", "date_comment": null, "date_stayed": "November 2023", "author": "Napoles E", "title": "Le petite chef", "comment": "Incre\u00edble la experiencia en el petite chef en un video en tercera dimensi\u00f3n donde un chef cito nos preparo nuestra cena, definitivamente la mejor cena que he probado, muy padres mis vacaciones en el grand oasis palm   Atendidos por Francisco Sambrana Alejandro Bartolomeo ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r925091717-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ea/2e/default-avatar-2020-67.jpg", "date_comment": null, "date_stayed": "October 2023", "author": "Elizabeth B", "title": "Excelente experiencia y muy buena comida \ud83d\udc4c le petit chef oasis hotel", "comment": "Le petit chef muy buena experiencia , comida muuy rica y excelente atenci\u00f3n  \ud83d\udc4c  100% recomendable.   Bartolome, francisco y victor! Gracias !! Por todo su acompa\u00f1amiento y dedicaci\u00f3n.    Muy agradable lugar.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r920113966-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 1.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f3/7c/default-avatar-2020-29.jpg", "date_comment": null, "date_stayed": "September 2023", "author": "Gabriela V", "title": "Pesima", "comment": "La calidad del tequila y de la cerveza es muy baja. Se supone que es la mejor categor\u00eda   de esa \u00e1rea   quitaron las camas  que estaban elrededor de la alberca  ahora las cobran . Por eso pagamos un todo incluido para no estar pagando . Solo hay 8 palapas  son insuficientes  la gente se queda dormida en las sillas prefieren tener las camas  solas .si reservas y no llegas a tu reserva te cobran  y eso est\u00e1 mal. Hize 2 reportes y no hubo ning\u00fan seguimiento . Y por \u00faltimo al salir no encontraban el pago y tuve que firmar un baycher por la misma cantidad de mi reserva. Porque mi CEL se quem\u00f3 y no pude abrir mis correos a fuerzas quer\u00edan el comprobante . Fue muy estresante . Si no vuelvo a firmar no nos dejan salir. No es posible que pase eso despu\u00e9s de 7 d\u00edas decirte que no", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r919974229-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/32/default-avatar-2020-12.jpg", "date_comment": null, "date_stayed": "September 2023", "author": "Laura G", "title": "Maravillosa estadia en familia y amigos!!!", "comment": "Fuimos en familia con mi marido y mi nena de 11 a\u00f1os y con otro matrimonio y su hijo.  Pasamos una estadia genial. La playa tiene sus quinchos de paja, conviene bajar a la playa temprano para tener sombra. Hay unos camastros  que son pagos pero si disfrutan de algunos servicios de spa se puede negociar y disfrutar de la tranquilidad. Los desayunos en buffet tienen variedad.  En White el desayuno es mas gourmet y delicioso (solo apto para adultos). Lo aconsejable es reservar los restaurantes para las cenas si no quieren ir al buffet.    The White box un 10 en comida, felicitaciones para el chef!! (de noche se puede ir con ni\u00f1os), Cocoa rica comida tanto al mediodia como a la noche y quiero agradecer especialmente al MOZO NACHO siempre nos atendio con su sonrisa y su excelente", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r918698769-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/99/default-avatar-2020-57.jpg", "date_comment": null, "date_stayed": "September 2023", "author": "Alessandro A", "title": "Me encanto la experiencia y la atenci\u00f3n", "comment": "Fue una bien experiencia.. y buena atenci\u00f3n, lo m\u00e1ximo fue la cena de le petit chef. Fue algo \u00fanico y agradable. Muy buena atenci\u00f3n de Juan, Bartolome, Alejandro y Francisco. Muy buena recomendaci\u00f3n para una familia. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r916614135-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens4_files/default-avatar-2020-46.jpg", "date_comment": null, "date_stayed": "September 2023", "author": "Laura C", "title": "Un d\u00eda relajado", "comment": "El ambiente familiar me agrado, es un hotel que no necesita mucho, un agradecimiento a claudia que estuvo con nosotras en las camas balinesas haci\u00e9ndose cargo que nada nos faltara, y que siempre tuviera algo que tomar y sus recomendaciones. y a omar ulloa de concierge por la reserva en el restaurante que nos recomendo y estar al pendiente de nuestra estad\u00eda. La verdad si lo recomendar\u00eda, me la pase super relajada y tranquila. Sin duda. No necesita mucho para estar tranquila. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r916031146-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/2a/4d/6e/e1/caption.jpg", "date_comment": null, "date_stayed": "September 2023", "author": "ESTEFANIA TORRES", "title": "En familia y relax", "comment": "La verdad un lugar acogedor y super tranquilo no necesitas realmente mucho para pas\u00e1rtela relax y en familia, mil gracias a claudia en camas balinesas por habernos atendido de lujo porque no hay palabra para decir m\u00e1s. Al caballero omar Ulloa de concieger por los consejos y estar al pendiente de mi estad\u00eda. Aun qu\u00e9 s\u00f3lo en la cena hubo un detalle que no me gust\u00f3 y fue que mancharon la ropa de mi bebe en cloro pero de ah\u00ed en fuera si lo recomiendo mucho! Gracias por todo!!!! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r915942498-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 1.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/7b/default-avatar-2020-6.jpg", "date_comment": null, "date_stayed": "September 2023", "author": "Marcela O", "title": "No para familias", "comment": "Horrible experiencia,este hotel no est\u00e1 pensado para ir con ni\u00f1os,es una estafa. Tambi\u00e9n mienten y te venden que cuentan con muchas opciones de restaurantes y la realidad es que se apuntan los restaurantes del hotel hermano que tienen a lado , de los cuales solo un par aceptan ni\u00f1os y solo puedes ir con reservaci\u00f3n pero que no se te ocurra faltar porque te cobran penalizaci\u00f3n !  As\u00ed que la realidad de este triste hotel es que solo tiene un restaurante buffet con un horario muy corto,que si tu ni\u00f1o se durmi\u00f3  ya no va a alcanzar a comer. Y para cereza del pastel el personal tiene p\u00e9sima actitud de servicio, nadie te puede resolver nada, nunca me hab\u00eda tocado que en general todo el personal del hotel estuviera mal pero lamentablemente as\u00ed fue. Y por \u00faltimo  las instalaciones,", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r915759446-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ea/b2/default-avatar-2020-69.jpg", "date_comment": null, "date_stayed": "September 2023", "author": "Leticia S", "title": "Excelentes vacaciones", "comment": "Nuestra estancia ha sido muy placentera, el personal amable e instalaciones limpias y seguras. Lo mejor de nuestro viaje fue la grata experiencia de conocer Le Petit Chef, un concepto muy europeo que aqu\u00ed en Oasis lo tienen, gracias a Juan, Bartolom\u00e9, Alejandro y Francisco quienes nos atendieron y se lucieron!! \u263a\ufe0f\ud83d\udc4d. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r914523817-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/2d/default-avatar-2020-48.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "nini valentina g", "title": "Vacaciones", "comment": "La atenci\u00f3n del hotel y todos sus colabores fue excelente durante mi estancia, la experiencia, amabilidad y concejos de cada uno fue s\u00faper bien, muchas gracias hotel they sens por cada una de sus atenciones.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r913685527-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/d5/default-avatar-2020-17.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Brenda R", "title": "Vacaciones en familia", "comment": "La mejor experiencia al finalizar nuestras vacaciones el restaurante Le Petit Chef vale la pena cada peso que pagas por esta experiencia la atenci\u00f3n de primera, gracias a Juan, Bartolome, Alejandro y Francisco hacen que esta experiencia sea formidable, muchas felicidades por este nuevo concepto!!!!!!  ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r913147906-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/59/default-avatar-2020-49.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Liliana Francis... O", "title": "Buen\u00edsima experiencia", "comment": "Maravillosas vacaciones, la mejor experiencia vivida en el petit chef, un peque\u00f1o chef nos cocino 4 platos y lo que m\u00e1s nos encanto a mis ni\u00f1as y nunca hab\u00edan probado fue la sopa de mariscos. Todos los platos una exquisites Muy agradecidos. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r912532521-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 3.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/df/2b/default-avatar-2020-39.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Diego Damian G", "title": "Inconforme con el servicio", "comment": "Todas kas comidas de todos los restaurantes como los snack sin sabor, y adem\u00e1s el negarme comida cuando se le pidi\u00f3 carne solo me dio un par de trozos de carne cuando se le pidi\u00f3 m\u00e1s de mal genio me dio 1 trozo muy peque\u00f1o de carne lo mismo le paso a mi esposa no en todos los lugares tienen todo tipo de servesa, otro punto le ped\u00ed apoyo a una personal del hotel en cargar mi celular d\u00e1ndole yo el cable para cargar explicandole que no funciona todos y los que si funcionan estaban ocupados me neg\u00f3 ayudarme   Otro punto para que sea m\u00e1s atractivo el afiliarse a ser socio deber\u00eda tener un poco m\u00e1s de privilegios como el no hacer filas en los restaurantes y en los bares por mencionar algunos de los servicios ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r912474990-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 3.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f3/e4/default-avatar-2020-30.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Luis leon", "title": "Tercera visita !!", "comment": "Excelente !!!  El check in fue excelente , nos explicaron a detalle los servicios , Gaby de Cocoa fue muy amable y profesional , Ra\u00fal segura excelente atenci\u00f3n ! Nos encanto que est\u00e1bamos en el Sens y pod\u00edamos ir al oasis Palm , el kids club muy amables las chicas , nuestra habitaci\u00f3n perfecta !! Sugerencia en caf\u00e9 de mar Solo falta un men\u00fa de ni\u00f1os ! El puente q cruza la alberca 10 !! Ya lo remodelaron , esta vez ya no me ca\u00ed \ud83d\ude4a. Gracias por hacer nuestra peque\u00f1a estancia inolvidable!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r912192771-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/60/default-avatar-2020-64.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Cesar Omar G", "title": "La raz\u00f3n por la cual deber\u00edas venir a este hotel de lujo y una de las experiencias que nos dejar\u00e1n un antes y un despu\u00e9s", "comment": "Exelente el hotel sens exelentes habitaciones y personal muchas cosas que resaltar en este hotel muy buenos restaurantes y en especial nuestro favorito Le Petit Chef por mucho y variado que a m\u00ed y a mi familia nos encant\u00f3 en todos los sentidos posibles en una animaci\u00f3n de 3 dimensiones exelente atenci\u00f3n por parte de  Juan Bartolome Francisco Alejandro Y C\u00e9sar todos ellos me dieron una experiencia inolvidable es encantador en definitiva el mejor por mucho su comida gourmet nos da una raz\u00f3n m\u00e1s para seguir viniendo ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r911360637-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens5_files/default-avatar-2020-68.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Elizabeth L", "title": "Restaurante Le petit chef dentro de Grand Oasis Palm y The Sens Cancun", "comment": "Restaurante Le petit chef uno de los restaurantes que no puedes dejar de visitar, su comida es excelente pero sobre todo la experiencia de vivir la fusi\u00f3n entre lo culinario y artes visuales lo hacen \u00fanico. Y los anfitriones hacen que tu estancia sea muy amena. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r911102409-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/9f/default-avatar-2020-16.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "\u1d0b\u026a\u1d05\u1d0f\u0280\u028f \u10e6", "title": "Celebrando Cumplea\u00f1os en Le Petit Chef", "comment": "Estamos en el Grand Oasis Palm disfrutando mis vacaciones, me ha parecido muy bien. Tenemos la nueva membres\u00eda y estamos muy a gusto y nos parece un lugar muy familiar donde nos trata bien, y tambi\u00e9n nuestra experiencia en Le petit Chef fue una experiencia inolvidable, fue el mejor, le doy un 10!! En Le petit Chef fuimos atendidos por, Juan, bariolone, Francisco, alejando, y el Gran chef cesar!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r909189334-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f3/23/default-avatar-2020-28.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Naty V", "title": "Experiencia unica le petit  Cheff", "comment": "Disfrutando las instalaciones pero sobretodo la experiencia incomparable con Le Petit Chef y el STaff a cargo, se llevan todas las preseas cumpliendo todas las expectativas en sabor y atenci\u00f3n por Staff juan, Bartolome, Francisco, Alejandro y Cesar, el mejor equipo", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r908660312-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/a9/default-avatar-2020-50.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Cecy M", "title": "Excelente trato e instalaciones", "comment": "Sens Canc\u00fan, la mejor experiencia que pude tener en mi viaje con familia, el servicio las comodidades, y el trato s\u00faper amable. La comida deliciosa, gracias a la srita Keyla rosales que siempre est\u00e1 muy pendiente y tiene un trato amable para con todos. El pr\u00f3ximo a\u00f1o regreso sin dudarlo", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r908135258-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/ef/default-avatar-2020-66.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Karla M", "title": "Excelente hotel en Canc\u00fan", "comment": "\u00bfBuscas hermosas playas, comida deliciosa, servicio de excelencia? La respuesta es the sens cancun.  Servicio al hu\u00e9sped,  Keyla Rosales es la excelencia hecha persona. Don Eduardo P\u00e9rez, Director, un caballero pero m\u00e1s que todo un l\u00edder que gu\u00eda a su gran equipo con el ejemplo.   Agradezco a todo el personal de The Sens Canc\u00fan el haber hecho de mis vacaciones momentos para recordar.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r908126602-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens5_files/default-avatar-2020-47.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Ezekiel V", "title": "Excelente Le petit Chef", "comment": "La atenci\u00f3n del personal del Hotel es excelente. La playa muy limpia al igual que las albercas. Los ni\u00f1os se la pasaron bien. Todo el tiempo estuvieron entretenidos y bien atendidos. El hotel esta muy seguro y limpio. Hay muchas actividades cercanas. Tuvimos la oportunidad de cenar en Le petit chef, un restaurante de excelente servicio con comida de excelente calidad con la technologia 3D que entretiene mientras esperamos la comida. Es una experiencia que le recomiendo a toda la familia. No se arrepentir\u00e1n de conocer al equipo de Le Petit Chef Juan, Bartolom\u00e9, Francisco, Alejandro y Cesar. Son personas muy profesionales, amables y amigables.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r907890178-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 1.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/08/eb/cd/01/schroeder17.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Schroeder17", "title": "INTOXICACI\u00d3N Y MENTIRAS UNA EXPERIENCIA INFERNAL", "comment": "sin duda una experiencia horrible de mentiras, comida de segunda e intoxicaci\u00f3n. la peor experiencia en a\u00f1os de viaje, te mienten en todo, desde la llegada vez la pobre calidad cuando te venden un hotel del lujo, NO ES ADULTS ONLY, ES MENTIRA! a pesar de tener la supuesta mejor categor\u00eda y habitaci\u00f3n ( sian kaan ) es pat\u00e9tico que te hagan hacer fila para las reservas, de que sirve la app y los concierge???? es una tonter\u00eda pagar para hacer fila y que te digan que est\u00e1n llenos cuando pasas y las \u00e1reas est\u00e1n a la mitad de su capacidad, LUJO ??? SIN ROOM SERVICE?? PAT\u00c9TICO EL ENGA\u00d1O, siguen y siguen las quejas todo el personal lo sabe y te dice que la gente se queja de todo porque los enga\u00f1an, esto es una trampa horrible; me intoxicaron en el restaurante CAREYES, DIZQUE FRANCES Y", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r907709991-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/ce/default-avatar-2020-7.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "veromassara", "title": "Excelente hotel all inclusive", "comment": "Un hotel para ir en familia o en pareja. Cuenta con muy buena atenci\u00f3n y sobre todo muy buena ubicaci\u00f3n ya que se encuentra a unos metros del muelle para tomar el ferry hacia Isla Mujeres. Tambi\u00e9n destac\u00f3 su gastronom\u00eda y tragos, contaban con mucha variedad y buena calidad. Restaurants 5 estrellas como Careyes, Sakura, Agaves, Cafe Del Mar entre otros . La atenci\u00f3n de todo el personal es excelente. Adem\u00e1s, la habitaci\u00f3n es c\u00f3moda y limpia. Agradezco a nuestra anfitriona, Valentina Beigun, por su hospitalidad y generosidad.  Sin dudas es un hotel el cual recomiendo y volver\u00eda! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r907460868-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/59/default-avatar-2020-24.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "David Fernando A", "title": "Le petit chef", "comment": "Muy buena experiencia 10/10. Vine con mi familia y es una experiencia muy rica y divertida. Tienen din\u00e1mica de fotos para la familia y la calidad de la comida muy buena, recomiendo la langosta y el tomahawk. Recib\u00ed atenci\u00f3n estupenda del equipo de meseros la atenci\u00f3n de Juan, Bartolom\u00e9 ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r907300025-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/bb/default-avatar-2020-65.jpg", "date_comment": null, "date_stayed": "August 2023", "author": "Garcia Arris S", "title": "Una experiencia incre\u00edble!", "comment": "El hotel fue muy agradable. Tienen una gran variedad de piscinas muy limpias y lindas. El hotel est\u00e1 frente al mar lo que hace que sea una experiencia bella cada que despiertas. En cuanto a los restaurantes, el mejor sin duda Le petite Chef, fue incre\u00edble, rica comida, los videos geniales, todo extraordinario.recomendado. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r907299241-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens6_files/default-avatar-2020-11.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Cesarr g", "title": "En pareja\ud83d\ude0e\ud83c\udf7bo en familia\u26bd\ud83c\udf8a super \u2b50", "comment": "Es la segunda vez que visitamos est\u00e9 hotel, en feb y julio, nos pareci\u00f3 muy bueno por el costo de la noche. Nos encant\u00f3 qu\u00e9 nos dieron una habitaci\u00f3n con  super vistas, limpio y muy padre la cama, los buffete la comida vale mucho la pena, la atenci\u00f3n en las barras de la alberca \ud83d\udc4cel buen alex \ud83d\ude0e, Jos\u00e9 y Miguel del buffet \ud83d\udc4f. Y sobre todo en recepci\u00f3n el buen luis. Con gusto regresaremos, en familia o en pareja lo recomiendo mucho ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r907215686-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 1.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ec/3d/default-avatar-2020-1.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "sandy c", "title": "NO REGRESO NUNCA", "comment": "Mi peor estancia, nunca regresar\u00eda !  Empezando por que recibimos el cuarto a las 5 pm !!!! Y aun as\u00ed se nos pide hacer check out a las 12:00. El personal de la recepci\u00f3n no tienen buena disposici\u00f3n ni es de su inter\u00e9s que el hu\u00e9sped est\u00e9 contento en el hotel.  Los cuartos est\u00e1n chicos y viejos, lo que hace que se vean sucio. M\u00e1s de una hora  de espera para entrar al buffet, nunca hay elevadores disponibles, est\u00e1n muy chicos para la cantidad de hu\u00e9spedes qu\u00e9 hay en el hotel. La alberca llena a explotar, no hay suficientes palapas para la playa y las qu\u00e9 hay cuestan $1200 (pesos mexicanos) por persona.  Lo \u00fanico rescatable del hotel es que la comida est\u00e1 bien (aunque no hay mucho de donde escoger), los meseros y las chicas que limpian el cuarto son muy amables.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r906721636-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/60/default-avatar-2020-64.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Isaac B", "title": "Me encant\u00f3 el hotel oasis!!!", "comment": "Nos encanta el oasis sens Canc\u00fan por qu\u00e9 nos tratan como familia!! El cuarto est\u00e1 muy espacioso y la vista es incre\u00edble tanto hacia el mar como hacia la laguna los restaurantes tienen gran variedad de comida en los buffets y los que son a la carta de reservaci\u00f3n est\u00e1n deliciosos. Las albercas  est\u00e1n padr\u00edsimas y la playa es genial y el trato del personal es muy amable y confiable. Vayan y disfruten", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r906603513-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/b8/default-avatar-2020-22.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Julieta Guadalu... D", "title": "Una gran experiencia", "comment": "Hasta el momento es la mejor experiencia que hemos adquirido como familia, sin duda lo recomiendo mucho para todo tipo de familias. En el restaurante Le Petit Chef es una experiencia inigualable que vivimos en 3D evento privado, la comida es exquisita pero sobre todo la atenci\u00f3n magnifica por parte del servicio de Bartolome principalmente sin dejar atr\u00e1s a Francisco, Alejandro, gerente Juan y al gran chef Juan repito de Le Petit Chef. Como tal la experiencia TOTAL del hotel es excelente sin duda cumplieron mis expectativas y mas. Regresamos en enero para celebrar nuestro cumplea\u00f1os \u263a\ufe0f", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r906349369-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e9/ef/default-avatar-2020-66.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Silvia H", "title": "Vacaciones.    Excelente Comida a la carta.", "comment": "The White Box y Alma son de lo mejor para comer bien . Excelente servicio, ambiente y comida. Felicitaciones a la chef Sandra Silva y a nuestro mesero estrella V\u00edctor. De lo mejor del lugar!  Lo malo es que no hay suficientes camastros ni sombrillas. Falta de letreros con recordatorios sobre lis reglamentos en las \u00e1reas comunes. Tambi\u00e9n recomiendo que haya m\u00e1s supervisi\u00f3n en los ba\u00f1os ya que invariablemente o no ten\u00edan papel o jab\u00f3n de ba\u00f1o ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r906279613-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 1.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "yessica S", "title": "Nada Recomendable. Por el mismo costo hay mejores opciones. Lo barato sale caro.", "comment": "\u00a1Nada Recomendable!. Descuidada el \u00e1rea de ni\u00f1os. P\u00e9simo sistema de reservaci\u00f3n de restaurantes. Jam\u00e1s tienen toallas. Lo \u00fanico rescatable es la amabilidad de los meseros. Ojal\u00e1 mejoren sus procesos. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r905860205-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 1.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "roman martinez", "title": "Gerente recepci\u00f3n poco profecional", "comment": "Gerente de recepci\u00f3n poco profecional , me presente en el hotel buscando reserva para 15 Persona seg\u00fan la persona  de nombre Gerardo Duran , le ped\u00ed informaci\u00f3n para completar mi pedido , ver las \u00e1reas del hotel respondiendo el mediocre que buscara en Google Maps las im\u00e1genes esa persona con sus caracter\u00edsticas muy particulares y poco profecional  No cumple con los est\u00e1ndares de calidad cadena OASIS menos para tratar directamente con clinete", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r905113350-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 1.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "Nicol\u00e1s N", "title": "Muy buena experiencia", "comment": "Excelente hotel. Muy limpio. Las instalaciones buen\u00edsimas. La comida y bebida muy buenas tambi\u00e9n. La atenci\u00f3n de los empleados es excelente, muy amables y trabajadores. Check in y check out s\u00faper r\u00e1pido y muy amables. Ivonne a la salida nos atendi\u00f3 muy bien. Recomendable!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r904545837-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "Eva C", "title": "Mejor lugar para vacacionar", "comment": "Todo excelente. Lugar muy limpio, confortable, bello. El mejor lugar para vacacionar. Quedamos muy contentos con el restaurante Le Petit Chef, un concepto diferente pero hermoso, el personal muy atento y amable, gracias a Juan , Bartolome, Francisco, Alejandro y el chef Cesar recomendadisimo.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r904442758-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 2.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "July 2023", "author": "FATIMA H", "title": "Decepci\u00f3n", "comment": "Admito que el precio fue muuuuy barato, pero en dicho pecado llevamos la penitencia!!!! Resulta que es un \"todo incluido enga\u00f1oso\" con tramites vacacionales engorrosos y restricciones varias. Puntos a favor que me hacer darle dos puntos: 1) la playa limpia y agradable con una peque\u00f1a zona azul claro donde puedes nadar, c\u00f3modas amacas para descansar en la playa si te gusta el sol, comida adecuada (no lujosa ni excelente pero rica en general), habitaciones c\u00f3modas. Puntos negativos 1. tienes que formarte para reservar la comida y cena a partir de las 4pm con cupo no limitado, sino super limitado, no incluye bebidas alcoh\u00f3licas como tequila, ron u otro destilado a c\u00f3ctel para acompa\u00f1ar alimentos, solo refrescos, cerveza y vino con limitaci\u00f3n; es decir, pides un copa y si quieres", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r903988466-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 2.0}', 'es');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens1_files/default-avatar-2020-23.jpg", "date_comment": null, "date_stayed": "November 2023", "author": "Stefan S", "title": "As always - we loved it !!!", "comment": "We had a group of 8 friends visiting this resort \u2013 again !!!! This is almost our home from home. Booked Sian Kaan and the Beach Club and its setting is super nice. Adults only in this section with your own bar and pool. WoW!!! Our rooms were located close to the beach and just a few steps away from our Balinese Beds. The rooms in this section are really nice, bright, clean. We loved the bed linens and the daily service. Breakfast at the White Box was great every morning and the wait staff there could not be more accommodating as well for lunch. The seafood casserole with the spicy tomato sauce there is the winner. We had dinner at Careyes, Miramar and Cocoa. Every time the food was fresh and tasty. Kudos to Eladio at Cocoa, Irene and Manuel at the Beach Club and of course", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r927515457-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-o/0e/29/b2/65/greg-d.jpg", "date_comment": null, "date_stayed": "January 2023", "author": "Greg D", "title": "Tue stars of customer service!!!", "comment": "We went there with 3 young kids for 12 days and just returned. We were a bit skeptical given price (real great deal) and mixed bag of reviews. I am glad to say this vacation exceded all expectations and we had a fantastic time. And it is all due to the wonderful staff at the resort. The service is simply incredible!!!  Room: We are 5 with 3 kids and the only option at booking was a grand room standard. We were doubtful about fitting all of us in this room and asked for an upgrade but there was no availability whatsoever. The room is very clean and the mattresses are good. The futon bed is OK for a young child but you should ask for a roll away bed which is a single mattress and it\\'s much better. Room was made every day and mini fridge was restocked every other day with water", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r874960865-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e3/1f/default-avatar-2020-46.jpg", "date_comment": null, "date_stayed": "October 2023", "author": "Fearless49630538672", "title": "Le Petit Chef at the cancun", "comment": "The Petite Chef was a unique dining and entertaining experience with Chef Cesar, Juan Carlos-Mgr, Francisco, Alexandro, Bartolome. Professionalism at its finest. We will come again. I highly recommend this restaurant. Perfect for a romantic dinner and private!!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r921942366-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/de/5a/default-avatar-2020-36.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Camper58863464772", "title": "Great family hotel", "comment": "The Sens was the best experience we had as family so far,  this is our second stay as a family and had a blast. Le petit Chef was a amazing  family experience \u2764\ufe0f.  We loved it . The best chef and amazing dining experience. A big shout to Bartolome, Francisco, Alejandro, Juan, and Chef Cesar. Thank you all for a great evening and we recommend everyone to visit this hotel and restaurant ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r907112248-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/06/default-avatar-2020-18.jpg", "date_comment": null, "date_stayed": "July 2023", "author": "Edgar R", "title": "Le petit chef an experience you can\u2019t miss!", "comment": "Perfect place for family and friends. They have multiple pools and restaurants. We had a great and unique experience at LE PETIT CHEF! restaurant, the food was delicious and their concept is something that you can\u2019t miss!! Juan, Bartolom\u00e9, Francisco Alejandro & Cesar when out off their way to make us feel welcome and special. Great staff!!! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r904255549-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/b8/default-avatar-2020-22.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Lynn Worden", "title": "Le Petite Chef", "comment": "The Sens offers incredible  options for dining but the Le Petite Chef is an experience a on its own. It\\'s worth the upcharge because it is an amazing one of a kind dining showcase. We were outstandingly  serviced by  5 incredible waiters named Juan,Alejandro, Barrolome,Francisco and Cesar, who made us feel pampered while eating some of the most delicious food we\\'ve experienced in a long time.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r899021697-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f1/b8/default-avatar-2020-22.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Carlos L", "title": "Ill be back", "comment": "Great restaurant. Our waiter, Alejandro Santiago was amazing. He explained the menu and picked a perfect 5 course dinner combination for us. I\u2019ll definitely be back asap.  There were 4 shows throughout our dinner experience and each one was very entertaining.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r894930772-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/2b/default-avatar-2020-52.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Brenda G", "title": "Server Oscar G at La Hacienda Food Hall was very attentive and friendly", "comment": "Our stay was at Oasis/ Sens was great! They had fun daily activities throughout  the day and night, canoeing, soccer in the beach, dance lessons we went on jet skis and much more! \u2026We couldn\u2019t miss the Michael Jackson show, it was amazing! The food at the buffets and restaurants was delicious . Oscar G our server at La Hacienda Food Hall was very attentive, and friendly . We had sushi and burgers while we were at the pool area and unlimited alcoholic beverages . The staff is very attentive,  they make sure that you are enjoying your stay at all times and have a great experience . We will definitely be back and recommend this place to everyone!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r894831154-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f4/20/default-avatar-2020-31.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Mychael D", "title": "Will absolutely be back again", "comment": "This was a fantastic experience for me. My waiter Marco Victor Fidel at the Sens Canc\u00fan Careyes gave me impeccable service. I will definitely be back again. I moved this place. Had a fantastic experience and I cannot wait to see what else the Sens Canc\u00fan has to offer. They also had no problem contributing to the all-inclusive offer like some other places do. I definitely cannot wait to recommend this place to other people as well. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r894737867-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens1_files/default-avatar-2020-47.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "paige b", "title": "Wonderful resort and staff", "comment": "We are having a blast! Definitely best vacation! Karina Sanchez was our waiter and she was amazing at her job! Great place for family and super safe to bring kids as well! We will definitely be back. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r894545172-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens2_files/default-avatar-2020-42.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Heather W", "title": "Le Petit Chef was a 10/10", "comment": "We had dinner at Le Petit Chef during our visit to the Sens Hotel and it was incredible! The experience was one of a kind. Juan, Alejandro, Francisco, Cesar, and Bartolome were fantastic hosts. Cesar was a great chef. Alejandro made me a perfect espresso martini. Juan and Francisco served us our food amazingly. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r894086800-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/ce/default-avatar-2020-7.jpg", "date_comment": null, "date_stayed": "June 2023", "author": "Alexandre G", "title": "Vacations", "comment": "Karla e Manuel Mendez provide at sensoria an amazing service. We had a good ti E with tem m, also they made a lot of difference. Thsnks for the Chicken Burguer. The best experience in the whole restaurant. Thanks guys", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r894068208-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "McKenzie L", "title": "Great service", "comment": "The staff is absolutely wonderful! They are so friendly and personable. We will be coming back! Jesus is a great waiter and Francisco is an amazing chef. All the complements to them! They are amassing! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r893628241-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Emily C", "title": "Summer family vacation", "comment": "We stayed on the balance beds during our stay and our waitress Karina did a great job and always made sure we were taken care of us. The service was great and the pools were very clean. We enjoyed our Stay very much.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r893447530-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Tatiana B", "title": "Wonderful family experience", "comment": "It was a wonderful family experience! The staff very friendly. The hotel and rooms very clean and comfortable. The restaurant Caf\u00e9 del Mar was delicious and right on the beach. Excellent food and service! Waiters very friendly and warm! Isai and Abimael very attentive The chef Marmolejo is wonderful.  The coffee place Coffee & Me great as well with a very warm and attentive staff. Delicious coffee! Highly recommend the hotel and the restaurants!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r893257230-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Thomas K", "title": "Great resort for dining options", "comment": "We enjoyed staying in the hotel. The restaurant variety was good and it was all-inclusive.  One could pick a la carte or buffett style meals anytime.  The best dining experience we had at a restaurant was at The White Box gastrobar were Carlos took excellent care of us.  This is not an optimal vacation experience for adults only as there seems to be a lot of loud kids around, however it was still a great experience.  We would highly recommend this place as an anchor for your vacation in Mexico.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r893234517-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "June 2023", "author": "Wallasson D", "title": "Wonderful, perfect, friendly \u2764\ufe0f", "comment": "We had a Wonderful dinner at cafe Del Mar . Cena restaurant whit a variety of tasty, we love ever single plate, the waiters Julio R, Luiz,J\u00falio B and cheff Marmolejo was very friendly and treated us like a very special person. We really appreciate everything  ,thank you for make our night so special. \u2764\ufe0f", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r893163987-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Pui S", "title": "Must dine", "comment": "Dined in the Sukura restaurant where we were entertained by multiple shows and the food was fantastic, presentation was excellent and the food was delicious. Special mention to Alejandro Santiago, Jesus Lazaro & Capitan Evelyn for being gracious hosts and making our dining experience memorable.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r892665961-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Roberto Edmunds", "title": "Highly recommended at The Sens", "comment": "Particularly appreciated the smooth efficient and private check in. Excellent concierge services provided by Javier. Beautiful and scenic location. Comfortable and well appointed room. Delicious and well catered to in Cafe del Mar by the host Luis and the chef, also named Luis! Thank you both for a memorable dinner with soft ambiance . The White was the best for breakfast and lunch. A thoroughly enjoyed visit to Senses!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r892488984-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "May 2023", "author": "Robert L", "title": "Trip to Hotel Sens.", "comment": "The mattress was a little soft. I sunk down and hard to get out. Rafael Pool Mesero de Buffet was an excellent waiter along with Ana that worked in the Buffet We enjoyed our stay. Will definitely come back.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r892462785-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens3_files/adevair-r.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Adevair R", "title": "Pool bar and Restaurant", "comment": "We were super well served by Ramon, Oman and all the pool waiters in a spectacular way and by Rafael and the team in the restaurant Ramon, David are super friendly and attentive. We hope to be back soon, but getting into the room took a while", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r892327848-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/ce/default-avatar-2020-7.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Ana P", "title": "Awesome vacation", "comment": "I use the spa sensoria and I absolutely love it!!!the lady who gave me a massage just when above and beyond!!!!!super professional!!!!going home felling like a new person!!!thank you Arely\ud83d\ude4f\ud83c\udffd\u2764\ufe0fbest vacation ever!!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r892192819-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e7/7b/default-avatar-2020-56.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Marcia C", "title": "AWSOME!", "comment": "I had an AWSOME experience!  Great customer service especially from Rafael Pool, Joshua, Emmanuel, Emilio, and Daniel. They made me feel welcome like a part of the family!  The food was wonder from Le Buffet. and I would highly recommend this resort so much that I\\'m already planning to return next year with my family!  \ud83d\ude0a", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r891920670-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/df/2b/default-avatar-2020-39.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "jacob p", "title": "All inclusive", "comment": "I came here for my 25th birthday and it was absolutely amazing. The staff was super friendly, they do not speak much English but that\u2019s okay! The drinks are 10/10 and the food is good! Definitely recommend coming here for any type of trip!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r891914148-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-o/11/02/e7/c7/brian-k.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Brian K", "title": "Relaxing beach experience", "comment": "The Siam Kaan, the adult area, was very nice and the new pool was excellent. The bar on the beach was great.  It was great to be able to reserve a bed on the beach for the day.  The upgraded adult floors are very well done with great views, and a nice bar for sunset.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r891904347-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f2/11/default-avatar-2020-23.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Stefan S", "title": "Every time we go - It gets Better and Better !!!", "comment": "We were staying at The Sens Cancun with some friends for four nights and we had a blast at the Adult Only Sian Kaan section. The rooms on the ninth and tenth floor are amazing with even better views. The bar on the 10th floor is a nice after dinner spot and we ended our evenings mostly there. We stayed at this hotel already several times because the service and proximity to the beach is second to none. This time to our big surprise - all the adult restaurants got an update or are New! Kudos to the team at Careyes \u2013 the food is so delicious. The White Box is just great for breakfast and so relaxing with its outside seating. The new Caf\u00e9 del Mar offers a great new style and menu. If you have the opportunity - try Sakura and the dinner show. What an experience. We had a beach bed", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r891805236-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/32/default-avatar-2020-12.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Explore44813958304", "title": "Beauty & Service - Great Experiences!", "comment": "The Sens was an amazing experience! The check-in process was quick and easy as well as the check-out. We LOVED the VIP Lounge Lounge Area, the staff was so attentive and knew all of our orders by the end of our stay. I have to give the biggest shout-out to Valentina - she is truly the best! She makes sure everything is perfect every time we stay at an Oasis Property. I have stayed at many Hotels in the Hotel Zone and this is one of the best!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r891777519-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/f0/9f/default-avatar-2020-16.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Blanca G", "title": "Great Beach Vibe !!", "comment": "Llegamos a este hermoso hotel donde tiene una vista incre\u00edble al mar las albercas est\u00e1n enormes y muy agradables y el area we loved the hotel and we were so happy to visit  Manuel Clemente and Paulette y Arturo Lopez were best people at the bar they were the best service abs we had a birthday party here they were the best please ask for them if you come by give them a high five if you want to make a new friend ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r891196029-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ed/00/default-avatar-2020-4.jpg", "date_comment": null, "date_stayed": "May 2023", "author": "Kevin H", "title": "Kid friendly and all around great experience", "comment": "My trip here to Cancun has been nothing short of wonderful. Although we went as a couple only there were plenty of kid friendly accommodations. There was food available at all times and variety was all over the map. Ramen, italian, mexican and available from 630am until about 2 am.  With so many choices to choose from as far as cooling down at the pools and beach we found great people who attended us as well. 2 people really stood out with me and one of them was  Eduin who catered to the kayak activities and gaming room. His personality stood out and was enthusiastic about catering to us on the activities he was assigned. Our package was all inclusive and the one guy who stood out to us was Manuel Javier; a bartender who took our orders and made them the correct way. I actually", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r888884972-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ef/9c/default-avatar-2020-14.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "G K", "title": "Bright CLEAN Warm", "comment": "EXCELLENT Service from A-Z The hotel was spotless thanks to people like Kena the lobby window washer who warmly greeted me daily and attention to detail in cleaning. Most beachfront hotels are littered with sand and grime but the Sens was a jewel.  ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r888695035-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens4_files/default-avatar-2020-34.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "skygoddez", "title": "Wonderful Sisters trip!!", "comment": "Where to begin...??  We had an Amazing time on our (hopefully, First Annual) Sisters Trip!   The Sens Cancun was Wonderful!  The hotel was Beautiful - the grounds were lovely & well maintained, the room was comfortable, clean, nice & large, the restaurants & bars were indeed Five Star quality, the pool & beach area were fantastic!  We especially loved the hammocks in the water.  The personnel were genuine and eager to make certain we had a perfect vacation.   The front desk agents - Ivan Valdivia & Eva Dominguez - were Very friendly & most welcoming.   We enjoyed starting each day with Nicholas @ the White Box for breakfast.   Aramis is the bartender everyone Loves - @ the main pool & the Sensoria Lounge.   Irene made our visit to the Sensoria, by making us feel extra special &", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r888595100-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/77/ae/default-avatar-2020-2.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Linda C", "title": "Wonderful stay!", "comment": "A Wonderful stay!!  Perfect setting for our Sisters Trip.  The room was large & Clean.   The grounds were Beautiful & well maintained. The bars & restaurants were definitely Five Star!!  The staff were very friendly, helpful & eager to please.  Cons: Add towel bars in room to facilitate reusing towels.  Room keys were too easily de-magnetized.  Toilet flushing was tricky ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r888579959-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/de/5a/default-avatar-2020-36.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Seth B", "title": "Awesome service", "comment": "The hotel was amazing and the service was wonderful. Elyer at the Sensoria Gastrobar was fantastic and made wonderful drinks and made great conversation! We asked him to make something off the top of his head and he delivered amazing drinks !", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r888155587-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/81/default-avatar-2020-43.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Erin K", "title": "Great drinks from Elyer", "comment": "Elyer made us the best drinks at the Sensora gastro bar! My boyfriend had a specialty marg and I had a baileys cocktail. It was delicious! We had so much fun! He was so personable. Great time! We will be back again before the end of our trip. We\u2019ve had a great time.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r888155552-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Coa C", "title": "Sakura Glass Bar", "comment": "Dakura Glass bar food and service is amazing. The Peruvian/Japanese fusion is so delicious and well done. Atmosphere is great, delicious food, Greta drinks, nice shows, awesome service.  Maria de la Cruz is doing a great job leading the team. Ovany and Jesus were amazing. Francisco is an amazing cheff.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r887462328-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Companion47239112569", "title": "Loved", "comment": "The service was amazing Alejandro  Santiago did a great job would definitely come back and see them again. My husband had the tomahawk steak and it was cooked perfectly. The service was amazing beautiful", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r887309958-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "June K", "title": "Le Petit @ Grand Oasis is the 5 star of this location!", "comment": "What an amazing experience at Le Petit at Grand Oasis Palm. Juan, Bartolome, Alejandro, Francisco and Chef Cesar were so attentive and made us feel extra special celebrating our anniversary!  We couldn\u2019t get into the Agave Restaurant so we decide to go to Le Petit and it was so worth the extra $$.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r887305009-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Jacolby M", "title": "Uziel", "comment": "Uziel is the man!!!! Best bartender ever!!! I\u2019ve been to plenty of bars but the atmosphere and drinks he makes are next level!! I would recommend him to anyone that comes to CANCUN! Trust me ask him you won\u2019t regret it ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r887184376-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Ainel B", "title": "great bartending experience", "comment": "Uziel is the greatest bartender! really made our time at the hotel. partner Dulce was very nice as well! cannot wait to come back to the bar and get more drinks with him! really enjoyed the atmosphere ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r887184342-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "April 2023", "author": "Luis L", "title": "Excellent", "comment": "The service was phenomenal from beginning to end. Hotel staff, restaurant staff makes me want to stay longer and/or just keep coming back.  Sakura restaurant was fantastic!  The food was top notch. The performances was beautiful. Ambience is perfect.  Just want to thank the guys Alejandro, Ojani and Capitan Maria for their amazing service. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r886592637-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens5_files/default-avatar-2020-56.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "flakita76", "title": "Fun in the sun", "comment": "Amazing  place. I love restaurant  the white  box. Server Nicolas snd hostess  estrella  was really nice.  Food in the white box was amazing!!!!!!!!!!!!. I will come back!!! The salmon was great! NICOLAS was amazing.  He was a very attentive  server!!!!! The way it should  be!!! I love the sense.  The white box is only adults lol I got to a break from my kids!! I  had lunch  while they were swimming in the pool....I could see them from the terrace.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r886503028-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/77/ae/default-avatar-2020-2.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Anne Marie Boyanov", "title": "Le petit Chef the best", "comment": "Tonight is our last night, and we wanted to experience something special. We heard about \u2018Le Petit Chef\u2019 restaurant at The Sens hotel and we decided to try it.  It was the best experience/ food we had at The Sens Oasis. . The staffs were so friendly and professionals. They knew what they were doing, Juan, Bartolome, Elexandro, Francisco and Chef Cesar are excellent in what they do.  We recommend this restaurant 100%, whoever has the chance to come here, you won\u2019t regret. Music, wine, food, the show were the best. ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r885860339-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e2/e6/default-avatar-2020-45.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Allen", "title": "Tremendous \u201clittle\u201d dinner!", "comment": "Had dinner at LePetite Chef and it was absolutely magnificent!!  Chef Cesar hit it out of the park and the service from the 4 amigos; Juan, Bartolome, Alejandro and Francisco was exceptional. We decided last minute to partake and it was absolutely worth it!!  Exceptional experience!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r885706953-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/2b/default-avatar-2020-52.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "nimisha s", "title": "Photo", "comment": "Very good service and the staff so helpful (Fernando Diaz)did very nice photo he give very very good service to us we give them 5 stars to all the staff and service very clean and organized we will be back soon too", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r885388801-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/2b/default-avatar-2020-52.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Kiki Tan", "title": "Systems could improve but great service!", "comment": "Claudia Bustamante was wonderful service in the cabanas. She works very hard in the family section. The entire staff at White Box was very lovely, especially our dinner service with Julio and Luis. The staff at Los Agaves made our birthday celebration very lovely- thank you to Rodalfo! It would be better to reserve meal times and book activities through the app- the app is not very helpful because sometimes the information hasn\u2019t been updated. Serena Maga\u00f1a was very fun during the group volleyball activity and we would play again but the timings didn\u2019t suit our group.  Every morning our coffee was made with love by Sara at Coffee & Me", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r884943394-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/ee/db/default-avatar-2020-10.jpg", "date_comment": null, "date_stayed": "April 2023", "author": "Crystal A", "title": "Uziel you are an amazing bartender!", "comment": "Uziel is the friendliest person and exceptional bartender! Would come back here just for his skills on the 10th floor adults only bar. Thank you for making our trip to the Sens amazing! See you next time! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r884841539-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e4/a9/default-avatar-2020-50.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Trail11851758382", "title": "Usiel the bar tender on the 10th floor", "comment": "Usiel the bar tender on the 10th  floor  makes the best drinks  I  recommend  him , he is very kind  and very respectful  really nice guy. I recommend  anyone who is in sens in cancun  see Usiel for a good  drink", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r884335368-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "https://media-cdn.tripadvisor.com/media/photo-t/1a/f6/e5/66/default-avatar-2020-53.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "stephane G", "title": "Excellent service", "comment": "Super fun staff great people! Japanese restaurant was the best experience  Estrella and the staff keep us entertained and our glasses was always full .. food was on time and very delicious They also had live shows (music and dance show with stunt) Mannager Maria de la Cruz the chef Franscico, Ovany and Jesus ensure that our experience was top notch .. thanks to all the staff and we will be back for sure ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r883974749-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Tamara M", "title": "Le Petit Chef!", "comment": "We ate at the Le Petit chef restaurant and the food was amazing, but the service was better! Juan Zambrano  Bartolome Canche, Francisco Sliva and Alejandro Gongora made the experience extra special. We highly recommend that you eat here in Mexico!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r883967664-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Christy L", "title": "Great food with great friends", "comment": "Very delicious and generous portions,  perfect for your own meal or sharing with friends. If the food was the  only thing the place offered,  it would have been enough, but the additional show and music, brought it to another level.   Thank you Maria, Jesus, Ovany, Estrella and the chef,.Francisco!!!!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r883453408-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens6_files/default-avatar-2020-67.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Rod W", "title": "Vacation", "comment": "Jesus is the best service in the Hotel Best food aswell we stayed 10days and visited Sakura 3 times . We are from Australia and will definitely come here again. Thank you for Estella lovely host  Rod and kym  ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r883451245-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": "./sens6_files/default-avatar-2020-20.jpg", "date_comment": null, "date_stayed": "March 2023", "author": "Venture679452", "title": "Travel friends", "comment": "Overall very happy  Sakura restaurant beautiful great service , staff was great Jesus Lazard, Ovany, Etthelbertho, the manager Maria and the Hoss Estrella. Lovely beach, great location. Recommendation this hotel ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r883083248-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Bjdmwill", "title": "Uziel at 10th floor bar- the BEST", "comment": "We stayed on the 10th floor. The room was nice but the bartender, Uziel, was the nicest person! He made us feel so welcome! He was willing to accommodate anything we needed. He was so friendly and always full of joy. He made our trip! ", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r882960691-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Gratian M", "title": "Vacation", "comment": "Its a nice place, i recomand to stay more in the hotel , the Oasis Grand is very ok you can party here, a lot of food and nice employers , there is a nice gilrl Anayatzin and Aide , ,,,, You can drink a lot of wiskey rel lable jb\u2026 for freee , restaurant is open until 3 o clock in the night", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r882523748-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Kevin M", "title": "The BEST Staff", "comment": "The highlight of our stay was the warm and welcoming staff at the Sens. To start, the front desk personally gave a tour of prospective rooms for our group. The bartenders at the swim up bar were quick and helpful. The Glass Bar recently opened a restaurant, and on its second day open, the staff was so kind and patient with our rambunctious group. Jesu\u015b, Ouany, Ethelberto, and Maria catered to our specific needs, especially when it came to two of our members gluten allergies. They went through the menu and told us what was and was not able to be made gluten free unlike some other places we had been in the past that did not understand. Ivan was also very personable and was a huge highlight of our trip once again. I can say that the Wi-Fi was not the best and some of the restaurant", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r882510114-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Happiness46894", "title": "Great stay for us in March 2023 !", "comment": "We visited the Sens Cancun in March 2023, stayed on the 10th floor. They have a private bar there, and the bartender, Uziel, was fantastic! He is very friendly, and warm....always remembered our favorite drinks and always had a smile to go with it! People like Uziel made our stay so great! The restaurants are great, friendly staff,  clean property!  Definitely recommend this resort, and if you can....stay on the 10th floor....Uziel will take very good care of you !", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r882452807-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "solar v", "title": "Excellent meal at Las Agaves", "comment": "We ate at Las Agaves at the Sens. Alejandro Santiago was our waiter, and Estrella was our hostess. The service was excellent, they had reserved a spot near to the Captain Hook boats, which we got to see depart and return during our meal. The food was delicious, and as I had mentioned the service was great. We\\'d gladly return.", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r882336989-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Ralph L", "title": "Ralph Lee", "comment": "I stayed at the Sens hotel and was extremely impressed with the hotel, beach and service.  I had an incredibly relaxing and happy experience which was made possible by the excellent service in the cabana area and in particular Claudia Bustamante was amazing! She remembered what type of drinks I wanted, politely inquired if I needed anything and made the trip amazing!! You will be lucky if you meet Claudia! There was no pressure to tip however because of the service she provided I couldn\u2019t help but tip very well.  If I could give her stars I would give Claudia 6 stars!!!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r881942488-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Remo M", "title": "The sens resort", "comment": "Great resort good rooms many choices of food to eat great entertainment beach is amazing staff very friendly there are many reviews saying this and that easy to navigate good WiFi buses were cheap Cancun has changed a lot since I was there in 2012 getting more and more things to do the buffets had a wide variety of choices swimming pools were great little bit expensive but worth it at the end the best way", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r881837120-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
INSERT INTO comments_advisor(hotel_id, rate, comment, lang) VALUES(41, 5.0, '{"profile_picture": null, "date_comment": null, "date_stayed": "March 2023", "author": "Christine H", "title": "Sian Ka'an Lounge Bar", "comment": "Uziel took such good care of us! Made our first night special and fun. Excellent service with a smile and good conversation. One of my favorite spots in the entire resort! You can watch the sunset, feel the ocean breeze. A perfect spot to start your night out!", "comment_link": "https://www.tripadvisor.com/ShowUserReviews-g150807-d671741-r881382145-The_Sens_Cancun-Cancun_Yucatan_Peninsula.html", "rate": 5.0}', 'en');
